{
  "name": "QualityControl Pro",
  "description": "A cross-platform manufacturing quality control and record-keeping application."
}

import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

const rootElement = document.getElementById('root');
if (!rootElement) {
  throw new Error("Could not find root element to mount to");
}

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

export enum UserRole {
  EMPLOYEE = 'EMPLOYEE',
  AUDITOR = 'AUDITOR',
}

export enum ProcessType {
  TERMINAL_CRIMPING = 'TERMINAL_CRIMPING',
  WAVE_SOLDERING = 'WAVE_SOLDERING',
  COATING = 'COATING',
  // Extensible for other types in the mind map
}

export enum SubmissionType {
  FIRST_PIECE = '首件',
  LAST_PIECE = '末件',
}

export enum AuditStatus {
  PENDING = '待审核',
  PASSED = '合格',
  FAILED = '不合格',
}

export interface TerminalSpec {
  id: string;
  name: string;
}

export interface WireSpec {
  id: string;
  name: string;
}

export interface StandardMapping {
  terminalId: string;
  wireId: string;
  standardPullForce: number; // In Newtons
}

export interface InspectionRecord {
  id: string;
  orderId: string;
  type: SubmissionType;
  submitterName: string;
  submittedAt: string;
  status: AuditStatus;

  // Audit details
  auditorName?: string;
  auditedAt?: string;
  measuredPullForce?: number;
  auditNote?: string;
}

export interface ProductionOrder {
  id: string; // Auto-generated
  productName: string;
  productModel: string; // Product No
  productionOrderNo: string; // External Order No
  toolNo: string;
  terminalSpecId: string;
  wireSpecId: string;
  standardPullForce: number;
  creatorName: string;
  createdAt: string;

  // Linked records
  records: InspectionRecord[];
}

import { TerminalSpec, WireSpec, StandardMapping } from './types';

// Mock Data for Dropdowns
export const TERMINAL_SPECS: TerminalSpec[] = [
  { id: 't1', name: 'XH2.54-Term' },
  { id: 't2', name: 'VH3.96-Term' },
  { id: 't3', name: 'CH3.81-Term' },
];

export const WIRE_SPECS: WireSpec[] = [
  { id: 'w1', name: 'AWG #22' },
  { id: 'w2', name: 'AWG #24' },
  { id: 'w3', name: 'AWG #26' },
];

// Matrix to determine standard pull force based on selection
export const STANDARD_MAPPINGS: StandardMapping[] = [
  { terminalId: 't1', wireId: 'w1', standardPullForce: 45 },
  { terminalId: 't1', wireId: 'w2', standardPullForce: 30 },
  { terminalId: 't1', wireId: 'w3', standardPullForce: 20 },
  { terminalId: 't2', wireId: 'w1', standardPullForce: 60 },
  { terminalId: 't2', wireId: 'w2', standardPullForce: 50 },
  { terminalId: 't2', wireId: 'w3', standardPullForce: 40 },
  { terminalId: 't3', wireId: 'w1', standardPullForce: 55 },
  { terminalId: 't3', wireId: 'w2', standardPullForce: 45 },
  { terminalId: 't3', wireId: 'w3', standardPullForce: 35 },
];

export const MOCK_USER_NAME = "赵科磊"; // Employee Name from image
export const MOCK_AUDITOR_NAME = "李工"; // Auditor Name

import React from 'react';
import { UserRole } from '../types';

interface LayoutProps {
  children: React.ReactNode;
  title: string;
  showBack?: boolean;
  onBack?: () => void;
  currentRole: UserRole;
  onToggleRole: () => void;
}

export const Layout: React.FC<LayoutProps> = ({ 
  children, 
  title, 
  showBack, 
  onBack,
  currentRole,
  onToggleRole
}) => {
  return (
    <div className="min-h-screen bg-gray-50 flex flex-col max-w-md mx-auto shadow-2xl overflow-hidden border-x border-gray-200">
      {/* Header */}
      <header className="bg-white px-4 py-4 flex items-center justify-between shadow-sm sticky top-0 z-10">
        <div className="flex items-center gap-3">
          {showBack && (
            <button onClick={onBack} className="p-1 -ml-2 text-gray-600 hover:bg-gray-100 rounded-full">
              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-6 h-6">
                <path strokeLinecap="round" strokeLinejoin="round" d="M15.75 19.5L8.25 12l7.5-7.5" />
              </svg>
            </button>
          )}
          <h1 className="text-lg font-bold text-gray-900 truncate max-w-[200px]">{title}</h1>
        </div>

        {/* Role Toggler for Demo Purpose */}
        <button 
          onClick={onToggleRole}
          className={`text-xs px-3 py-1 rounded-full font-medium transition-colors ${
            currentRole === UserRole.EMPLOYEE 
              ? 'bg-blue-100 text-blue-700' 
              : 'bg-purple-100 text-purple-700'
          }`}
        >
          {currentRole === UserRole.EMPLOYEE ? '当前: 员工' : '当前: 检验'}
        </button>
      </header>

      {/* Main Content */}
      <main className="flex-1 overflow-y-auto p-4 space-y-4">
        {children}
      </main>
    </div>
  );
};

import React from 'react';

interface InputProps extends React.InputHTMLAttributes<HTMLInputElement> {
  label: string;
}

export const Input: React.FC<InputProps> = ({ label, className, ...props }) => {
  return (
    <div className="flex flex-col gap-1.5">
      <label className="text-sm font-medium text-gray-700">{label}</label>
      <input
        className={`w-full px-3 py-3 bg-white border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-brand-500 focus:border-transparent disabled:bg-gray-100 disabled:text-gray-500 ${className}`}
        {...props}
      />
    </div>
  );
};

import React from 'react';

interface SelectProps extends React.SelectHTMLAttributes<HTMLSelectElement> {
  label: string;
  options: { value: string; label: string }[];
}

export const Select: React.FC<SelectProps> = ({ label, options, className, ...props }) => {
  return (
    <div className="flex flex-col gap-1.5">
      <label className="text-sm font-medium text-gray-700">{label}</label>
      <div className="relative">
        <select
          className={`w-full px-3 py-3 bg-white border border-gray-300 rounded-lg text-sm appearance-none focus:outline-none focus:ring-2 focus:ring-brand-500 focus:border-transparent disabled:bg-gray-100 ${className}`}
          {...props}
        >
          <option value="" disabled>请选择{label}</option>
          {options.map((opt) => (
            <option key={opt.value} value={opt.value}>
              {opt.label}
            </option>
          ))}
        </select>
        <div className="absolute inset-y-0 right-0 flex items-center px-2 pointer-events-none">
          <svg className="w-4 h-4 text-gray-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7" />
          </svg>
        </div>
      </div>
    </div>
  );
};

