import React, { useState, useEffect } from 'react';
import { Input } from '../../components/Input';
import { Select } from '../../components/Select';
import { ProductionOrder } from '../../types';
import { TERMINAL_SPECS, WIRE_SPECS, STANDARD_MAPPINGS, MOCK_USER_NAME } from '../../constants';

interface CreateOrderProps {
  onSubmit: (order: ProductionOrder) => void;
  dailyCounter: number;
}

export const CreateOrder: React.FC<CreateOrderProps> = ({ onSubmit, dailyCounter }) => {
  const [formData, setFormData] = useState({
    productName: '',
    productModel: '', // Product No
    productionOrderNo: '',
    toolNo: '',
    terminalSpecId: '',
    wireSpecId: '',
  });

  const [generatedId, setGeneratedId] = useState('');
  const [standardPullForce, setStandardPullForce] = useState<number | ''>('');

  // 1. Auto-generate ID logic: Production Order + Date + Daily Counter
  useEffect(() => {
    if (formData.productionOrderNo) {
      const today = new Date();
      const dateStr = today.getFullYear().toString() + 
                      (today.getMonth() + 1).toString().padStart(2, '0') + 
                      today.getDate().toString().padStart(2, '0');
      const counterStr = dailyCounter.toString().padStart(3, '0');

      setGeneratedId(`${formData.productionOrderNo}-${dateStr}-${counterStr}`);
    } else {
      setGeneratedId('等待输入生产工单号...');
    }
  }, [formData.productionOrderNo, dailyCounter]);

  // 2. Auto-calculate Standard Pull Force
  useEffect(() => {
    if (formData.terminalSpecId && formData.wireSpecId) {
      const mapping = STANDARD_MAPPINGS.find(
        m => m.terminalId === formData.terminalSpecId && m.wireId === formData.wireSpecId
      );
      setStandardPullForce(mapping ? mapping.standardPullForce : 0);
    } else {
      setStandardPullForce('');
    }
  }, [formData.terminalSpecId, formData.wireSpecId]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = () => {
    if (!formData.productionOrderNo || !formData.terminalSpecId || !standardPullForce) {
      alert("请填写完整信息");
      return;
    }

    const newOrder: ProductionOrder = {
      id: generatedId,
      productName: formData.productName,
      productModel: formData.productModel,
      productionOrderNo: formData.productionOrderNo,
      toolNo: formData.toolNo,
      terminalSpecId: formData.terminalSpecId,
      wireSpecId: formData.wireSpecId,
      standardPullForce: Number(standardPullForce),
      creatorName: MOCK_USER_NAME,
      createdAt: new Date().toISOString(),
      records: []
    };

    onSubmit(newOrder);
  };

  return (
    <div className="space-y-4 pb-20">
      <div className="bg-white p-4 rounded-xl shadow-sm border border-gray-100 space-y-4">
        <Input 
          label="编号 (自动生成)" 
          value={generatedId} 
          disabled 
          className="bg-gray-50 text-gray-500 font-mono"
        />

        <div className="grid grid-cols-2 gap-4">
            <Input 
                label="产品名称" 
                name="productName" 
                placeholder="输入产品名称" 
                value={formData.productName} 
                onChange={handleChange}
            />
            <Input 
                label="产品型号" 
                name="productModel" 
                placeholder="输入产品型号" 
                value={formData.productModel} 
                onChange={handleChange}
            />
        </div>

        <div className="grid grid-cols-2 gap-4">
            <Input 
                label="生产工单号" 
                name="productionOrderNo" 
                placeholder="输入生产工单号" 
                value={formData.productionOrderNo} 
                onChange={handleChange}
            />
             <Input 
                label="压接工具编号" 
                name="toolNo" 
                placeholder="输入工具编号" 
                value={formData.toolNo} 
                onChange={handleChange}
            />
        </div>

        <div className="grid grid-cols-2 gap-4">
          <Select 
            label="端子规格" 
            name="terminalSpecId"
            options={TERMINAL_SPECS.map(t => ({ value: t.id, label: t.name }))}
            value={formData.terminalSpecId}
            onChange={handleChange}
          />
          <Select 
            label="导线规格" 
            name="wireSpecId"
            options={WIRE_SPECS.map(w => ({ value: w.id, label: w.name }))}
            value={formData.wireSpecId}
            onChange={handleChange}
          />
        </div>

        <div className="grid grid-cols-2 gap-4">
          <Input 
            label="拉力标准值 (N)" 
            value={standardPullForce} 
            disabled 
            placeholder="自动生成"
            className="bg-gray-50 font-bold text-gray-700"
          />
           <Input 
            label="工作者" 
            value={MOCK_USER_NAME} 
            disabled 
            className="bg-gray-50"
          />
        </div>
      </div>

      <button 
        onClick={handleSubmit}
        className="w-full bg-brand-600 text-white py-4 rounded-xl font-bold text-lg shadow-lg hover:bg-brand-700 active:scale-95 transition-all fixed bottom-6 left-4 right-4 max-w-[calc(100%-2rem)] md:max-w-[416px] mx-auto z-20"
      >
        新增订单
      </button>
      <div className="h-12"></div> 
    </div>
  );
};

import React, { useState } from 'react';
import { ProductionOrder, SubmissionType, AuditStatus, InspectionRecord, UserRole } from '../../types';
import { CreateOrder } from './CreateOrder';
import { Card } from '../../components/Card';
import { MOCK_USER_NAME, MOCK_AUDITOR_NAME } from '../../constants';

interface CrimpingDashboardProps {
  currentRole: UserRole;
}

export const CrimpingDashboard: React.FC<CrimpingDashboardProps> = ({ currentRole }) => {
  const [view, setView] = useState<'LIST' | 'CREATE'>('LIST');
  const [orders, setOrders] = useState<ProductionOrder[]>([]);
  const [dailyCounter, setDailyCounter] = useState(1);
  const [selectedOrder, setSelectedOrder] = useState<ProductionOrder | null>(null);

  // Mock initial data handling or DB fetch simulation could go here

  const handleCreateOrder = (newOrder: ProductionOrder) => {
    setOrders([newOrder, ...orders]);
    setDailyCounter(prev => prev + 1);
    setView('LIST');
  };

  const handleAddRecord = (orderId: string, type: SubmissionType) => {
    const newRecord: InspectionRecord = {
      id: Date.now().toString(),
      orderId,
      type,
      submitterName: MOCK_USER_NAME,
      submittedAt: new Date().toISOString(),
      status: AuditStatus.PENDING,
    };

    setOrders(prev => prev.map(o => {
      if (o.id === orderId) {
        return { ...o, records: [newRecord, ...o.records] };
      }
      return o;
    }));
  };

  const handleAuditRecord = (orderId: string, recordId: string, measured: number, passed: boolean) => {
    setOrders(prev => prev.map(o => {
      if (o.id === orderId) {
        const updatedRecords = o.records.map(r => {
          if (r.id === recordId) {
            return {
              ...r,
              status: passed ? AuditStatus.PASSED : AuditStatus.FAILED,
              measuredPullForce: measured,
              auditorName: MOCK_AUDITOR_NAME,
              auditedAt: new Date().toISOString(),
            };
          }
          return r;
        });
        return { ...o, records: updatedRecords };
      }
      return o;
    }));
  };

  if (view === 'CREATE') {
    return (
      <>
        <div className="mb-4">
             <button onClick={() => setView('LIST')} className="text-sm text-gray-500 flex items-center gap-1">
                &larr; 返回列表
             </button>
        </div>
        <CreateOrder onSubmit={handleCreateOrder} dailyCounter={dailyCounter} />
      </>
    );
  }

  // Details View (Order Selected)
  if (selectedOrder) {
    return (
      <OrderDetailView 
        order={selectedOrder} 
        onBack={() => setSelectedOrder(null)}
        onAddRecord={handleAddRecord}
        onAuditRecord={handleAuditRecord}
        currentRole={currentRole}
      />
    );
  }

  // List View
  return (
    <div className="space-y-4">
      {currentRole === UserRole.EMPLOYEE && (
        <button 
            onClick={() => setView('CREATE')}
            className="w-full bg-white border-2 border-dashed border-gray-300 text-gray-500 py-4 rounded-xl font-medium hover:bg-gray-50 hover:border-brand-300 hover:text-brand-500 transition-colors"
        >
            + 新增生产订单
        </button>
      )}

      {orders.length === 0 ? (
        <div className="text-center py-10 text-gray-400">
          <p>暂无订单记录</p>
        </div>
      ) : (
        orders.map(order => (
          <Card key={order.id} onClick={() => setSelectedOrder(order)} className="border-l-4 border-l-brand-500">
            <div className="flex justify-between items-start">
              <div>
                <h3 className="font-bold text-gray-900">{order.productName}</h3>
                <p className="text-xs text-gray-500 font-mono mt-1">{order.id}</p>
              </div>
              <span className={`px-2 py-1 text-xs rounded-md ${
                order.records.length > 0 && order.records[0].status === AuditStatus.PENDING 
                ? 'bg-yellow-100 text-yellow-700'
                : 'bg-green-100 text-green-700'
              }`}>
                {order.records.length} 条记录
              </span>
            </div>
            <div className="mt-3 flex gap-2 text-xs text-gray-500">
                <span>标准: {order.standardPullForce}N</span>
                <span>•</span>
                <span>工单: {order.productionOrderNo}</span>
            </div>
          </Card>
        ))
      )}
    </div>
  );
};

// Sub-component for Order Details
const OrderDetailView: React.FC<{
  order: ProductionOrder;
  onBack: () => void;
  onAddRecord: (orderId: string, type: SubmissionType) => void;
  onAuditRecord: (orderId: string, recordId: string, val: number, pass: boolean) => void;
  currentRole: UserRole;
}> = ({ order, onBack, onAddRecord, onAuditRecord, currentRole }) => {

  // Logic: First Piece must be PASSED before Last Piece
  const firstPiecePassed = order.records.some(r => r.type === SubmissionType.FIRST_PIECE && r.status === AuditStatus.PASSED);
  const hasPendingFirstPiece = order.records.some(r => r.type === SubmissionType.FIRST_PIECE && r.status === AuditStatus.PENDING);

  const [auditValue, setAuditValue] = useState<string>('');
  const [activeAuditRecordId, setActiveAuditRecordId] = useState<string | null>(null);

  const handleAuditSubmit = (recordId: string) => {
    const val = parseFloat(auditValue);
    if (isNaN(val)) return;
    const passed = val >= order.standardPullForce;
    onAuditRecord(order.id, recordId, val, passed);
    setAuditValue('');
    setActiveAuditRecordId(null);
  };

  return (
    <div className="space-y-4 pb-10">
      <button onClick={onBack} className="text-sm text-gray-500 mb-2 flex items-center">&larr; 返回列表</button>

      {/* Order Info Header */}
      <div className="bg-white p-4 rounded-xl shadow-sm border border-gray-100">
        <div className="flex justify-between mb-2">
            <h2 className="font-bold text-lg">{order.productName}</h2>
            <span className="font-mono text-xs text-gray-400 self-center">{order.productionOrderNo}</span>
        </div>
        <div className="grid grid-cols-2 gap-y-2 text-sm text-gray-600">
            <p>标准拉力: <span className="font-bold text-brand-600">{order.standardPullForce}N</span></p>
            <p>端子: {TERMINAL_SPECS.find(t=>t.id===order.terminalSpecId)?.name}</p>
            <p>导线: {WIRE_SPECS.find(w=>w.id===order.wireSpecId)?.name}</p>
            <p>工具: {order.toolNo}</p>
        </div>
      </div>

      {/* Action Area (Employee Only) */}
      {currentRole === UserRole.EMPLOYEE && (
        <div className="grid grid-cols-2 gap-3">
          <button
            disabled={hasPendingFirstPiece || firstPiecePassed} // Disable if pending or already passed (assuming 1 first piece per day usually, or logic can be looser)
            onClick={() => onAddRecord(order.id, SubmissionType.FIRST_PIECE)}
            className={`py-3 rounded-xl font-medium shadow-sm transition-all ${
               firstPiecePassed 
                ? 'bg-green-100 text-green-700 cursor-not-allowed opacity-50' 
                : hasPendingFirstPiece 
                    ? 'bg-yellow-100 text-yellow-700 cursor-not-allowed'
                    : 'bg-white text-brand-600 border border-brand-200 hover:bg-brand-50'
            }`}
          >
            {firstPiecePassed ? '首件已合格' : hasPendingFirstPiece ? '首件审核中' : '提交首件'}
          </button>

          <button
            disabled={!firstPiecePassed}
            onClick={() => onAddRecord(order.id, SubmissionType.LAST_PIECE)}
            className={`py-3 rounded-xl font-medium shadow-sm transition-all ${
              !firstPiecePassed 
                ? 'bg-gray-100 text-gray-400 cursor-not-allowed' 
                : 'bg-brand-600 text-white hover:bg-brand-700'
            }`}
          >
            提交末件
          </button>
        </div>
      )}

      {/* Records Stream */}
      <div className="space-y-3">
        <h3 className="font-medium text-gray-700 ml-1">提交记录</h3>
        {order.records.length === 0 && <p className="text-gray-400 text-sm ml-1">暂无记录</p>}
        {order.records.map(record => (
          <Card key={record.id} className="relative">
            <div className="flex justify-between items-start mb-2">
              <div className="flex items-center gap-2">
                <span className={`px-2 py-0.5 rounded text-xs font-bold ${
                    record.type === SubmissionType.FIRST_PIECE ? 'bg-purple-100 text-purple-700' : 'bg-blue-100 text-blue-700'
                }`}>
                    {record.type}
                </span>
                <span className="text-xs text-gray-400">{new Date(record.submittedAt).toLocaleTimeString()}</span>
              </div>

              <StatusBadge status={record.status} />
