            }`}
          >
            {firstPiecePassed ? '首件已合格' : hasPendingFirstPiece ? '⏳ 等待审核...' : '提交首件'}
          </button>

          <button
            disabled={!firstPiecePassed}
            onClick={handleSubmitLastPiece}
            className={`py-3 rounded-xl font-medium shadow-sm transition-all ${
              !firstPiecePassed 
                ? 'bg-gray-100 text-gray-400 cursor-not-allowed' 
                : 'bg-brand-600 text-white hover:bg-brand-700'
            }`}
          >
            提交末件
          </button>
        </div>
      )}

      {/* Records Stream */}
      <div className="space-y-3">
        <h3 className="font-medium text-gray-700 ml-1">提交记录</h3>
        {order.records.length === 0 && <p className="text-gray-400 text-sm ml-1">暂无记录</p>}
        {order.records.map(record => (
          <Card key={record.id} className="relative">
            <div className="flex justify-between items-start mb-2">
              <div className="flex items-center gap-2">
                <span className={`px-2 py-0.5 rounded text-xs font-bold ${
                    record.type === SubmissionType.FIRST_PIECE ? 'bg-purple-100 text-purple-700' : 'bg-blue-100 text-blue-700'
                }`}>
                    {record.type}
                </span>
                <span className="text-xs text-gray-400">{new Date(record.submittedAt).toLocaleTimeString()}</span>
              </div>

              <StatusBadge status={record.status} />
            </div>

            <div className="text-sm space-y-1">
               <p className="text-gray-600">提交人: {record.submitterName}</p>
               {record.status !== AuditStatus.PENDING && (
                 <div className="mt-2 pt-2 border-t border-gray-100">
                    <p className="text-gray-900 flex justify-between">
                        <span>实测拉力: <b>{record.measuredPullForce}N</b></span>
                        {record.measuredPullForce && (
                            <span className={record.measuredPullForce >= order.standardPullForce ? 'text-green-500' : 'text-red-500'}>
                                {record.measuredPullForce >= order.standardPullForce ? '达标' : '未达标'}
                            </span>
                        )}
                    </p>
                    <p className="text-xs text-gray-400 mt-1">审核人: {record.auditorName}</p>
                 </div>
               )}
            </div>

            {/* Auditor Action Area */}
            {currentRole === UserRole.AUDITOR && record.status === AuditStatus.PENDING && (
                <div className="mt-4 pt-3 border-t border-gray-100 animate-in fade-in">
                    {activeAuditRecordId === record.id ? (
                        <div className="flex gap-2 items-end">
                            <div className="flex-1">
                                <label className="text-xs text-gray-500 mb-1 block">输入实测值 (标准: {order.standardPullForce}N)</label>
                                <input 
                                    type="number" 
                                    autoFocus
                                    className="w-full border rounded px-2 py-2 text-sm"
                                    placeholder="输入数值"
                                    value={auditValue}
                                    onChange={(e) => setAuditValue(e.target.value)}
                                />
                            </div>
                            <button 
                                onClick={() => handleAuditSubmit(record.id)}
                                className="bg-brand-600 text-white px-4 py-2 rounded text-sm h-10 mb-[1px]"
                            >
                                判定
                            </button>
                            <button 
                                onClick={() => setActiveAuditRecordId(null)}
                                className="text-gray-400 px-2 py-2 text-sm h-10 mb-[1px]"
                            >
                                取消
                            </button>
                        </div>
                    ) : (
                        <button 
                            onClick={() => {
                                setActiveAuditRecordId(record.id);
                                setAuditValue('');
                            }}
                            className="w-full py-2 bg-indigo-50 text-indigo-600 text-sm font-medium rounded hover:bg-indigo-100"
                        >
                            开始审核
                        </button>
                    )}
                </div>
            )}
          </Card>
        ))}
      </div>
    </div>
  );
};

const StatusBadge: React.FC<{status: AuditStatus}> = ({status}) => {
    const colors = {
        [AuditStatus.PENDING]: 'bg-yellow-100 text-yellow-700',
        [AuditStatus.PASSED]: 'bg-green-100 text-green-700',
        [AuditStatus.FAILED]: 'bg-red-100 text-red-700',
    };
    return (
        <span className={`text-xs px-2 py-0.5 rounded-full font-medium ${colors[status]}`}>
            {status}
        </span>
    )
}

export enum UserRole {
  EMPLOYEE = 'EMPLOYEE',
  AUDITOR = 'AUDITOR',
}

export enum ProcessType {
  TERMINAL_CRIMPING = 'TERMINAL_CRIMPING',
  WAVE_SOLDERING = 'WAVE_SOLDERING',
  COATING = 'COATING',
  // Extensible for other types in the mind map
}

export enum SubmissionType {
  FIRST_PIECE = '首件',
  LAST_PIECE = '末件',
}

export enum AuditStatus {
  PENDING = '待审核',
  PASSED = '合格',
  FAILED = '不合格',
}

export interface TerminalSpec {
  id: string;
  name: string;
}

export interface WireSpec {
  id: string;
  name: string;
}

export interface StandardMapping {
  terminalId: string;
  wireId: string;
  standardPullForce: number; // In Newtons
}

export interface TerminalSample {
  id: number; // 1, 2, 3
  measuredForce?: number;
  isPassed?: boolean;
}

export interface InspectionRecord {
  id: string;
  orderId: string;
  type: SubmissionType;
  submitterName: string;
  submittedAt: string;
  status: AuditStatus;

  // Audit details
  auditorName?: string;
  auditedAt?: string;
  samples: TerminalSample[]; // Changed from single measuredPullForce to array of samples
  auditNote?: string;
}

export interface ProductionOrder {
  id: string; // Auto-generated
  productName: string;
  productModel: string; // Product No
  productionOrderNo: string; // External Order No
  toolNo: string;
  terminalSpecId: string;
  wireSpecId: string;
  standardPullForce: number;
  creatorName: string;
  createdAt: string;

  // Linked records
  records: InspectionRecord[];
}

import React, { useState, useEffect } from 'react';
import { ProductionOrder, SubmissionType, AuditStatus, InspectionRecord, UserRole, TerminalSample } from '../../types';
import { CreateOrder } from './CreateOrder';
import { Card } from '../../components/Card';
import { MOCK_USER_NAME, MOCK_AUDITOR_NAME, TERMINAL_SPECS, WIRE_SPECS } from '../../constants';

interface CrimpingDashboardProps {
  currentRole: UserRole;
}

// --- MOCK INITIAL DATA FOR DEMO ---
const MOCK_INITIAL_ORDER: ProductionOrder = {
  id: "PO-20250520-001",
  productName: "控制板电源线束",
  productModel: "CB-PWR-01",
  productionOrderNo: "GD20250520",
  toolNo: "T-204",
  terminalSpecId: "t1", // XH2.54
  wireSpecId: "w2",     // AWG #24
  standardPullForce: 30, // From constants mapping
  creatorName: MOCK_USER_NAME,
  createdAt: new Date(Date.now() - 86400000).toISOString(),
  records: [
    {
      id: "rec-001",
      orderId: "PO-20250520-001",
      type: SubmissionType.FIRST_PIECE,
      submitterName: MOCK_USER_NAME,
      submittedAt: new Date(Date.now() - 3600000).toISOString(),
      status: AuditStatus.PENDING,
      samples: [
        { id: 1 },
        { id: 2 },
        { id: 3 }
      ]
    }
  ]
};

export const CrimpingDashboard: React.FC<CrimpingDashboardProps> = ({ currentRole }) => {
  const [view, setView] = useState<'LIST' | 'CREATE'>('LIST');
  const [orders, setOrders] = useState<ProductionOrder[]>([MOCK_INITIAL_ORDER]); // Pre-load mock data
  const [dailyCounter, setDailyCounter] = useState(2);
  const [selectedOrder, setSelectedOrder] = useState<ProductionOrder | null>(null);

  const handleCreateOrder = (newOrder: ProductionOrder) => {
    setOrders([newOrder, ...orders]);
    setDailyCounter(prev => prev + 1);
    setView('LIST');
  };

  const handleAddRecord = (orderId: string, type: SubmissionType) => {
    const newRecord: InspectionRecord = {
      id: Date.now().toString(),
      orderId,
      type,
      submitterName: MOCK_USER_NAME,
      submittedAt: new Date().toISOString(),
      status: AuditStatus.PENDING,
      // Initialize 3 empty samples
      samples: [
        { id: 1 },
        { id: 2 },
        { id: 3 }
      ]
    };

    setOrders(prev => prev.map(o => {
      if (o.id === orderId) {
        return { ...o, records: [newRecord, ...o.records] };
      }
      return o;
    }));
  };

  const handleAuditRecord = (orderId: string, recordId: string, samples: TerminalSample[]) => {
    // Check if all samples are passed
    const allPassed = samples.every(s => s.isPassed);

    setOrders(prev => prev.map(o => {
      if (o.id === orderId) {
        const updatedRecords = o.records.map(r => {
          if (r.id === recordId) {
            return {
              ...r,
              status: allPassed ? AuditStatus.PASSED : AuditStatus.FAILED,
              samples: samples,
              auditorName: MOCK_AUDITOR_NAME,
              auditedAt: new Date().toISOString(),
            };
          }
          return r;
        });
        return { ...o, records: updatedRecords };
      }
      return o;
    }));
  };

  if (view === 'CREATE') {
    return (
      <>
        <div className="mb-4">
             <button onClick={() => setView('LIST')} className="text-sm text-gray-500 flex items-center gap-1">
                &larr; 返回列表
             </button>
        </div>
        <CreateOrder onSubmit={handleCreateOrder} dailyCounter={dailyCounter} />
      </>
    );
  }

  // Details View (Order Selected)
  if (selectedOrder) {
    // Find the latest version of the order from state to ensure updates are reflected
    const liveOrder = orders.find(o => o.id === selectedOrder.id) || selectedOrder;

    return (
      <OrderDetailView 
        order={liveOrder} 
        onBack={() => setSelectedOrder(null)}
        onAddRecord={handleAddRecord}
        onAuditRecord={handleAuditRecord}
        currentRole={currentRole}
      />
    );
  }

  // List View
  return (
    <div className="space-y-4">
      {currentRole === UserRole.EMPLOYEE && (
        <button 
            onClick={() => setView('CREATE')}
            className="w-full bg-white border-2 border-dashed border-gray-300 text-gray-500 py-4 rounded-xl font-medium hover:bg-gray-50 hover:border-brand-300 hover:text-brand-500 transition-colors"
        >
            + 新增生产订单
        </button>
      )}

      {orders.length === 0 ? (
        <div className="text-center py-10 text-gray-400">
          <p>暂无订单记录</p>
        </div>
      ) : (
        orders.map(order => (
          <Card key={order.id} onClick={() => setSelectedOrder(order)} className="border-l-4 border-l-brand-500">
            <div className="flex justify-between items-start">
              <div>
                <h3 className="font-bold text-gray-900">{order.productName}</h3>
                <p className="text-xs text-gray-500 font-mono mt-1">{order.id}</p>
              </div>
              <span className={`px-2 py-1 text-xs rounded-md ${
                order.records.length > 0 && order.records[0].status === AuditStatus.PENDING 
                ? 'bg-yellow-100 text-yellow-700'
                : 'bg-green-100 text-green-700'
              }`}>
                {order.records.length} 条记录
              </span>
            </div>
            <div className="mt-3 flex gap-2 text-xs text-gray-500">
                <span>标准: {order.standardPullForce}N</span>
                <span>•</span>
                <span>工单: {order.productionOrderNo}</span>
            </div>
          </Card>
        ))
      )}
    </div>
  );
};

// Sub-component for Order Details
const OrderDetailView: React.FC<{
  order: ProductionOrder;
  onBack: () => void;
  onAddRecord: (orderId: string, type: SubmissionType) => void;
  onAuditRecord: (orderId: string, recordId: string, samples: TerminalSample[]) => void;
  currentRole: UserRole;
}> = ({ order, onBack, onAddRecord, onAuditRecord, currentRole }) => {

  const firstPiecePassed = order.records.some(r => r.type === SubmissionType.FIRST_PIECE && r.status === AuditStatus.PASSED);
  const hasPendingFirstPiece = order.records.some(r => r.type === SubmissionType.FIRST_PIECE && r.status === AuditStatus.PENDING);

  const handleSubmitFirstPiece = () => {
    const isSuccess = true; 
    if (isSuccess) {
        onAddRecord(order.id, SubmissionType.FIRST_PIECE);
        window.alert("✅ 提交成功！\n\n首件记录已生成，系统已锁定提交权限，请等待检验员审核。");
    } else {
        window.alert("❌ 提交失败，请检查网络或联系管理员。");
    }
  };

  const handleSubmitLastPiece = () => {
      onAddRecord(order.id, SubmissionType.LAST_PIECE);
      window.alert("✅ 末件提交成功！");
  }

  return (
    <div className="space-y-4 pb-10">
      <button onClick={onBack} className="text-sm text-gray-500 mb-2 flex items-center">&larr; 返回列表</button>

      {/* Order Info Header */}
      <div className="bg-white p-4 rounded-xl shadow-sm border border-gray-100 relative overflow-hidden">
        <div className="absolute top-0 right-0 p-2 opacity-10">
            <svg className="w-24 h-24 text-brand-600" fill="currentColor" viewBox="0 0 24 24"><path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-1 17.93c-3.95-.49-7-3.85-7-7.93 0-.62.08-1.21.21-1.79L9 15v1c0 1.1.9 2 2 2v1.93zm6.9-2.54c-.26-.81-1-1.39-1.9-1.39h-1v-3c0-.55-.45-1-1-1H8v-2h2c.55 0 1-.45 1-1V7h2c1.1 0 2-.9 2-2v-.41c2.93 1.19 5 4.06 5 7.41 0 2.08-.8 3.97-2.1 5.39z"/></svg>
        </div>
        <div className="flex justify-between mb-2 relative z-10">
            <h2 className="font-bold text-lg text-gray-900">{order.productName}</h2>
            <span className="font-mono text-xs bg-gray-100 px-2 py-1 rounded text-gray-600 self-center">{order.productionOrderNo}</span>
        </div>
        <div className="grid grid-cols-2 gap-y-2 text-xs text-gray-600 relative z-10 mt-3">
            <div className="flex flex-col">
                <span className="text-gray-400">标准拉力</span>
                <span className="font-bold text-lg text-brand-600">{order.standardPullForce} N</span>
            </div>
            <div className="flex flex-col">
                <span className="text-gray-400">压接工具</span>
                <span className="font-medium text-gray-800">{order.toolNo}</span>
            </div>
            <div className="flex flex-col">
                <span className="text-gray-400">端子规格</span>
                <span className="font-medium text-gray-800">{TERMINAL_SPECS.find(t=>t.id===order.terminalSpecId)?.name}</span>
            </div>
            <div className="flex flex-col">
                <span className="text-gray-400">导线规格</span>
                <span className="font-medium text-gray-800">{WIRE_SPECS.find(w=>w.id===order.wireSpecId)?.name}</span>
            </div>
        </div>
      </div>

      {/* Action Area (Employee Only) */}
      {currentRole === UserRole.EMPLOYEE && (
        <div className="grid grid-cols-2 gap-3">
          <button
            disabled={hasPendingFirstPiece || firstPiecePassed} 
            onClick={handleSubmitFirstPiece}
            className={`py-3 rounded-xl font-medium shadow-sm transition-all border flex flex-col items-center justify-center gap-1 h-20 ${
               firstPiecePassed 
                ? 'bg-green-50 border-green-200 text-green-700 cursor-not-allowed opacity-60' 
                : hasPendingFirstPiece 
                    ? 'bg-yellow-50 border-yellow-200 text-yellow-700 cursor-not-allowed opacity-80'
                    : 'bg-white border-brand-200 text-brand-600 hover:bg-brand-50'
            }`}
          >
            <span className="font-bold">{firstPiecePassed ? '首件合格' : '提交首件'}</span>
            <span className="text-[10px] font-normal">{hasPendingFirstPiece ? '等待审核...' : firstPiecePassed ? '已允许生产' : '需审核后生产'}</span>
          </button>

          <button
            disabled={!firstPiecePassed}
            onClick={handleSubmitLastPiece}
            className={`py-3 rounded-xl font-medium shadow-sm transition-all border flex flex-col items-center justify-center gap-1 h-20 ${
              !firstPiecePassed 
                ? 'bg-gray-50 border-gray-200 text-gray-400 cursor-not-allowed' 
                : 'bg-brand-600 border-brand-600 text-white hover:bg-brand-700'
            }`}
          >
            <span className="font-bold">提交末件</span>
            <span className="text-[10px] font-normal">结束生产时提交</span>
          </button>
        </div>
      )}

      {/* Records Stream */}
      <div className="space-y-4">
        <h3 className="font-bold text-gray-800 text-sm uppercase tracking-wide ml-1">检验记录</h3>

        {order.records.length === 0 && <div className="text-center py-8 bg-white rounded-xl border border-dashed text-gray-400 text-sm">暂无提交记录</div>}

        {order.records.map(record => (
          <InspectionCard 
            key={record.id} 
            record={record} 
            standardPullForce={order.standardPullForce}
            isAuditor={currentRole === UserRole.AUDITOR}
            onAuditSubmit={(samples) => onAuditRecord(order.id, record.id, samples)}
          />
        ))}
      </div>
    </div>
  );
};

// Component for Individual Inspection Record Card
const InspectionCard: React.FC<{
    record: InspectionRecord;
    standardPullForce: number;
    isAuditor: boolean;
    onAuditSubmit: (samples: TerminalSample[]) => void;
}> = ({ record, standardPullForce, isAuditor, onAuditSubmit }) => {

    // Local state for auditor editing
    const [editSamples, setEditSamples] = useState<TerminalSample[]>(
        record.samples.map(s => ({ ...s }))
    );

    const isPending = record.status === AuditStatus.PENDING;
    const canAudit = isAuditor && isPending;

    // Handle input change
    const handleValueChange = (index: number, valStr: string) => {
        const val = parseFloat(valStr);
        const newSamples = [...editSamples];
        newSamples[index] = {
            ...newSamples[index],
            measuredForce: isNaN(val) ? undefined : val,
            isPassed: !isNaN(val) ? val >= standardPullForce : false
        };
        setEditSamples(newSamples);
    };

    const handleSave = () => {
        // Validation: Ensure all have values
        if (editSamples.some(s => s.measuredForce === undefined)) {
            alert("请填写所有端子的测试值");
            return;
        }
        onAuditSubmit(editSamples);
    };

    // Determine what to show: Edited samples if auditing, else record samples
    const displaySamples = canAudit ? editSamples : record.samples;

    return (
        <Card className={`overflow-hidden p-0 ${isPending ? 'border-yellow-200 ring-4 ring-yellow-50' : 'border-gray-200'}`}>
            {/* Header */}
            <div className={`px-4 py-3 border-b flex justify-between items-center ${isPending ? 'bg-yellow-50' : 'bg-gray-50'}`}>
                <div className="flex items-center gap-2">
                    <span className={`px-2 py-1 rounded text-xs font-bold ${
                        record.type === SubmissionType.FIRST_PIECE ? 'bg-purple-600 text-white' : 'bg-blue-600 text-white'
                    }`}>
                        {record.type}
                    </span>
                    <div className="flex flex-col">
                        <span className="text-xs text-gray-500 leading-tight">提交人: {record.submitterName}</span>
                        <span className="text-[10px] text-gray-400 leading-tight">{new Date(record.submittedAt).toLocaleString()}</span>
                    </div>
                </div>
                <StatusBadge status={record.status} />
            </div>

            {/* Content Table */}
            <div className="p-4">
                <table className="w-full text-sm">
                    <thead>
                        <tr className="text-left text-xs text-gray-500 border-b border-gray-100">
                            <th className="pb-2 font-medium w-16">端子样件</th>
                            <th className="pb-2 font-medium">拉力实测 (N)</th>
                            <th className="pb-2 font-medium text-center">结论</th>
                            <th className="pb-2 font-medium text-right text-xs">日期</th>
                        </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-50">
                        {displaySamples.map((sample, idx) => {
                            const dateStr = record.auditedAt 
                                ? new Date(record.auditedAt).toISOString().slice(0, 10).replace(/-/g, '') 
                                : new Date().toISOString().slice(0, 10).replace(/-/g, '');

                            return (
                                <tr key={sample.id}>
                                    <td className="py-3 font-medium text-gray-700">端子 {sample.id}</td>
                                    <td className="py-3 pr-2">
                                        {canAudit ? (
                                            <input 
                                                type="number" 
                                                className={`w-full border rounded px-2 py-1.5 focus:ring-2 focus:ring-brand-500 focus:outline-none transition-colors ${
                                                    sample.measuredForce !== undefined && sample.measuredForce < standardPullForce ? 'border-red-300 bg-red-50 text-red-600' : 'border-gray-300'
                                                }`}
                                                placeholder="0.0"
                                                value={sample.measuredForce ?? ''}
                                                onChange={(e) => handleValueChange(idx, e.target.value)}
                                            />
                                        ) : (
                                            <span className="font-mono text-gray-900">{sample.measuredForce ?? '-'}</span>
                                        )}
                                    </td>
                                    <td className="py-3 text-center">
                                        <div className="flex items-center justify-center gap-1">
                                            {/* Qualified Indicator */}
                                            <div className={`flex items-center gap-1 ${sample.isPassed ? 'opacity-100' : 'opacity-20 grayscale'}`}>
                                                <div className="w-4 h-4 rounded-full bg-green-500 flex items-center justify-center">
                                                    <svg className="w-2.5 h-2.5 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path strokeLinecap="round" strokeLinejoin="round" d="M5 13l4 4L19 7" /></svg>
                                                </div>
                                                <span className="text-xs text-green-700 font-medium hidden sm:inline">合格</span>
                                            </div>
                                            {/* Unqualified Indicator */}
                                            <div className={`flex items-center gap-1 ${sample.isPassed === false ? 'opacity-100' : 'opacity-20 grayscale'}`}>
                                                <div className="w-4 h-4 rounded-full bg-red-500 flex items-center justify-center">
                                                    <svg className="w-2.5 h-2.5 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" /></svg>
                                                </div>
                                                <span className="text-xs text-red-700 font-medium hidden sm:inline">不合格</span>
                                            </div>
                                        </div>
                                    </td>
                                    <td className="py-3 text-right text-xs text-gray-400 font-mono">
                                        {record.status !== AuditStatus.PENDING || canAudit ? dateStr : '-'}
                                    </td>
                                </tr>
                            );
                        })}
                    </tbody>
                </table>

                {canAudit && (
                    <div className="mt-4 pt-3 border-t border-gray-100 flex justify-end gap-3">
                        <div className="text-xs text-gray-400 self-center mr-auto">
                            标准值: {standardPullForce}N <br/>
                            全部合格才可通过
                        </div>
                        <button 
                            className="bg-brand-600 text-white px-6 py-2 rounded-lg text-sm font-medium shadow-sm hover:bg-brand-700 active:scale-95 transition-all"
                            onClick={handleSave}
                        >
                            提交判定结果
                        </button>
                    </div>
                )}

                {record.status !== AuditStatus.PENDING && (
                    <div className="mt-2 text-right text-xs text-gray-400">
                        审核人: {record.auditorName}
                    </div>
                )}
            </div>
        </Card>
    );
};

const StatusBadge: React.FC<{status: AuditStatus}> = ({status}) => {
    const colors = {
        [AuditStatus.PENDING]: 'bg-yellow-100 text-yellow-700 border-yellow-200',
        [AuditStatus.PASSED]: 'bg-green-100 text-green-700 border-green-200',
        [AuditStatus.FAILED]: 'bg-red-100 text-red-700 border-red-200',
    };
    return (
        <span className={`text-xs px-2.5 py-0.5 rounded-full font-medium border ${colors[status]}`}>
            {status}
        </span>
    )
}

