interface LoginProps {
  onLogin: (user: User) => void;
}

export const Login: React.FC<LoginProps> = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  // 新增: isLoading 状态用于控制按钮是否可点击，防止重复提交
  const [isLoading, setIsLoading] = useState(false);

  /**
   * 处理登录逻辑
   * async/await 是处理异步请求的关键
   */
  const handleLogin = async () => {
    if(!username || !password) {
        setError("请输入账号和密码");
        return;
    }

    setIsLoading(true); // 开始加载，显示转圈或禁用按钮
    setError('');

    try {
      // 调用 services/api.ts 中的登录方法
      const user = await api.login(username, password);
      onLogin(user); // 登录成功，更新 App.tsx 的状态
    } catch (err: any) {
      // 捕获错误（如密码错误或网络中断）
      setError(err.message || '登录失败，请重试');
    } finally {
      setIsLoading(false); // 无论成功失败，都结束加载状态
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 px-4">
      <div className="bg-white p-8 rounded-2xl shadow-xl w-full max-w-sm space-y-6">
        <div className="text-center">
          <div className="w-16 h-16 bg-brand-100 text-brand-600 rounded-2xl mx-auto flex items-center justify-center mb-4">
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-8 h-8">
              <path strokeLinecap="round" strokeLinejoin="round" d="M9 12.75L11.25 15 15 9.75M21 12c0 1.268-.63 2.39-1.593 3.068a3.745 3.745 0 01-1.043 3.296 3.745 3.745 0 01-3.296 1.043A3.745 3.745 0 0112 21c-1.268 0-2.39-.63-3.068-1.593a3.746 3.746 0 01-3.296-1.043 3.745 3.745 0 01-1.043-3.296A3.745 3.745 0 013 12c0-1.268.63-2.39 1.593-3.068a3.745 3.745 0 011.043-3.296 3.746 3.746 0 013.296-1.043A3.746 3.746 0 0112 3c1.268 0 2.39.63 3.068 1.593a3.746 3.746 0 013.296 1.043 3.746 3.746 0 011.043 3.296A3.745 3.745 0 0121 12z" />
            </svg>
          </div>
          <h1 className="text-2xl font-bold text-gray-900">质量控制系统</h1>
          <p className="text-sm text-gray-500 mt-2">请使用员工或检验员账号登录</p>
        </div>

        <div className="space-y-4">
            <Input 
                label="账号" 
                placeholder="请输入账号" 
                value={username}
                onChange={e => setUsername(e.target.value)}
                disabled={isLoading} 
            />
            <Input 
                label="密码" 
                type="password"
                placeholder="请输入密码"
                value={password}
                onChange={e => setPassword(e.target.value)}
                disabled={isLoading}
            />
        </div>

        {error && <div className="bg-red-50 text-red-500 text-sm p-3 rounded-lg text-center">{error}</div>}

        <button 
            onClick={handleLogin}
            disabled={isLoading}
            className={`w-full py-3.5 rounded-xl font-bold text-white transition-colors shadow-lg 
                ${isLoading ? 'bg-gray-400 cursor-not-allowed' : 'bg-brand-600 hover:bg-brand-700 shadow-brand-500/30'}`}
        >
            {isLoading ? '登录中...' : '立即登录'}
        </button>
      </div>
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { ProductionOrder, SubmissionType, AuditStatus, InspectionRecord, UserRole, TerminalSample, User } from '../../types';
import { CreateOrder } from './CreateOrder';
import { Card } from '../../components/Card';
import { TERMINAL_SPECS, WIRE_SPECS } from '../../constants';
import { api } from '../../services/api'; // 引入 API

interface CrimpingDashboardProps {
  currentUser: User;
}

export const CrimpingDashboard: React.FC<CrimpingDashboardProps> = ({ currentUser }) => {
  const [view, setView] = useState<'LIST' | 'CREATE'>('LIST');

  // 初始化为空数组，不再使用模拟数据
  const [orders, setOrders] = useState<ProductionOrder[]>([]); 

  // 新增: 加载状态，用于显示 Loading 提示
  const [loading, setLoading] = useState(true);

  const [selectedOrder, setSelectedOrder] = useState<ProductionOrder | null>(null);

  /**
   * useEffect (React Hook) 学习重点:
   * 这是一个“副作用”钩子。它的作用是当组件“挂载”（即第一次显示在屏幕上）时执行代码。
   * 这里我们用它来从后端获取数据。
   * 
   * 空数组 [] 作为第二个参数意味着：只在组件加载时执行一次。
   */
  useEffect(() => {
    loadData();
  }, []);

  // 封装获取数据的函数
  const loadData = async () => {
    try {
      setLoading(true);
      const data = await api.getOrders(); // 调用 API
      setOrders(data);
    } catch (error) {
      console.error("获取订单失败", error);
      alert("无法连接服务器获取数据");
    } finally {
      setLoading(false); // 数据回来后，取消加载状态
    }
  };

  const handleCreateOrder = async (newOrder: ProductionOrder) => {
    try {
        // 1. 调用 API 保存到后端
        const savedOrder = await api.createOrder(newOrder);

        // 2. 更新本地列表 (将新订单加到最前面)
        setOrders([savedOrder, ...orders]);

        // 3. 切换回列表视图
        setView('LIST');
    } catch (e) {
        alert("创建失败");
    }
  };

  const handleAddRecord = async (orderId: string, type: SubmissionType) => {
    // 构造新记录对象
    const newRecord: InspectionRecord = {
      id: Date.now().toString(), // 临时 ID，实际上后端生成更好
      orderId,
      type,
      submitterName: currentUser.name, 
      submittedAt: new Date().toISOString(),
      status: AuditStatus.PENDING,
      samples: [ { id: 1 }, { id: 2 }, { id: 3 } ]
    };

    try {
        // 1. 调用 API
        await api.addRecord(orderId, newRecord);

        // 2. 乐观更新 UI (Optimistic UI Update)
        // 意思是：假设后端一定会成功，先更新界面，让用户感觉很快。
        setOrders(prev => prev.map(o => {
            if (o.id === orderId) {
                return { ...o, records: [newRecord, ...o.records] };
            }
            return o;
        }));
    } catch (e) {
        alert("提交记录失败");
    }
  };

  const handleAuditRecord = async (orderId: string, recordId: string, samples: TerminalSample[]) => {
    const allPassed = samples.every(s => s.isPassed);
    const newStatus = allPassed ? AuditStatus.PASSED : AuditStatus.FAILED;

    try {
        // 1. 调用 API
        await api.auditRecord(recordId, samples, currentUser.name, newStatus);

        // 2. 更新本地状态
        setOrders(prev => prev.map(o => {
            if (o.id === orderId) {
                const updatedRecords = o.records.map(r => {
                if (r.id === recordId) {
                    return {
                        ...r,
                        status: newStatus,
                        samples: samples,
                        auditorName: currentUser.name,
                        auditedAt: new Date().toISOString(),
                    };
                }
                return r;
                });
                return { ...o, records: updatedRecords };
            }
            return o;
        }));
    } catch (e) {
        alert("审核提交失败");
    }
  };

  if (view === 'CREATE') {
    return (
      <>
        <div className="mb-4">
             <button onClick={() => setView('LIST')} className="text-sm text-gray-500 flex items-center gap-1">
                &larr; 返回列表
             </button>
        </div>
        {/* 注意：这里的 dailyCounter 也应该从后端 API 获取最新的 */}
        <CreateOrder 
            onSubmit={handleCreateOrder} 
            dailyCounter={orders.length + 1} 
            creatorName={currentUser.name}
        />
      </>
    );
  }

  // 详情视图
  if (selectedOrder) {
    const liveOrder = orders.find(o => o.id === selectedOrder.id) || selectedOrder;
    return (
      <OrderDetailView 
        order={liveOrder} 
        onBack={() => setSelectedOrder(null)}
        onAddRecord={handleAddRecord}
        onAuditRecord={handleAuditRecord}
        currentUser={currentUser}
      />
    );
  }

  // 列表视图
  return (
    <div className="space-y-4">
      {/* 只有员工能看见创建按钮 */}
      {currentUser.role === UserRole.EMPLOYEE && (
        <button 
            onClick={() => setView('CREATE')}
            className="w-full bg-white border-2 border-dashed border-gray-300 text-gray-500 py-4 rounded-xl font-medium hover:bg-gray-50 hover:border-brand-300 hover:text-brand-500 transition-colors"
        >
            + 新增生产订单
        </button>
      )}

      {/* 加载中状态 */}
      {loading ? (
        <div className="text-center py-10 text-gray-400">
            <div className="animate-spin w-6 h-6 border-2 border-brand-500 border-t-transparent rounded-full mx-auto mb-2"></div>
            正在加载数据...
        </div>
      ) : orders.length === 0 ? (
        <div className="text-center py-10 text-gray-400">
          <p>暂无订单记录</p>
        </div>
      ) : (
        orders.map(order => (
          <Card key={order.id} onClick={() => setSelectedOrder(order)} className="border-l-4 border-l-brand-500">
            <div className="flex justify-between items-start">
              <div>
                <h3 className="font-bold text-gray-900">{order.productName}</h3>
                <p className="text-xs text-gray-500 font-mono mt-1">{order.id}</p>
              </div>
              <span className={`px-2 py-1 text-xs rounded-md ${
                order.records.length > 0 && order.records[0].status === AuditStatus.PENDING 
                ? 'bg-yellow-100 text-yellow-700'
                : 'bg-green-100 text-green-700'
              }`}>
                {order.records.length} 条记录
              </span>
            </div>
            <div className="mt-3 flex gap-2 text-xs text-gray-500">
                <span>标准: {order.standardPullForce}N</span>
                <span>•</span>
                <span>工单: {order.productionOrderNo}</span>
            </div>
          </Card>
        ))
      )}
    </div>
  );
};

// --- 下面的子组件代码保持基本不变，主要负责展示 UI ---
const OrderDetailView: React.FC<{
  order: ProductionOrder;
  onBack: () => void;
  onAddRecord: (orderId: string, type: SubmissionType) => void;
  onAuditRecord: (orderId: string, recordId: string, samples: TerminalSample[]) => void;
  currentUser: User;
