// 单条记录卡片组件
const InspectionCard: React.FC<{
    record: InspectionRecord;
    standardPullForce: number;
    isAuditor: boolean;
    onAuditSubmit: (samples: TerminalSample[]) => void;
}> = ({ record, standardPullForce, isAuditor, onAuditSubmit }) => {

    // 编辑状态：用于审核员输入测试值
    const [editSamples, setEditSamples] = useState<TerminalSample[]>(
        record.samples.map(s => ({ ...s }))
    );

    const isPending = record.status === AuditStatus.PENDING;
    // 只有当是审核员，且记录处于“待审核”状态时，才允许编辑
    const canAudit = isAuditor && isPending;

    // 处理输入变化
    const handleValueChange = (index: number, valStr: string) => {
        const val = parseFloat(valStr);
        const newSamples = [...editSamples];
        // 自动判定：如果输入值 >= 标准值，则该样品合格
        newSamples[index] = {
            ...newSamples[index],
            measuredForce: isNaN(val) ? undefined : val,
            isPassed: !isNaN(val) ? val >= standardPullForce : false
        };
        setEditSamples(newSamples);
    };

    const handleSave = () => {
        // 校验：必须填写所有值
        if (editSamples.some(s => s.measuredForce === undefined)) {
            alert("请填写所有端子的测试值");
            return;
        }
        onAuditSubmit(editSamples);
    };

    // 显示逻辑：如果是审核模式，显示本地的 editSamples；否则显示记录里的原始 samples
    const displaySamples = canAudit ? editSamples : record.samples;

    return (
        <Card className={`overflow-hidden p-0 ${isPending ? 'border-yellow-200 ring-4 ring-yellow-50' : 'border-gray-200'}`}>
            {/* 卡片头部 */}
            <div className={`px-4 py-3 border-b flex justify-between items-center ${isPending ? 'bg-yellow-50' : 'bg-gray-50'}`}>
                <div className="flex items-center gap-2">
                    <span className={`px-2 py-1 rounded text-xs font-bold ${
                        record.type === SubmissionType.FIRST_PIECE ? 'bg-purple-600 text-white' : 'bg-blue-600 text-white'
                    }`}>
                        {record.type}
                    </span>
                    <div className="flex flex-col">
                        <span className="text-xs text-gray-500 leading-tight">提交人: {record.submitterName}</span>
                        <span className="text-[10px] text-gray-400 leading-tight">{new Date(record.submittedAt).toLocaleString()}</span>
                    </div>
                </div>
                <StatusBadge status={record.status} />
            </div>

            {/* 内容表格 */}
            <div className="p-4">
                <table className="w-full text-sm">
                    <thead>
                        <tr className="text-left text-xs text-gray-500 border-b border-gray-100">
                            <th className="pb-2 font-medium w-16">端子样件</th>
                            <th className="pb-2 font-medium">拉力实测 (N)</th>
                            <th className="pb-2 font-medium text-center">结论</th>
                            <th className="pb-2 font-medium text-right text-xs">日期</th>
                        </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-50">
                        {displaySamples.map((sample, idx) => {
                            const dateStr = record.auditedAt 
                                ? new Date(record.auditedAt).toISOString().slice(0, 10).replace(/-/g, '') 
                                : new Date().toISOString().slice(0, 10).replace(/-/g, '');

                            return (
                                <tr key={sample.id}>
                                    <td className="py-3 font-medium text-gray-700">端子 {sample.id}</td>
                                    <td className="py-3 pr-2">
                                        {canAudit ? (
                                            <input 
                                                type="number" 
                                                className={`w-full border rounded px-2 py-1.5 focus:ring-2 focus:ring-brand-500 focus:outline-none transition-colors ${
                                                    sample.measuredForce !== undefined && sample.measuredForce < standardPullForce ? 'border-red-300 bg-red-50 text-red-600' : 'border-gray-300'
                                                }`}
                                                placeholder="0.0"
                                                value={sample.measuredForce ?? ''}
                                                onChange={(e) => handleValueChange(idx, e.target.value)}
                                            />
                                        ) : (
                                            <span className="font-mono text-gray-900">{sample.measuredForce ?? '-'}</span>
                                        )}
                                    </td>
                                    <td className="py-3 text-center">
                                        <div className="flex items-center justify-center gap-1">
                                            {/* 合格图标 */}
                                            <div className={`flex items-center gap-1 ${sample.isPassed ? 'opacity-100' : 'opacity-20 grayscale'}`}>
                                                <div className="w-4 h-4 rounded-full bg-green-500 flex items-center justify-center">
                                                    <svg className="w-2.5 h-2.5 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path strokeLinecap="round" strokeLinejoin="round" d="M5 13l4 4L19 7" /></svg>
                                                </div>
                                                <span className="text-xs text-green-700 font-medium hidden sm:inline">合格</span>
                                            </div>
                                            {/* 不合格图标 */}
                                            <div className={`flex items-center gap-1 ${sample.isPassed === false ? 'opacity-100' : 'opacity-20 grayscale'}`}>
                                                <div className="w-4 h-4 rounded-full bg-red-500 flex items-center justify-center">
                                                    <svg className="w-2.5 h-2.5 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" /></svg>
                                                </div>
                                                <span className="text-xs text-red-700 font-medium hidden sm:inline">不合格</span>
                                            </div>
                                        </div>
                                    </td>
                                    <td className="py-3 text-right text-xs text-gray-400 font-mono">
                                        {record.status !== AuditStatus.PENDING || canAudit ? dateStr : '-'}
                                    </td>
                                </tr>
                            );
                        })}
                    </tbody>
                </table>

                {/* 提交判定按钮区 */}
                {canAudit && (
                    <div className="mt-4 pt-3 border-t border-gray-100 flex justify-end gap-3">
                        <div className="text-xs text-gray-400 self-center mr-auto">
                            标准值: {standardPullForce}N <br/>
                            全部合格才可通过
                        </div>
                        <button 
                            className="bg-brand-600 text-white px-6 py-2 rounded-lg text-sm font-medium shadow-sm hover:bg-brand-700 active:scale-95 transition-all"
                            onClick={handleSave}
                        >
                            提交判定结果
                        </button>
                    </div>
                )}

                {record.status !== AuditStatus.PENDING && (
                    <div className="mt-2 text-right text-xs text-gray-400">
                        审核人: {record.auditorName}
                    </div>
                )}
            </div>
        </Card>
    );
};

const StatusBadge: React.FC<{status: AuditStatus}> = ({status}) => {
    const colors = {
        [AuditStatus.PENDING]: 'bg-yellow-100 text-yellow-700 border-yellow-200',
        [AuditStatus.PASSED]: 'bg-green-100 text-green-700 border-green-200',
        [AuditStatus.FAILED]: 'bg-red-100 text-red-700 border-red-200',
    };
    return (
        <span className={`text-xs px-2.5 py-0.5 rounded-full font-medium border ${colors[status]}`}>
            {status}
        </span>
    )
}

import React, { useState, useEffect } from 'react';
import { Input } from '../../components/Input';
import { Select } from '../../components/Select';
import { ProductionOrder } from '../../types';
import { TERMINAL_SPECS, WIRE_SPECS, STANDARD_MAPPINGS } from '../../constants';

interface CreateOrderProps {
  onSubmit: (order: ProductionOrder) => void;
  dailyCounter: number;
  creatorName: string;
}

export const CreateOrder: React.FC<CreateOrderProps> = ({ onSubmit, dailyCounter, creatorName }) => {
  const [formData, setFormData] = useState({
    productName: '',
    productModel: '',
    productionOrderNo: '',
    toolNo: '',
    terminalSpecId: '',
    wireSpecId: '',
  });

  const [generatedId, setGeneratedId] = useState('');
  const [standardPullForce, setStandardPullForce] = useState<number | ''>('');

  /**
   * 自动生成 ID 逻辑
   * 注意：在正式的生产环境中，ID 最好由后端生成，以防止并发冲突（两个人在同一微秒点击）。
   * 但为了用户体验，前端可以先生成一个“预览”ID。
   */
  useEffect(() => {
    if (formData.productionOrderNo) {
      const today = new Date();
      const dateStr = today.getFullYear().toString() + 
                      (today.getMonth() + 1).toString().padStart(2, '0') + 
                      today.getDate().toString().padStart(2, '0');
      // dailyCounter 是从父组件传来的，父组件应该从 API 获取当天最新的序号
      const counterStr = dailyCounter.toString().padStart(3, '0');

      setGeneratedId(`${formData.productionOrderNo}-${dateStr}-${counterStr}`);
    } else {
      setGeneratedId('等待输入生产工单号...');
    }
  }, [formData.productionOrderNo, dailyCounter]);

  // 自动计算标准拉力值
  useEffect(() => {
    if (formData.terminalSpecId && formData.wireSpecId) {
      const mapping = STANDARD_MAPPINGS.find(
        m => m.terminalId === formData.terminalSpecId && m.wireId === formData.wireSpecId
      );
      setStandardPullForce(mapping ? mapping.standardPullForce : 0);
    } else {
      setStandardPullForce('');
    }
  }, [formData.terminalSpecId, formData.wireSpecId]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = () => {
    if (!formData.productionOrderNo || !formData.terminalSpecId || !standardPullForce) {
      alert("请填写完整信息");
      return;
    }

    const newOrder: ProductionOrder = {
      id: generatedId,
      productName: formData.productName,
      productModel: formData.productModel,
      productionOrderNo: formData.productionOrderNo,
      toolNo: formData.toolNo,
      terminalSpecId: formData.terminalSpecId,
      wireSpecId: formData.wireSpecId,
      standardPullForce: Number(standardPullForce),
      creatorName: creatorName,
      createdAt: new Date().toISOString(),
      records: []
    };

    onSubmit(newOrder);
  };

  return (
    <div className="space-y-4 pb-20">
