/**
 * API 服务层
 * 负责所有与后端服务器的数据交换。
 * 
 * 后端地址: http://localhost:5075
 * 
 * 学习笔记:
 * 1. fetch: 现代浏览器用于发起网络请求的函数。
 * 2. async/await: 让异步代码（网络请求）看起来像同步代码，更易读。
 * 3. JSON.stringify: 将 JavaScript 对象转换为 JSON 字符串发送给服务器。
 * 4. response.json(): 将服务器返回的 JSON 字符串转换回 JavaScript 对象。
 */

const API_BASE_URL = 'http://localhost:5075/api'; // 假设您的 Controller 路由以 /api 开头

// 辅助函数：统一处理 HTTP 响应
const handleResponse = async (response: Response) => {
  if (!response.ok) {
    // 如果状态码不是 200-299，抛出错误
    const errorText = await response.text();
    throw new Error(errorText || `请求失败: ${response.status}`);
  }
  // 如果是 204 No Content (无返回内容)，直接返回 null
  if (response.status === 204) {
    return null;
  }
  return response.json();
};

export const api = {

  /**
   * 登录
   * 后端预期: POST /api/auth/login
   * Body: { username: string, password: string }
   * Return: { username: string, name: string, role: int (0 or 1) }
   */
  login: async (username: string, password: string): Promise<User> => {
    // 真实请求
    const response = await fetch(`${API_BASE_URL}/auth/login`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, password }),
    });

    const data = await handleResponse(response);

    // 数据转换 (Data Mapping)
    // 后端返回的 role 是 int (0: 员工, 1: 检验员)
    // 前端使用的是枚举 UserRole ('EMPLOYEE', 'AUDITOR')
    // 这里需要进行转换，以便前端代码更易读
    const mappedRole = data.role === 1 ? UserRole.AUDITOR : UserRole.EMPLOYEE;

    return {
      username: data.username,
      name: data.name || username, // 如果后端没返回昵称，用账号代替
      role: mappedRole,
      token: data.token // 如果有 JWT
    };
  },

  /**
   * 获取订单列表
   * 后端预期: GET /api/orders
   */
  getOrders: async (): Promise<ProductionOrder[]> => {
    const response = await fetch(`${API_BASE_URL}/orders`, {
      method: 'GET',
      headers: {
        'Accept': 'application/json'
        // 如果后端需要鉴权，这里通常需要添加 'Authorization': `Bearer ${token}`
      }
    });
    return handleResponse(response);
  },

  /**
   * 创建订单
   * 后端预期: POST /api/orders
   */
  createOrder: async (order: ProductionOrder): Promise<ProductionOrder> => {
    const response = await fetch(`${API_BASE_URL}/orders`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(order),
    });
    return handleResponse(response);
  },

  /**
   * 提交检验记录
   * 后端预期: POST /api/orders/{orderId}/records
   */
  addRecord: async (orderId: string, record: InspectionRecord): Promise<void> => {
    const response = await fetch(`${API_BASE_URL}/orders/${orderId}/records`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(record),
    });
    return handleResponse(response);
  },

  /**
   * 审核记录
   * 后端预期: PUT /api/records/{recordId}/audit
   */
  auditRecord: async (recordId: string, samples: TerminalSample[], auditorName: string, status: string): Promise<void> => {
    const response = await fetch(`${API_BASE_URL}/records/${recordId}/audit`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ 
        samples, 
        auditorName, 
        status, 
        auditedAt: new Date().toISOString() 
      }),
    });
    return handleResponse(response);
  }
};

