      setLoading(true);
      // 并行获取所有业务数据和基础配置数据
      const [fetchedOrders, fetchedTools, fetchedTerminals, fetchedWires, fetchedStandards] = await Promise.all([
        api.getOrders(),
        api.getCrimpingTools(),
        api.getTerminalSpecs(),
        api.getWireSpecs(),
        api.getPullForceStandards()
      ]);

      setOrders(fetchedOrders);
      setTools(fetchedTools);
      setTerminals(fetchedTerminals);
      setWires(fetchedWires);
      setStandards(fetchedStandards);
    } catch (error) {
      console.error("数据加载失败", error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateOrder = async (newOrder: ProductionOrder) => {
    try {
        const savedOrder = await api.createOrder(newOrder);
        // 重新获取列表以确保包含后端生成的所有字段
        const latestOrders = await api.getOrders();
        setOrders(latestOrders);
        setView('LIST');
    } catch (e) {
        alert("创建失败，请检查网络");
    }
  };

  const handleAddRecord = async (orderId: string, type: SubmissionType) => {
    // 构造符合 C# TerminalSample 的初始数据
    const initialSamples: TerminalSample[] = [
        { id: 0, sampleIndex: 1, measuredForce: 0, isPassed: false }, 
        { id: 0, sampleIndex: 2, measuredForce: 0, isPassed: false }, 
        { id: 0, sampleIndex: 3, measuredForce: 0, isPassed: false }
    ];

    const newRecord: InspectionRecord = {
      id: crypto.randomUUID(),
      orderId,
      type: type === SubmissionType.FIRST_PIECE ? "首件" : "末件",
      submitterName: currentUser.name, 
      submittedAt: new Date().toISOString(),
      status: AuditStatus.PENDING,
      samples: initialSamples
    };

    try {
        await api.addRecord(orderId, newRecord);
        // 乐观更新：直接修改本地 state，提升体验
        setOrders(prev => prev.map(o => {
            if (o.id === orderId) {
                return { ...o, records: [newRecord, ...o.records] };
            }
            return o;
        }));
    } catch (e) {
        alert("提交记录失败");
    }
  };

  const handleAuditRecord = async (orderId: string, recordId: string, samples: TerminalSample[]) => {
    // status: 0=Pending, 1=Passed, 2=Failed
    const allPassed = samples.every(s => s.isPassed);
    const newStatus = allPassed ? AuditStatus.PASSED : AuditStatus.FAILED;

    try {
        await api.auditRecord(recordId, samples, currentUser.name, newStatus);

        // 刷新整个列表以获取最新数据（或者做复杂的局部更新）
        const latestOrders = await api.getOrders();
        setOrders(latestOrders);

        // 如果当前正在查看详情，也更新选中的订单
        if (selectedOrder) {
            const updated = latestOrders.find(o => o.id === selectedOrder.id);
            if (updated) setSelectedOrder(updated);
        }

    } catch (e) {
        alert("审核提交失败");
    }
  };

  if (view === 'CREATE') {
    return (
      <>
        <div className="mb-4">
             <button onClick={() => setView('LIST')} className="text-sm text-gray-500 flex items-center gap-1">
                &larr; 返回列表
             </button>
        </div>
        <CreateOrder 
            onSubmit={handleCreateOrder} 
            dailyCounter={orders.length + 1} 
            creatorName={currentUser.name}
            tools={tools}
            terminals={terminals}
            wires={wires}
            standards={standards} // 传递标准表
        />
      </>
    );
  }

  if (selectedOrder) {
    // 确保从 orders 数组中获取最新的对象引用
    const liveOrder = orders.find(o => o.id === selectedOrder.id) || selectedOrder;
    return (
      <OrderDetailView 
        order={liveOrder} 
        onBack={() => setSelectedOrder(null)}
        onAddRecord={handleAddRecord}
        onAuditRecord={handleAuditRecord}
        currentUser={currentUser}
        tools={tools}
        terminals={terminals}
        wires={wires}
      />
    );
  }

  // 计算状态文本辅助函数
  const getStatusText = (status: AuditStatus) => {
    if (status === AuditStatus.PENDING) return '待审核';
    if (status === AuditStatus.PASSED) return '合格';
    if (status === AuditStatus.FAILED) return '不合格';
    return '-';
  };

  return (
    <div className="space-y-4">
      {currentUser.role === UserRole.EMPLOYEE && (
        <button 
            onClick={() => setView('CREATE')}
            className="w-full bg-white border-2 border-dashed border-gray-300 text-gray-500 py-4 rounded-xl font-medium hover:bg-gray-50 hover:border-brand-300 hover:text-brand-500 transition-colors"
        >
            + 新增生产订单
        </button>
      )}

      {loading ? (
        <div className="text-center py-10 text-gray-400">
            <div className="animate-spin w-6 h-6 border-2 border-brand-500 border-t-transparent rounded-full mx-auto mb-2"></div>
            正在加载数据...
        </div>
      ) : orders.length === 0 ? (
        <div className="text-center py-10 text-gray-400">
          <p>暂无订单记录</p>
        </div>
      ) : (
        orders.map(order => (
          <Card key={order.id} onClick={() => setSelectedOrder(order)} className="border-l-4 border-l-brand-500">
            <div className="flex justify-between items-start">
              <div>
                <h3 className="font-bold text-gray-900">{order.productName}</h3>
                <p className="text-xs text-gray-500 font-mono mt-1">{order.productionOrderNo}</p>
              </div>
              <span className={`px-2 py-1 text-xs rounded-md ${
                order.records.length > 0 && order.records[0].status === AuditStatus.PENDING 
                ? 'bg-yellow-100 text-yellow-700'
                : 'bg-green-100 text-green-700'
              }`}>
                {order.records.length} 条记录
              </span>
            </div>
            <div className="mt-3 flex gap-2 text-xs text-gray-500">
                <span>标准: {order.standardPullForce}N</span>
                <span>•</span>
                {/* 显示端子名称 */}
                <span>{terminals.find(t => String(t.id) === String(order.terminalSpecId))?.name || '未知端子'}</span>
            </div>
          </Card>
        ))
      )}
    </div>
  );
};

// --- OrderDetailView 和 InspectionCard 保持 UI 逻辑，增加类型匹配 ---
const OrderDetailView: React.FC<{
