const InspectionCard: React.FC<{
    record: InspectionRecord;
    standardPullForce: number;
    isAuditor: boolean;
    onAuditSubmit: (samples: TerminalSample[]) => void;
}> = ({ record, standardPullForce, isAuditor, onAuditSubmit }) => {

    // C# records might define Status as 0,1,2.
    const isPending = record.status === AuditStatus.PENDING;
    const canAudit = isAuditor && isPending;

    // Local state for editing values
    const [editSamples, setEditSamples] = useState<TerminalSample[]>(
        record.samples.map(s => ({ ...s }))
    );

    const handleValueChange = (index: number, valStr: string) => {
        const val = parseFloat(valStr);
        const newSamples = [...editSamples];
        newSamples[index] = {
            ...newSamples[index],
            measuredForce: isNaN(val) ? 0 : val,
            isPassed: !isNaN(val) ? val >= standardPullForce : false
        };
        setEditSamples(newSamples);
    };

    const handleSave = () => {
        // Simple validation
        if (editSamples.some(s => !s.measuredForce && s.measuredForce !== 0)) {
            alert("请填写所有端子的测试值");
            return;
        }
        onAuditSubmit(editSamples);
    };

    const displaySamples = canAudit ? editSamples : record.samples;

    return (
        <Card className={`overflow-hidden p-0 ${isPending ? 'border-yellow-200 ring-4 ring-yellow-50' : 'border-gray-200'}`}>
            <div className={`px-4 py-3 border-b flex justify-between items-center ${isPending ? 'bg-yellow-50' : 'bg-gray-50'}`}>
                <div className="flex items-center gap-2">
                    <span className={`px-2 py-1 rounded text-xs font-bold ${
                        record.type === "首件" ? 'bg-purple-600 text-white' : 'bg-blue-600 text-white'
                    }`}>
                        {record.type}
                    </span>
                    <div className="flex flex-col">
                        <span className="text-xs text-gray-500 leading-tight">提交人: {record.submitterName}</span>
                        <span className="text-[10px] text-gray-400 leading-tight">{new Date(record.submittedAt).toLocaleString()}</span>
                    </div>
                </div>
                <StatusBadge status={record.status} />
            </div>

            <div className="p-4">
                <table className="w-full text-sm">
                    <thead>
                        <tr className="text-left text-xs text-gray-500 border-b border-gray-100">
                            <th className="pb-2 font-medium w-16">样件</th>
                            <th className="pb-2 font-medium">拉力(N)</th>
                            <th className="pb-2 font-medium text-center">结论</th>
                        </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-50">
                        {displaySamples.map((sample, idx) => (
                            <tr key={idx}>
                                <td className="py-3 font-medium text-gray-700">#{sample.sampleIndex}</td>
                                <td className="py-3 pr-2">
                                    {canAudit ? (
                                        <input 
                                            type="number" 
                                            className="w-full border rounded px-2 py-1"
                                            value={sample.measuredForce || ''}
                                            onChange={(e) => handleValueChange(idx, e.target.value)}
                                        />
                                    ) : (
                                        <span className="font-mono text-gray-900">{sample.measuredForce}</span>
                                    )}
                                </td>
                                <td className="py-3 text-center">
                                    {sample.isPassed ? 
                                        <span className="text-green-600 text-xs">合格</span> : 
                                        <span className="text-red-500 text-xs">NG</span>}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                {canAudit && (
                    <div className="mt-4 text-right">
                         <button onClick={handleSave} className="bg-brand-600 text-white px-4 py-2 rounded text-sm">提交判定</button>
                    </div>
                )}
            </div>
        </Card>
    );
};

const StatusBadge: React.FC<{status: AuditStatus}> = ({status}) => {
    if(status === AuditStatus.PENDING) return <span className="text-xs bg-yellow-100 text-yellow-800 px-2 py-1 rounded">待审核</span>;
    if(status === AuditStatus.PASSED) return <span className="text-xs bg-green-100 text-green-800 px-2 py-1 rounded">合格</span>;
    return <span className="text-xs bg-red-100 text-red-800 px-2 py-1 rounded">不合格</span>;
}

import React, { useState, useEffect } from 'react';
import { Input } from '../../components/Input';
import { Select } from '../../components/Select';
import { ProductionOrder, CrimpingTool, TerminalSpec, WireSpec, PullForceStandard } from '../../types';

interface CreateOrderProps {
  onSubmit: (order: ProductionOrder) => void;
  dailyCounter: number;
  creatorName: string;
  tools: CrimpingTool[];
  terminals: TerminalSpec[];
  wires: WireSpec[];
  standards: PullForceStandard[]; // 新增：标准表
}

export const CreateOrder: React.FC<CreateOrderProps> = ({ 
    onSubmit, dailyCounter, creatorName, tools, terminals, wires, standards 
}) => {
  const [formData, setFormData] = useState({
    productName: '',
    productModel: '',
    productionOrderNo: '',
    toolNo: '', 
    terminalSpecId: '',
    wireSpecId: '',
  });

  const [generatedId, setGeneratedId] = useState('');
  const [standardPullForce, setStandardPullForce] = useState<number | ''>('');

  // 自动生成 ID (前端预览 PO-Date-xxx)
  useEffect(() => {
    if (formData.productionOrderNo) {
      const today = new Date();
      const dateStr = today.getFullYear().toString() + 
                      (today.getMonth() + 1).toString().padStart(2, '0') + 
                      today.getDate().toString().padStart(2, '0');
      const counterStr = dailyCounter.toString().padStart(3, '0');
      setGeneratedId(`${formData.productionOrderNo}-${dateStr}-${counterStr}`);
    } else {
      setGeneratedId('等待输入生产工单号...');
    }
  }, [formData.productionOrderNo, dailyCounter]);

  // --- 关键逻辑：前端匹配拉力标准 ---
  useEffect(() => {
    if (formData.terminalSpecId && formData.wireSpecId) {
        // 1. 找到选中的端子对象 (获取 Method)
        const selectedTerminal = terminals.find(t => String(t.id) === formData.terminalSpecId);
        // 2. 找到选中的导线对象 (获取 SectionArea)
        const selectedWire = wires.find(w => String(w.id) === formData.wireSpecId);

        if (selectedTerminal && selectedWire) {
            // 3. 在 standards 列表中查找匹配项
            // 匹配逻辑：Standard.Method == Terminal.Method 且 Standard.SectionArea == Wire.SectionArea
            const match = standards.find(s => 
                s.method === selectedTerminal.method && 
                s.sectionArea === selectedWire.sectionArea
            );

            if (match) {
                setStandardPullForce(match.standardValue);
            } else {
                setStandardPullForce(0); // 未找到匹配标准
                console.warn("未找到匹配的拉力标准", { 
                    method: selectedTerminal.method, 
                    area: selectedWire.sectionArea 
                });
            }
        }
    } else {
        setStandardPullForce('');
    }
  }, [formData.terminalSpecId, formData.wireSpecId, terminals, wires, standards]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = () => {
    if (!formData.productionOrderNo || !formData.terminalSpecId || !formData.toolNo || !formData.wireSpecId) {
      alert("请填写完整信息");
      return;
    }

    if (!standardPullForce) {
        if(!window.confirm("未找到对应的拉力标准值，是否强制提交？(标准值将为0)")) {
            return;
        }
    }

    const newOrder: ProductionOrder = {
      id: generatedId,
      productName: formData.productName,
      productModel: formData.productModel,
      productionOrderNo: formData.productionOrderNo,
      // 注意：C# ToolNo 是 string，这里我们传 ID string 或者 Model string
      // 根据您的数据库 ToolNo 是 varchar，建议存 Model 或 ID。这里存 ID。
      toolNo: formData.toolNo, 
      terminalSpecId: formData.terminalSpecId,
      wireSpecId: formData.wireSpecId,
      standardPullForce: Number(standardPullForce),
      creatorName: creatorName,
      createdAt: new Date().toISOString(),
      records: []
    };

    onSubmit(newOrder);
  };

  return (
    <div className="space-y-4 pb-20">
      <div className="bg-white p-4 rounded-xl shadow-sm border border-gray-100 space-y-4">
        <Input 
          label="编号 (预览)" 
          value={generatedId} 
          disabled 
          className="bg-gray-50 text-gray-500 font-mono"
        />

        <div className="grid grid-cols-2 gap-4">
            <Input 
                label="产品名称" 
                name="productName" 
                placeholder="输入产品名称" 
                value={formData.productName} 
                onChange={handleChange}
            />
            <Input 
                label="产品型号" 
                name="productModel" 
                placeholder="输入产品型号" 
                value={formData.productModel} 
                onChange={handleChange}
            />
        </div>

        <div className="grid grid-cols-2 gap-4">
            <Input 
                label="生产工单号" 
                name="productionOrderNo" 
                placeholder="输入生产工单号" 
                value={formData.productionOrderNo} 
                onChange={handleChange}
            />
            <Select 
                label="压接工具" 
                name="toolNo" 
                value={formData.toolNo} 
                onChange={handleChange}
                // 使用 Tool.Id 作为 value
                options={tools.map(t => ({ value: String(t.id), label: t.model }))}
            />
        </div>

        <div className="grid grid-cols-2 gap-4">
          <Select 
            label="端子规格" 
            name="terminalSpecId"
            options={terminals.map(t => ({ value: String(t.id), label: t.name }))}
            value={formData.terminalSpecId}
            onChange={handleChange}
          />
          <Select 
            label="导线规格" 
            name="wireSpecId"
            options={wires.map(w => ({ value: String(w.id), label: w.displayName }))}
            value={formData.wireSpecId}
            onChange={handleChange}
          />
        </div>

        <div className="grid grid-cols-2 gap-4">
          <Input 
            label="拉力标准值 (N)" 
            value={standardPullForce} 
            disabled 
            placeholder="自动计算"
            className="bg-gray-50 font-bold text-gray-700"
          />
           <Input 
            label="工作者" 
            value={creatorName} 
            disabled 
            className="bg-gray-50"
          />
        </div>
      </div>

      <button 
        onClick={handleSubmit}
        className="w-full bg-brand-600 text-white py-4 rounded-xl font-bold text-lg shadow-lg hover:bg-brand-700 active:scale-95 transition-all fixed bottom-6 left-4 right-4 max-w-[calc(100%-2rem)] md:max-w-[416px] mx-auto z-20"
      >
        新增订单
      </button>
      <div className="h-12"></div> 
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { Input } from '../../components/Input';
import { Select } from '../../components/Select';
import { ProductionOrder, CrimpingTool, TerminalSpec, WireSpec, PullForceStandard } from '../../types';

interface CreateOrderProps {
  onSubmit: (order: ProductionOrder) => void;
  dailyCounter: number;
  creatorName: string;
  tools: CrimpingTool[];
  terminals: TerminalSpec[];
  wires: WireSpec[];
  standards: PullForceStandard[]; 
}

export const CreateOrder: React.FC<CreateOrderProps> = ({ 
    onSubmit, dailyCounter, creatorName, tools, terminals, wires, standards 
}) => {
  const [formData, setFormData] = useState({
    productName: '',
    productModel: '',
    productionOrderNo: '',
    toolNo: '', 
    terminalSpecId: '',
    wireSpecId: '',
  });

  const [generatedId, setGeneratedId] = useState('');
  const [standardPullForce, setStandardPullForce] = useState<number | ''>('');

  // 用于界面展示的中间计算值
  const [matchInfo, setMatchInfo] = useState<{ method?: number; sectionArea?: number } | null>(null);

  // 自动生成 ID (前端预览 PO-Date-xxx)
  useEffect(() => {
    if (formData.productionOrderNo) {
      const today = new Date();
      const dateStr = today.getFullYear().toString() + 
                      (today.getMonth() + 1).toString().padStart(2, '0') + 
                      today.getDate().toString().padStart(2, '0');
      const counterStr = dailyCounter.toString().padStart(3, '0');
      setGeneratedId(`${formData.productionOrderNo}-${dateStr}-${counterStr}`);
    } else {
      setGeneratedId('等待输入生产工单号...');
    }
  }, [formData.productionOrderNo, dailyCounter]);

