    if (formData.terminalSpecId && formData.wireSpecId) {
        // 1. 找到选中的端子对象 (获取 Method)
        const selectedTerminal = terminals.find(t => String(t.id) === formData.terminalSpecId);
        // 2. 找到选中的导线对象 (获取 SectionArea)
        const selectedWire = wires.find(w => String(w.id) === formData.wireSpecId);

        if (selectedTerminal && selectedWire) {
            // 更新匹配信息展示
            setMatchInfo({
                method: selectedTerminal.method,
                sectionArea: selectedWire.sectionArea
            });

            // 3. 在 standards 列表中查找匹配项
            // 匹配逻辑：Standard.Method == Terminal.Method 且 Standard.SectionArea == Wire.SectionArea
            const match = standards.find(s => 
                s.method === selectedTerminal.method && 
                s.sectionArea === selectedWire.sectionArea
            );

            if (match) {
                setStandardPullForce(match.standardValue);
            } else {
                setStandardPullForce(0); // 未找到匹配标准
            }
        }
    } else {
        setStandardPullForce('');
        setMatchInfo(null);
    }
  }, [formData.terminalSpecId, formData.wireSpecId, terminals, wires, standards]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = () => {
    if (!formData.productionOrderNo || !formData.terminalSpecId || !formData.toolNo || !formData.wireSpecId) {
      alert("请填写完整信息");
      return;
