// ... 现有的引用

namespace QualityControlAPI.Services.Crimping
{
    public class CrimpingService
    {
        // ... 现有的代码 ...

        // --- 新增这个方法 ---
        public async Task AuditRecordAsync(string recordId, List<TerminalSample> newSamples, string auditorName, int status)
        {
            // 1. 获取记录，包含关联的 Samples
            var record = await _context.Records
                .Include(r => r.Samples)
                .FirstOrDefaultAsync(r => r.Id == recordId);

            if (record == null) throw new KeyNotFoundException();

            // 2. 更新主记录状态
            record.Status = status; // 1=合格, 2=不合格
            record.AuditorName = auditorName;
            record.AuditedAt = DateTime.Now;

            // 3. 更新样本数据
            // 策略：由于前端传回了完整的样本列表（包含实测值），
            // 简单做法是：删除旧样本，插入新样本（带数据的）
            // 或者：遍历更新（如果 ID 匹配）

            // 这里采用先清空旧样本，再添加新样本的方式，确保数据完全一致
            _context.TerminalSamples.RemoveRange(record.Samples);

            // 确保新样本关联到当前记录 ID
            foreach (var sample in newSamples)
            {
                sample.Id = 0; // 重置 ID 让数据库重新生成
                // 注意：如果您的 TerminalSample 模型里没有外键属性 InspectionRecordId，
                // EF Core 会通过 record.Samples.Add 自动处理。
                // 如果有外键属性，请手动赋值，例如: sample.InspectionRecordId = recordId;
            }

            record.Samples = newSamples;

            await _context.SaveChangesAsync();
        }
    }
}
