        public DbSet<TerminalSpec> TerminalSpecs { get; set; }
        public DbSet<WireSpec> WireSpecs { get; set; }
        public DbSet<PullForceStandard> PullForceStandards { get; set; }

        // 改名: Orders -> ProductionOrders
        public DbSet<ProductionOrder> ProductionOrders { get; set; }
        // 改名: Records -> InspectionRecords
        public DbSet<InspectionRecord> InspectionRecords { get; set; }
        // 改名: Samples -> TerminalSamples
        public DbSet<TerminalSample> TerminalSamples { get; set; }

        protected override void OnModelCreating(ModelBuilder modelBuilder)
        {
            base.OnModelCreating(modelBuilder);

            // --- 2. 配置精度 (对应 SQL 中的 DECIMAL 类型) ---

            // 导线截面积 DECIMAL(6,2)
            modelBuilder.Entity<WireSpec>()
