import { ProductionOrder, User, InspectionRecord, UserRole, TerminalSample, CrimpingTool, TerminalSpec, WireSpec, PullForceStandard } from '../types';

// C# 后端地址
const API_BASE_URL = 'http://localhost:5075/api';

const handleResponse = async (response: Response) => {
  // 1. 处理成功响应 (200-299)
  if (response.ok) {
    if (response.status === 204) return null;

    const text = await response.text();
    // 兼容 C# return Ok() 空包体的情况
    if (!text) return null; 

    try {
      return JSON.parse(text);
    } catch (e) {
      console.warn("Response was not valid JSON but request succeeded:", text);
      return null;
    }
  }

  // 2. 处理错误响应
  const errorText = await response.text();
  console.error(`API Error (${response.status}):`, errorText);

  let errorMessage = `请求失败: ${response.status}`;

  if (errorText) {
    try {
        const jsonError = JSON.parse(errorText);
        // 尝试提取 C# 标准错误格式
        // 优先顺序: message -> title (ValidationProblem) -> error
        const msg = jsonError.message || jsonError.title || jsonError.error;

        // 提取详细验证错误 errors: { "field": ["msg"] }
        let details = '';
        if (jsonError.errors) {
            details = Object.entries(jsonError.errors)
                .map(([key, msgs]) => `${key}: ${(msgs as any[]).join(', ')}`)
                .join('; ');
        }

        if (msg) {
            errorMessage = details ? `${msg} (${details})` : msg;
        } else if (details) {
            errorMessage = details;
        }
    } catch (e) {
        // 如果无法解析为 JSON (例如 HTML 404 页面或纯文本)
        // 如果是短文本，直接显示；如果是 HTML，只显示状态码
        if (errorText.length < 500 && !errorText.trim().startsWith('<')) {
            errorMessage = `${errorMessage}: ${errorText}`;
        }
    }
  }

  throw new Error(errorMessage);
};

export const api = {
  // --- AuthController ---
  login: async (username: string, password: string): Promise<User> => {
    const response = await fetch(`${API_BASE_URL}/Auth/login`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, password }),
    });
    const data = await handleResponse(response);

    // C# User Model: Role is int (0=Employee, 1=Auditor)
