import { ProductionOrder, User, InspectionRecord, UserRole, TerminalSample, CrimpingTool, TerminalSpec, WireSpec, PullForceStandard } from '../types';

// C# 后端地址
const API_BASE_URL = 'http://localhost:5075/api';

const handleResponse = async (response: Response) => {
  // 1. 处理成功响应 (200-299)
  if (response.ok) {
    if (response.status === 204) return null;

    const text = await response.text();
    // 兼容 C# return Ok() 空包体的情况
    if (!text) return null; 

    try {
      return JSON.parse(text);
    } catch (e) {
      console.warn("Response was not valid JSON but request succeeded:", text);
      return null;
    }
  }

  // 2. 处理错误响应
  const errorText = await response.text();
  console.error(`API Error (${response.status}):`, errorText);

  let errorMessage = `请求失败: ${response.status}`;

  if (errorText) {
    try {
        const jsonError = JSON.parse(errorText);
        // 尝试提取 C# 标准错误格式
        // 优先顺序: message -> title (ValidationProblem) -> error
        const msg = jsonError.message || jsonError.title || jsonError.error;

        // 提取详细验证错误 errors: { "field": ["msg"] }
        let details = '';
        if (jsonError.errors) {
            details = Object.entries(jsonError.errors)
                .map(([key, msgs]) => `${key}: ${(msgs as any[]).join(', ')}`)
                .join('; ');
        }

        if (msg) {
            errorMessage = details ? `${msg} (${details})` : msg;
        } else if (details) {
            errorMessage = details;
        }
    } catch (e) {
        // 如果无法解析为 JSON (例如 HTML 404 页面或纯文本)
        // 如果是短文本，直接显示；如果是 HTML，只显示状态码
        if (errorText.length < 500 && !errorText.trim().startsWith('<')) {
            errorMessage = `${errorMessage}: ${errorText}`;
        }
    }
  }

  throw new Error(errorMessage);
};

export const api = {
  // --- AuthController ---
  login: async (username: string, password: string): Promise<User> => {
    const response = await fetch(`${API_BASE_URL}/Auth/login`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, password }),
    });
    const data = await handleResponse(response);

    // C# User Model: Role is int (0=Employee, 1=Auditor)
    // 前端 Types: Role is Enum
    const mappedRole = data.role === 1 ? UserRole.AUDITOR : UserRole.EMPLOYEE;

    return {
      username: data.employeeId, // 使用工号作为 username
      name: data.name,
      role: mappedRole,
      token: undefined // 后端暂未返回 Token，如果返回了可加在这里
    };
  },

  // --- ConfigController ---

  getCrimpingTools: async (): Promise<CrimpingTool[]> => {
    const response = await fetch(`${API_BASE_URL}/config/tools`);
    return handleResponse(response);
  },

  getTerminalSpecs: async (): Promise<TerminalSpec[]> => {
    const response = await fetch(`${API_BASE_URL}/config/terminals`);
    return handleResponse(response);
  },

  getWireSpecs: async (): Promise<WireSpec[]> => {
    const response = await fetch(`${API_BASE_URL}/config/wires`);
    return handleResponse(response);
  },

  /** 
   * 获取所有拉力标准
   * 前端拿到这个列表后，结合 Terminal.Method 和 Wire.SectionArea 进行匹配
   */
  getPullForceStandards: async (): Promise<PullForceStandard[]> => {
    const response = await fetch(`${API_BASE_URL}/config/standards`);
    return handleResponse(response);
  },

  // --- OrdersController ---

  getOrders: async (): Promise<ProductionOrder[]> => {
    // 增加时间戳防止浏览器缓存 GET 请求
    const timestamp = new Date().getTime();
    const response = await fetch(`${API_BASE_URL}/Orders?_t=${timestamp}`, {
      headers: {
        'Cache-Control': 'no-cache',
        'Pragma': 'no-cache'
      }
    });
    return handleResponse(response);
  },

  createOrder: async (order: ProductionOrder): Promise<ProductionOrder> => {
    const response = await fetch(`${API_BASE_URL}/Orders`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(order),
    });
    return handleResponse(response);
  },

  // 修改返回类型为 Promise<void>，因为后端 Ok() 没有返回 Body
  addRecord: async (orderId: string, record: InspectionRecord): Promise<void> => {
    const response = await fetch(`${API_BASE_URL}/Orders/${orderId}/records`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(record),
    });
    return handleResponse(response);
  },

  /**
   * 审核记录
   * 对应后端 RecordAuditDto 结构
   */
  auditRecord: async (recordId: string, samples: TerminalSample[], auditorName: string, status: number, auditNote?: string): Promise<void> => {
    const response = await fetch(`${API_BASE_URL}/Orders/records/${recordId}/audit`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ 
        samples,      // 对应 RecordAuditDto.Samples (List<SampleUpdateDto>)
        auditorName,  // 对应 RecordAuditDto.AuditorName
        status,       // 对应 RecordAuditDto.Status
        auditNote     // 对应 RecordAuditDto.AuditNote
      }),
    });
    return handleResponse(response);
  },

  /**
   * 关闭/结束订单
   */
  closeOrder: async (orderId: string): Promise<void> => {
    // 后端接口：[HttpPatch("{id}/close")] public async Task<IActionResult> CloseOrder(string id, [FromBody] bool isClosed)
    // 注意：FromBody 接收一个 boolean 值
    // 某些 .NET 配置下，FromBody bool 需要直接传 true/false，Content-Type: application/json
    const response = await fetch(`${API_BASE_URL}/Orders/${orderId}/close`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(true), // 传 true 表示关闭
    });
    return handleResponse(response);
  }
};

import React, { useState, useEffect } from 'react';
import { ProductionOrder, SubmissionType, AuditStatus, InspectionRecord, UserRole, TerminalSample, User, CrimpingTool, TerminalSpec, WireSpec, PullForceStandard } from '../../types';
import { CreateOrder } from './CreateOrder';
import { Card } from '../../components/Card';
import { api } from '../../services/api'; 

interface CrimpingDashboardProps {
  currentUser: User;
}

export const CrimpingDashboard: React.FC<CrimpingDashboardProps> = ({ currentUser }) => {
  const [view, setView] = useState<'LIST' | 'CREATE'>('LIST');
  const [orders, setOrders] = useState<ProductionOrder[]>([]); 
  const [loading, setLoading] = useState(true);
  const [selectedOrder, setSelectedOrder] = useState<ProductionOrder | null>(null);

  // 基础数据状态
  const [tools, setTools] = useState<CrimpingTool[]>([]);
  const [terminals, setTerminals] = useState<TerminalSpec[]>([]);
  const [wires, setWires] = useState<WireSpec[]>([]);
  const [standards, setStandards] = useState<PullForceStandard[]>([]);

  // 增加关闭操作的 Loading 状态
  const [isClosing, setIsClosing] = useState(false);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      // 并行获取所有业务数据和基础配置数据
      const [fetchedOrders, fetchedTools, fetchedTerminals, fetchedWires, fetchedStandards] = await Promise.all([
        api.getOrders(),
        api.getCrimpingTools(),
        api.getTerminalSpecs(),
        api.getWireSpecs(),
        api.getPullForceStandards()
      ]);

      setOrders(fetchedOrders);
      setTools(fetchedTools);
      setTerminals(fetchedTerminals);
      setWires(fetchedWires);
      setStandards(fetchedStandards);
    } catch (error) {
      console.error("数据加载失败", error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateOrder = async (newOrder: ProductionOrder) => {
    try {
        const savedOrder = await api.createOrder(newOrder);
        // 重新获取列表以确保包含后端生成的所有字段
        const latestOrders = await api.getOrders();
        setOrders(latestOrders);
        setView('LIST');
    } catch (e: any) {
        alert(`创建失败: ${e.message}`);
    }
  };

  const handleAddRecord = async (orderId: string, type: SubmissionType) => {
    // 构造符合 C# TerminalSample 的初始数据
    const initialSamples: TerminalSample[] = [
        { id: 0, sampleIndex: 1, measuredForce: 0, isPassed: false }, 
        { id: 0, sampleIndex: 2, measuredForce: 0, isPassed: false }, 
        { id: 0, sampleIndex: 3, measuredForce: 0, isPassed: false }
    ];

    // 生成 UUID (如果浏览器支持 crypto.randomUUID 则使用，否则使用简单回退)
    const generateId = () => {
        if (typeof crypto !== 'undefined' && crypto.randomUUID) {
            return crypto.randomUUID();
        }
        return Date.now().toString(36) + Math.random().toString(36).substring(2);
    };

    const newRecord: InspectionRecord = {
      id: generateId(), 
      orderId,
      type: type === SubmissionType.FIRST_PIECE ? "首件" : "末件",
      submitterName: currentUser.name, 
      submittedAt: new Date().toISOString(),
      status: AuditStatus.PENDING,
      samples: initialSamples,
      auditNote: null // 初始化为空
    };

    try {
        await api.addRecord(orderId, newRecord);
        // 成功后重新加载数据
        await loadData();

        // 如果当前正在查看某个订单详情，也需要刷新选中的订单状态
        if (selectedOrder) {
            const latestOrders = await api.getOrders();
            const updated = latestOrders.find(o => o.id === selectedOrder.id);
            if (updated) setSelectedOrder(updated);
        }

    } catch (e: any) {
        console.error(e);
        // 显示具体的后端错误信息
        alert(`提交失败: ${e.message}`);
    }
  };

  const handleAuditRecord = async (orderId: string, recordId: string, samples: TerminalSample[]) => {
    // status: 0=Pending, 1=Passed, 2=Failed
    const allPassed = samples.every(s => s.isPassed);
    const newStatus = allPassed ? AuditStatus.PASSED : AuditStatus.FAILED;

    try {
        // 传递 null 作为 auditNote (或者后续可以添加 UI 让用户输入备注)
        await api.auditRecord(recordId, samples, currentUser.name, newStatus, null);

        // 刷新整个列表以获取最新数据
        const latestOrders = await api.getOrders();
        setOrders(latestOrders);

        // 如果当前正在查看详情，也更新选中的订单
        if (selectedOrder) {
            const updated = latestOrders.find(o => o.id === selectedOrder.id);
            if (updated) setSelectedOrder(updated);
        }

    } catch (e: any) {
        alert(`审核提交失败: ${e.message}`);
    }
  };

  const handleCloseOrder = async (orderId: string) => {
      if(!window.confirm("【警告】\n\n您确定要结束此生产订单吗？\n\n一旦结束，该订单将进入归档状态，所有人员将无法再提交数据或进行审核操作。")) {
          return;
      }

      setIsClosing(true);

      try {
          await api.closeOrder(orderId);

          // 1. 本地乐观更新 (Optimistic Update): 立即更新 UI，不需要等待 API 返回
          // 这样即使 API 刷新因为缓存原因没有拿到最新状态，用户也能看到变化
          const updatedOrders = orders.map(o => o.id === orderId ? { ...o, isClosed: true } : o);
          setOrders(updatedOrders);
          if (selectedOrder && selectedOrder.id === orderId) {
             setSelectedOrder({ ...selectedOrder, isClosed: true });
          }

          // 2. 后台刷新数据确保同步
          const latestOrders = await api.getOrders();
          setOrders(latestOrders);

          if (selectedOrder) {
            const updated = latestOrders.find(o => o.id === selectedOrder.id);
            // 只有当服务端数据确实有变化时才覆盖，保留乐观更新的结果防止闪烁
            if (updated && updated.isClosed) setSelectedOrder(updated);
          }
      } catch (e: any) {
          alert(`关闭订单失败: ${e.message}`);
          // 如果失败，这里需要回滚乐观更新（此处简化处理，刷新列表即可）
          await loadData();
      } finally {
          setIsClosing(false);
      }
  };

  if (view === 'CREATE') {
    return (
      <>
        <div className="mb-4">
             <button onClick={() => setView('LIST')} className="text-sm text-gray-500 flex items-center gap-1">
                &larr; 返回列表
             </button>
        </div>
        <CreateOrder 
            onSubmit={handleCreateOrder} 
            dailyCounter={orders.length + 1} 
            creatorName={currentUser.name}
            tools={tools}
            terminals={terminals}
            wires={wires}
            standards={standards} // 传递标准表
        />
      </>
    );
  }

  if (selectedOrder) {
    // 确保从 orders 数组中获取最新的对象引用
    const liveOrder = orders.find(o => o.id === selectedOrder.id) || selectedOrder;
    return (
      <OrderDetailView 
        order={liveOrder} 
        onBack={() => setSelectedOrder(null)}
        onAddRecord={handleAddRecord}
        onAuditRecord={handleAuditRecord}
        onCloseOrder={handleCloseOrder}
        currentUser={currentUser}
        tools={tools}
        terminals={terminals}
        wires={wires}
        isClosing={isClosing}
      />
    );
  }

  return (
    <div className="space-y-4">
      {currentUser.role === UserRole.EMPLOYEE && (
        <button 
            onClick={() => setView('CREATE')}
            className="w-full bg-white border-2 border-dashed border-gray-300 text-gray-500 py-4 rounded-xl font-medium hover:bg-gray-50 hover:border-brand-300 hover:text-brand-500 transition-colors"
        >
            + 新增生产订单
        </button>
      )}

      {loading ? (
        <div className="text-center py-10 text-gray-400">
            <div className="animate-spin w-6 h-6 border-2 border-brand-500 border-t-transparent rounded-full mx-auto mb-2"></div>
            正在加载数据...
        </div>
      ) : orders.length === 0 ? (
        <div className="text-center py-10 text-gray-400">
          <p>暂无订单记录</p>
        </div>
      ) : (
        orders.map(order => (
          <Card key={order.id} onClick={() => setSelectedOrder(order)} className={`border-l-4 ${order.isClosed ? 'border-l-gray-400 opacity-75' : 'border-l-brand-500'}`}>
            <div className="flex justify-between items-start">
              <div>
                <h3 className={`font-bold ${order.isClosed ? 'text-gray-600 line-through' : 'text-gray-900'}`}>{order.productName}</h3>
                <p className="text-xs text-gray-500 font-mono mt-1">{order.productionOrderNo}</p>
              </div>
              <div className="flex flex-col items-end gap-1">
                 {order.isClosed && <span className="text-[10px] bg-gray-200 text-gray-600 px-1.5 py-0.5 rounded font-bold">已结束</span>}
                 <span className={`px-2 py-1 text-xs rounded-md ${
                    order.records.length > 0 && order.records[0].status === AuditStatus.PENDING 
                    ? 'bg-yellow-100 text-yellow-700'
                    : 'bg-green-100 text-green-700'
                }`}>
                    {order.records.length} 条记录
                </span>
              </div>
            </div>
            <div className="mt-3 flex gap-2 text-xs text-gray-500">
                <span>标准: {order.standardPullForce}N</span>
                <span>•</span>
                {/* 显示端子名称 */}
                <span>{terminals.find(t => String(t.id) === String(order.terminalSpecId))?.name || '未知端子'}</span>
            </div>
          </Card>
        ))
      )}
    </div>
  );
};

// --- OrderDetailView 和 InspectionCard ---
const OrderDetailView: React.FC<{
  order: ProductionOrder;
  onBack: () => void;
  onAddRecord: (orderId: string, type: SubmissionType) => void;
  onAuditRecord: (orderId: string, recordId: string, samples: TerminalSample[]) => void;
  onCloseOrder: (orderId: string) => void;
  currentUser: User;
  tools: CrimpingTool[];
  terminals: TerminalSpec[];
  wires: WireSpec[];
  isClosing?: boolean;
}> = ({ order, onBack, onAddRecord, onAuditRecord, onCloseOrder, currentUser, tools, terminals, wires, isClosing }) => {

  const firstPiecePassed = order.records.some(r => r.type === "首件" && r.status === AuditStatus.PASSED);
  const hasPendingFirstPiece = order.records.some(r => r.type === "首件" && r.status === AuditStatus.PENDING);

  const getToolName = (val: string) => tools.find(t => String(t.id) === val || t.model === val)?.model || val;
  const getTerminalName = (id: string) => terminals.find(t => String(t.id) === id)?.name || id;
  const getWireName = (id: string) => wires.find(w => String(w.id) === id)?.displayName || id;

  return (
    <div className="space-y-4 pb-10">
      <button onClick={onBack} className="text-sm text-gray-500 mb-2 flex items-center">&larr; 返回列表</button>

      {order.isClosed && (
        <div className="bg-gray-800 text-white p-3 rounded-lg text-sm flex items-center gap-2 animate-pulse-once">
            <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" /></svg>
            <span>此订单已归档结束，处于只读模式。</span>
        </div>
      )}

      <div className={`bg-white p-4 rounded-xl shadow-sm border border-gray-100 relative overflow-hidden transition-all duration-500 ${order.isClosed ? 'grayscale opacity-90' : ''}`}>
        <div className="absolute top-0 right-0 p-2 opacity-10">
            <svg className="w-24 h-24 text-brand-600" fill="currentColor" viewBox="0 0 24 24"><path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-1 17.93c-3.95-.49-7-3.85-7-7.93 0-.62.08-1.21.21-1.79L9 15v1c0 1.1.9 2 2 2v1.93zm6.9-2.54c-.26-.81-1-1.39-1.9-1.39h-1v-3c0-.55-.45-1-1-1H8v-2h2c.55 0 1-.45 1-1V7h2c1.1 0 2-.9 2-2v-.41c2.93 1.19 5 4.06 5 7.41 0 2.08-.8 3.97-2.1 5.39z"/></svg>
        </div>
        <div className="flex justify-between mb-2 relative z-10">
            <h2 className="font-bold text-lg text-gray-900">{order.productName}</h2>
            <span className="font-mono text-xs bg-gray-100 px-2 py-1 rounded text-gray-600 self-center">{order.productionOrderNo}</span>
        </div>
        <div className="grid grid-cols-2 gap-y-2 text-xs text-gray-600 relative z-10 mt-3">
            <div className="flex flex-col">
                <span className="text-gray-400">标准拉力</span>
                <span className="font-bold text-lg text-brand-600">{order.standardPullForce} N</span>
            </div>
            <div className="flex flex-col">
                <span className="text-gray-400">压接工具</span>
                <span className="font-medium text-gray-800">{getToolName(order.toolNo)}</span>
            </div>
            <div className="flex flex-col">
                <span className="text-gray-400">端子规格</span>
                <span className="font-medium text-gray-800">{getTerminalName(order.terminalSpecId)}</span>
            </div>
            <div className="flex flex-col">
                <span className="text-gray-400">导线规格</span>
                <span className="font-medium text-gray-800">{getWireName(order.wireSpecId)}</span>
            </div>
        </div>
      </div>

      {!order.isClosed && currentUser.role === UserRole.EMPLOYEE && (
        <div className="grid grid-cols-2 gap-3">
          <button
            disabled={hasPendingFirstPiece || firstPiecePassed} 
            onClick={() => onAddRecord(order.id, SubmissionType.FIRST_PIECE)}
            className={`py-3 rounded-xl font-medium shadow-sm transition-all border flex flex-col items-center justify-center gap-1 h-20 ${
               firstPiecePassed 
                ? 'bg-green-50 border-green-200 text-green-700 cursor-not-allowed opacity-60' 
                : hasPendingFirstPiece 
                    ? 'bg-yellow-50 border-yellow-200 text-yellow-700 cursor-not-allowed opacity-80'
                    : 'bg-white border-brand-200 text-brand-600 hover:bg-brand-50'
            }`}
          >
            <span className="font-bold">{firstPiecePassed ? '首件合格' : '提交首件'}</span>
            <span className="text-[10px] font-normal">{hasPendingFirstPiece ? '等待审核...' : firstPiecePassed ? '已允许生产' : '需审核后生产'}</span>
          </button>

          <button
            disabled={!firstPiecePassed}
            onClick={() => onAddRecord(order.id, SubmissionType.LAST_PIECE)}
            className={`py-3 rounded-xl font-medium shadow-sm transition-all border flex flex-col items-center justify-center gap-1 h-20 ${
              !firstPiecePassed 
                ? 'bg-gray-50 border-gray-200 text-gray-400 cursor-not-allowed' 
                : 'bg-brand-600 border-brand-600 text-white hover:bg-brand-700'
            }`}
          >
            <span className="font-bold">提交末件</span>
            <span className="text-[10px] font-normal">结束生产时提交</span>
          </button>
        </div>
      )}

      {/* 结束订单按钮 - 仅检验人员 (Auditor) 可见，且订单未关闭时显示 */}
      {!order.isClosed && currentUser.role === UserRole.AUDITOR && (
          <button 
            onClick={() => onCloseOrder(order.id)}
            disabled={isClosing}
            className={`w-full text-red-600 border border-red-200 py-3 rounded-xl font-medium transition-colors flex items-center justify-center gap-2 ${
                isClosing ? 'bg-red-50 opacity-50 cursor-wait' : 'bg-red-50 hover:bg-red-100'
            }`}
          >
            {isClosing ? (
                 <>
                    <div className="w-4 h-4 border-2 border-red-600 border-t-transparent rounded-full animate-spin"></div>
                    <span>正在结束...</span>
                 </>
            ) : (
                <>
                    <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" /></svg>
                    <span>结束订单 (归档)</span>
                </>
            )}
          </button>
      )}

      <div className="space-y-4">
        {order.records.map(record => (
          <InspectionCard 
            key={record.id} 
            record={record} 
            standardPullForce={order.standardPullForce}
            isAuditor={currentUser.role === UserRole.AUDITOR}
            isOrderClosed={!!order.isClosed}
            onAuditSubmit={(samples) => onAuditRecord(order.id, record.id, samples)}
          />
        ))}
      </div>
    </div>
  );
};

const InspectionCard: React.FC<{
    record: InspectionRecord;
    standardPullForce: number;
    isAuditor: boolean;
    isOrderClosed: boolean;
    onAuditSubmit: (samples: TerminalSample[]) => void;
}> = ({ record, standardPullForce, isAuditor, isOrderClosed, onAuditSubmit }) => {

    // 如果订单已关闭，任何人都不能审核
    const isPending = record.status === AuditStatus.PENDING;
    const canAudit = isAuditor && isPending && !isOrderClosed;

    // Local state for editing values
    const [editSamples, setEditSamples] = useState<TerminalSample[]>(
        record.samples.map(s => ({ ...s }))
    );

    const handleValueChange = (index: number, valStr: string) => {
        const val = parseFloat(valStr);
        const newSamples = [...editSamples];
        newSamples[index] = {
            ...newSamples[index],
            measuredForce: isNaN(val) ? 0 : val,
            isPassed: !isNaN(val) ? val >= standardPullForce : false
        };
        setEditSamples(newSamples);
    };

    const handleSave = () => {
        // Simple validation
        if (editSamples.some(s => !s.measuredForce && s.measuredForce !== 0)) {
            alert("请填写所有端子的测试值");
            return;
        }
        onAuditSubmit(editSamples);
    };

    const displaySamples = canAudit ? editSamples : record.samples;

    return (
        <Card className={`overflow-hidden p-0 ${isPending ? 'border-yellow-200 ring-4 ring-yellow-50' : 'border-gray-200'} ${isOrderClosed ? 'opacity-80' : ''}`}>
            <div className={`px-4 py-3 border-b flex justify-between items-center ${isPending ? 'bg-yellow-50' : 'bg-gray-50'}`}>
                <div className="flex items-center gap-2">
                    <span className={`px-2 py-1 rounded text-xs font-bold ${
                        record.type === "首件" ? 'bg-purple-600 text-white' : 'bg-blue-600 text-white'
                    }`}>
                        {record.type}
                    </span>
                    <div className="flex flex-col">
                        <span className="text-xs text-gray-500 leading-tight">提交人: {record.submitterName}</span>
                        <span className="text-[10px] text-gray-400 leading-tight">{new Date(record.submittedAt).toLocaleString()}</span>
                    </div>
                </div>
                <StatusBadge status={record.status} />
            </div>

            <div className="p-4">
                <table className="w-full text-sm">
                    <thead>
                        <tr className="text-left text-xs text-gray-500 border-b border-gray-100">
                            <th className="pb-2 font-medium w-16">样件</th>
                            <th className="pb-2 font-medium">拉力(N)</th>
                            <th className="pb-2 font-medium text-center">结论</th>
                        </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-50">
                        {displaySamples.map((sample, idx) => (
                            <tr key={idx}>
                                <td className="py-3 font-medium text-gray-700">#{sample.sampleIndex}</td>
                                <td className="py-3 pr-2">
                                    {canAudit ? (
                                        <input 
                                            type="number" 
                                            className="w-full border rounded px-2 py-1"
                                            value={sample.measuredForce || ''}
                                            onChange={(e) => handleValueChange(idx, e.target.value)}
                                        />
                                    ) : (
                                        <span className="font-mono text-gray-900">{sample.measuredForce}</span>
                                    )}
                                </td>
                                <td className="py-3 text-center">
                                    {sample.isPassed ? 
                                        <span className="text-green-600 text-xs">合格</span> : 
                                        <span className="text-red-500 text-xs">NG</span>}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                {canAudit && (
                    <div className="mt-4 text-right">
                         <button onClick={handleSave} className="bg-brand-600 text-white px-4 py-2 rounded text-sm">提交判定</button>
                    </div>
                )}
            </div>
        </Card>
    );
};

const StatusBadge: React.FC<{status: AuditStatus}> = ({status}) => {
    if(status === AuditStatus.PENDING) return <span className="text-xs bg-yellow-100 text-yellow-800 px-2 py-1 rounded">待审核</span>;
    if(status === AuditStatus.PASSED) return <span className="text-xs bg-green-100 text-green-800 px-2 py-1 rounded">合格</span>;
    return <span className="text-xs bg-red-100 text-red-800 px-2 py-1 rounded">不合格</span>;
}

import React, { useState, useEffect } from 'react';
import { ProductionOrder, SubmissionType, AuditStatus, InspectionRecord, UserRole, TerminalSample, User, CrimpingTool, TerminalSpec, WireSpec, PullForceStandard } from '../../types';
import { CreateOrder } from './CreateOrder';
import { Card } from '../../components/Card';
import { api } from '../../services/api'; 

interface CrimpingDashboardProps {
  currentUser: User;
}

export const CrimpingDashboard: React.FC<CrimpingDashboardProps> = ({ currentUser }) => {
  const [view, setView] = useState<'LIST' | 'CREATE'>('LIST');
  const [orders, setOrders] = useState<ProductionOrder[]>([]); 
  const [loading, setLoading] = useState(true);
  const [selectedOrder, setSelectedOrder] = useState<ProductionOrder | null>(null);

  const [tools, setTools] = useState<CrimpingTool[]>([]);
  const [terminals, setTerminals] = useState<TerminalSpec[]>([]);
  const [wires, setWires] = useState<WireSpec[]>([]);
  const [standards, setStandards] = useState<PullForceStandard[]>([]);

  const [isClosing, setIsClosing] = useState(false);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [fetchedOrders, fetchedTools, fetchedTerminals, fetchedWires, fetchedStandards] = await Promise.all([
        api.getOrders(),
        api.getCrimpingTools(),
        api.getTerminalSpecs(),
        api.getWireSpecs(),
        api.getPullForceStandards()
      ]);

      setOrders(fetchedOrders);
      setTools(fetchedTools);
      setTerminals(fetchedTerminals);
      setWires(fetchedWires);
      setStandards(fetchedStandards);
    } catch (error) {
      console.error("数据加载失败", error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateOrder = async (newOrder: ProductionOrder) => {
    try {
        await api.createOrder(newOrder);
        const latestOrders = await api.getOrders();
        setOrders(latestOrders);
        setView('LIST');
    } catch (e: any) {
        alert(`创建失败: ${e.message}`);
    }
  };

  const handleAddRecord = async (orderId: string, type: SubmissionType) => {
    const initialSamples: TerminalSample[] = [
        { id: 0, sampleIndex: 1, measuredForce: 0, isPassed: false }, 
        { id: 0, sampleIndex: 2, measuredForce: 0, isPassed: false }, 
        { id: 0, sampleIndex: 3, measuredForce: 0, isPassed: false }
    ];

    const generateId = () => {
        if (typeof crypto !== 'undefined' && crypto.randomUUID) {
            return crypto.randomUUID();
        }
        return Date.now().toString(36) + Math.random().toString(36).substring(2);
    };

    const newRecord: InspectionRecord = {
      id: generateId(), 
      orderId,
      type: type === SubmissionType.FIRST_PIECE ? "首件" : "末件",
      submitterName: currentUser.name, 
      submittedAt: new Date().toISOString(),
      status: AuditStatus.PENDING,
      samples: initialSamples,
      auditNote: null
    };

    try {
        await api.addRecord(orderId, newRecord);
        await loadData();
        if (selectedOrder) {
            const latestOrders = await api.getOrders();
            const updated = latestOrders.find(o => o.id === selectedOrder.id);
            if (updated) setSelectedOrder(updated);
        }
    } catch (e: any) {
        alert(`提交失败: ${e.message}`);
    }
  };

  const handleAuditRecord = async (orderId: string, recordId: string, samples: TerminalSample[]) => {
    const allPassed = samples.every(s => s.isPassed);
    const newStatus = allPassed ? AuditStatus.PASSED : AuditStatus.FAILED;

    try {
        await api.auditRecord(recordId, samples, currentUser.name, newStatus, null);
        const latestOrders = await api.getOrders();
        setOrders(latestOrders);
        if (selectedOrder) {
            const updated = latestOrders.find(o => o.id === selectedOrder.id);
            if (updated) setSelectedOrder(updated);
        }
    } catch (e: any) {
        alert(`审核提交失败: ${e.message}`);
    }
  };

  /**
   * 增强后的结束订单逻辑
   */
  const handleCloseOrder = async (orderId: string) => {
      const orderToClose = orders.find(o => o.id === orderId);
      if (!orderToClose) return;

      // 1. 业务逻辑校验
      const hasFirstPiecePassed = orderToClose.records.some(r => r.type === "首件" && r.status === AuditStatus.PASSED);
      const hasLastPiece = orderToClose.records.some(r => r.type === "末件");
      const hasPendingRecords = orderToClose.records.some(r => r.status === AuditStatus.PENDING);

      if (!hasFirstPiecePassed) {
          alert("【无法结束】\n此订单尚无“合格”的首件记录，流程不合规。");
          return;
      }

      if (!hasLastPiece) {
          alert("【无法结束】\n尚未提交“末件”记录，无法完成生产闭环。");
          return;
      }

      if (hasPendingRecords) {
          alert("【无法结束】\n此订单仍有记录处于“待审核”状态。请检验人员完成所有判定后再结束订单。");
          return;
      }

      // 2. 二次确认
      if(!window.confirm("【最终确认】\n\n该订单已满足结束条件（有合格首件、已提末件、所有记录已判定）。\n\n结束后的订单将进入归档状态，不可再修改。确定执行吗？")) {
          return;
      }

      setIsClosing(true);

      try {
          await api.closeOrder(orderId);

          // 本地乐观更新
          const updatedOrders = orders.map(o => o.id === orderId ? { ...o, isClosed: true } : o);
          setOrders(updatedOrders);
          if (selectedOrder && selectedOrder.id === orderId) {
             setSelectedOrder({ ...selectedOrder, isClosed: true });
          }

          // 后台同步
          const latestOrders = await api.getOrders();
          setOrders(latestOrders);
      } catch (e: any) {
          alert(`关闭订单失败: ${e.message}`);
          await loadData();
      } finally {
          setIsClosing(false);
      }
  };

  if (view === 'CREATE') {
    return (
      <>
        <div className="mb-4">
             <button onClick={() => setView('LIST')} className="text-sm text-gray-500 flex items-center gap-1">
                &larr; 返回列表
             </button>
        </div>
        <CreateOrder 
            onSubmit={handleCreateOrder} 
            dailyCounter={orders.length + 1} 
            creatorName={currentUser.name}
            tools={tools}
            terminals={terminals}
            wires={wires}
            standards={standards}
        />
      </>
    );
  }

  if (selectedOrder) {
    const liveOrder = orders.find(o => o.id === selectedOrder.id) || selectedOrder;
    return (
      <OrderDetailView 
        order={liveOrder} 
        onBack={() => setSelectedOrder(null)}
        onAddRecord={handleAddRecord}
        onAuditRecord={handleAuditRecord}
        onCloseOrder={handleCloseOrder}
        currentUser={currentUser}
        tools={tools}
        terminals={terminals}
        wires={wires}
        isClosing={isClosing}
      />
    );
  }

  return (
    <div className="space-y-4">
      {currentUser.role === UserRole.EMPLOYEE && (
        <button 
            onClick={() => setView('CREATE')}
            className="w-full bg-white border-2 border-dashed border-gray-300 text-gray-500 py-4 rounded-xl font-medium hover:bg-gray-50 hover:border-brand-300 hover:text-brand-500 transition-colors"
        >
            + 新增生产订单
        </button>
      )}

      {loading ? (
        <div className="text-center py-10 text-gray-400">
            <div className="animate-spin w-6 h-6 border-2 border-brand-500 border-t-transparent rounded-full mx-auto mb-2"></div>
            正在加载数据...
        </div>
      ) : (
        orders.map(order => (
          <Card key={order.id} onClick={() => setSelectedOrder(order)} className={`border-l-4 ${order.isClosed ? 'border-l-gray-400 opacity-75' : 'border-l-brand-500'}`}>
            <div className="flex justify-between items-start">
              <div>
                <h3 className={`font-bold ${order.isClosed ? 'text-gray-600 line-through' : 'text-gray-900'}`}>{order.productName}</h3>
                <p className="text-xs text-gray-500 font-mono mt-1">{order.productionOrderNo}</p>
              </div>
              <div className="flex flex-col items-end gap-1">
                 {order.isClosed && <span className="text-[10px] bg-gray-200 text-gray-600 px-1.5 py-0.5 rounded font-bold">已结束</span>}
                 <span className={`px-2 py-1 text-xs rounded-md ${
                    order.records.length > 0 && order.records.some(r => r.status === AuditStatus.PENDING) 
                    ? 'bg-yellow-100 text-yellow-700'
                    : 'bg-green-100 text-green-700'
                }`}>
                    {order.records.length} 条记录
                </span>
              </div>
            </div>
            <div className="mt-3 flex gap-2 text-xs text-gray-500">
                <span>标准: {order.standardPullForce}N</span>
                <span>•</span>
                <span>{terminals.find(t => String(t.id) === String(order.terminalSpecId))?.name || '未知端子'}</span>
            </div>
          </Card>
        ))
      )}
    </div>
  );
};

const OrderDetailView: React.FC<{
  order: ProductionOrder;
  onBack: () => void;
  onAddRecord: (orderId: string, type: SubmissionType) => void;
  onAuditRecord: (orderId: string, recordId: string, samples: TerminalSample[]) => void;
  onCloseOrder: (orderId: string) => void;
  currentUser: User;
  tools: CrimpingTool[];
  terminals: TerminalSpec[];
  wires: WireSpec[];
  isClosing?: boolean;
}> = ({ order, onBack, onAddRecord, onAuditRecord, onCloseOrder, currentUser, tools, terminals, wires, isClosing }) => {

  const firstPiecePassed = order.records.some(r => r.type === "首件" && r.status === AuditStatus.PASSED);
  const hasPendingFirstPiece = order.records.some(r => r.type === "首件" && r.status === AuditStatus.PENDING);

  const getToolName = (val: string) => tools.find(t => String(t.id) === val || t.model === val)?.model || val;
  const getTerminalName = (id: string) => terminals.find(t => String(t.id) === id)?.name || id;
  const getWireName = (id: string) => wires.find(w => String(w.id) === id)?.displayName || id;

  return (
    <div className="space-y-4 pb-10">
      <button onClick={onBack} className="text-sm text-gray-500 mb-2 flex items-center">&larr; 返回列表</button>

      {order.isClosed && (
        <div className="bg-gray-800 text-white p-3 rounded-lg text-sm flex items-center gap-2">
            <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" /></svg>
            <span>此订单已归档结束，处于只读模式。</span>
        </div>
      )}

      <div className={`bg-white p-4 rounded-xl shadow-sm border border-gray-100 relative overflow-hidden transition-all duration-500 ${order.isClosed ? 'grayscale opacity-90' : ''}`}>
        <div className="absolute top-0 right-0 p-2 opacity-10">
            <svg className="w-24 h-24 text-brand-600" fill="currentColor" viewBox="0 0 24 24"><path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-1 17.93c-3.95-.49-7-3.85-7-7.93 0-.62.08-1.21.21-1.79L9 15v1c0 1.1.9 2 2 2v1.93zm6.9-2.54c-.26-.81-1-1.39-1.9-1.39h-1v-3c0-.55-.45-1-1-1H8v-2h2c.55 0 1-.45 1-1V7h2c1.1 0 2-.9 2-2v-.41c2.93 1.19 5 4.06 5 7.41 0 2.08-.8 3.97-2.1 5.39z"/></svg>
        </div>
        <div className="flex justify-between mb-2 relative z-10">
            <h2 className="font-bold text-lg text-gray-900">{order.productName}</h2>
            <span className="font-mono text-xs bg-gray-100 px-2 py-1 rounded text-gray-600 self-center">{order.productionOrderNo}</span>
        </div>
        <div className="grid grid-cols-2 gap-y-2 text-xs text-gray-600 relative z-10 mt-3">
            <div className="flex flex-col">
                <span className="text-gray-400">标准拉力</span>
                <span className="font-bold text-lg text-brand-600">{order.standardPullForce} N</span>
            </div>
            <div className="flex flex-col">
                <span className="text-gray-400">压接工具</span>
                <span className="font-medium text-gray-800">{getToolName(order.toolNo)}</span>
            </div>
            <div className="flex flex-col">
                <span className="text-gray-400">端子规格</span>
                <span className="font-medium text-gray-800">{getTerminalName(order.terminalSpecId)}</span>
            </div>
            <div className="flex flex-col">
                <span className="text-gray-400">导线规格</span>
                <span className="font-medium text-gray-800">{getWireName(order.wireSpecId)}</span>
            </div>
        </div>
      </div>

      {!order.isClosed && currentUser.role === UserRole.EMPLOYEE && (
        <div className="grid grid-cols-2 gap-3">
          <button
            disabled={hasPendingFirstPiece || firstPiecePassed} 
            onClick={() => onAddRecord(order.id, SubmissionType.FIRST_PIECE)}
            className={`py-3 rounded-xl font-medium shadow-sm transition-all border flex flex-col items-center justify-center gap-1 h-20 ${
               firstPiecePassed 
                ? 'bg-green-50 border-green-200 text-green-700 cursor-not-allowed opacity-60' 
                : hasPendingFirstPiece 
                    ? 'bg-yellow-50 border-yellow-200 text-yellow-700 cursor-not-allowed opacity-80'
                    : 'bg-white border-brand-200 text-brand-600 hover:bg-brand-50'
            }`}
          >
            <span className="font-bold">{firstPiecePassed ? '首件合格' : '提交首件'}</span>
            <span className="text-[10px] font-normal">{hasPendingFirstPiece ? '等待审核...' : firstPiecePassed ? '已允许生产' : '需审核后生产'}</span>
          </button>

          <button
            disabled={!firstPiecePassed}
            onClick={() => onAddRecord(order.id, SubmissionType.LAST_PIECE)}
            className={`py-3 rounded-xl font-medium shadow-sm transition-all border flex flex-col items-center justify-center gap-1 h-20 ${
              !firstPiecePassed 
                ? 'bg-gray-50 border-gray-200 text-gray-400 cursor-not-allowed' 
                : 'bg-brand-600 border-brand-600 text-white hover:bg-brand-700'
            }`}
          >
            <span className="font-bold">提交末件</span>
            <span className="text-[10px] font-normal">结束生产时提交</span>
          </button>
        </div>
      )}

      {!order.isClosed && currentUser.role === UserRole.AUDITOR && (
          <button 
            onClick={() => onCloseOrder(order.id)}
            disabled={isClosing}
            className={`w-full text-red-600 border border-red-200 py-3 rounded-xl font-medium transition-colors flex items-center justify-center gap-2 ${
                isClosing ? 'bg-red-50 opacity-50 cursor-wait' : 'bg-red-50 hover:bg-red-100'
            }`}
          >
            {isClosing ? (
                 <>
                    <div className="w-4 h-4 border-2 border-red-600 border-t-transparent rounded-full animate-spin"></div>
                    <span>校验并结束中...</span>
                 </>
            ) : (
                <>
                    <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" /></svg>
                    <span>结束订单 (归档)</span>
                </>
            )}
          </button>
      )}

      <div className="space-y-4">
        {order.records.map(record => (
          <InspectionCard 
            key={record.id} 
            record={record} 
            standardPullForce={order.standardPullForce}
            isAuditor={currentUser.role === UserRole.AUDITOR}
            isOrderClosed={!!order.isClosed}
            onAuditSubmit={(samples) => onAuditRecord(order.id, record.id, samples)}
          />
        ))}
      </div>
    </div>
  );
};

const InspectionCard: React.FC<{
    record: InspectionRecord;
    standardPullForce: number;
    isAuditor: boolean;
    isOrderClosed: boolean;
    onAuditSubmit: (samples: TerminalSample[]) => void;
}> = ({ record, standardPullForce, isAuditor, isOrderClosed, onAuditSubmit }) => {

    const isPending = record.status === AuditStatus.PENDING;
    const canAudit = isAuditor && isPending && !isOrderClosed;

    const [editSamples, setEditSamples] = useState<TerminalSample[]>(
        record.samples.map(s => ({ ...s }))
    );

    const handleValueChange = (index: number, valStr: string) => {
        const val = parseFloat(valStr);
        const newSamples = [...editSamples];
        newSamples[index] = {
            ...newSamples[index],
            measuredForce: isNaN(val) ? 0 : val,
            isPassed: !isNaN(val) ? val >= standardPullForce : false
        };
        setEditSamples(newSamples);
    };

    const handleSave = () => {
        if (editSamples.some(s => !s.measuredForce && s.measuredForce !== 0)) {
            alert("请填写所有端子的测试值");
            return;
        }
        onAuditSubmit(editSamples);
    };

    const displaySamples = canAudit ? editSamples : record.samples;

    return (
        <Card className={`overflow-hidden p-0 ${isPending ? 'border-yellow-200 ring-4 ring-yellow-50' : 'border-gray-200'} ${isOrderClosed ? 'opacity-80' : ''}`}>
            <div className={`px-4 py-3 border-b flex justify-between items-center ${isPending ? 'bg-yellow-50' : 'bg-gray-50'}`}>
                <div className="flex items-center gap-2">
                    <span className={`px-2 py-1 rounded text-xs font-bold ${
                        record.type === "首件" ? 'bg-purple-600 text-white' : 'bg-blue-600 text-white'
                    }`}>
                        {record.type}
                    </span>
                    <div className="flex flex-col">
                        <span className="text-xs text-gray-500 leading-tight">提交人: {record.submitterName}</span>
                        <span className="text-[10px] text-gray-400 leading-tight">{new Date(record.submittedAt).toLocaleString()}</span>
                    </div>
                </div>
                <StatusBadge status={record.status} />
            </div>

            <div className="p-4">
                <table className="w-full text-sm">
                    <thead>
                        <tr className="text-left text-xs text-gray-500 border-b border-gray-100">
                            <th className="pb-2 font-medium w-16">样件</th>
                            <th className="pb-2 font-medium">拉力(N)</th>
                            <th className="pb-2 font-medium text-center">结论</th>
                        </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-50">
                        {displaySamples.map((sample, idx) => (
                            <tr key={idx}>
                                <td className="py-3 font-medium text-gray-700">#{sample.sampleIndex}</td>
                                <td className="py-3 pr-2">
                                    {canAudit ? (
                                        <input 
                                            type="number" 
                                            className="w-full border rounded px-2 py-1"
                                            value={sample.measuredForce || ''}
                                            onChange={(e) => handleValueChange(idx, e.target.value)}
                                        />
                                    ) : (
                                        <span className="font-mono text-gray-900">{sample.measuredForce}</span>
                                    )}
                                </td>
                                <td className="py-3 text-center">
                                    {sample.isPassed ? 
                                        <span className="text-green-600 text-xs">合格</span> : 
                                        <span className="text-red-500 text-xs">NG</span>}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                {canAudit && (
                    <div className="mt-4 text-right">
                         <button onClick={handleSave} className="bg-brand-600 text-white px-4 py-2 rounded text-sm">提交判定</button>
                    </div>
                )}
            </div>
        </Card>
    );
};

const StatusBadge: React.FC<{status: AuditStatus}> = ({status}) => {
    if(status === AuditStatus.PENDING) return <span className="text-xs bg-yellow-100 text-yellow-800 px-2 py-1 rounded">待审核</span>;
    if(status === AuditStatus.PASSED) return <span className="text-xs bg-green-100 text-green-800 px-2 py-1 rounded">合格</span>;
    return <span className="text-xs bg-red-100 text-red-800 px-2 py-1 rounded">不合格</span>;
}

import React, { useState, useEffect } from 'react';
import { ProductionOrder, SubmissionType, AuditStatus, InspectionRecord, UserRole, TerminalSample, User, CrimpingTool, TerminalSpec, WireSpec, PullForceStandard } from '../../types';
import { CreateOrder } from './CreateOrder';
import { Card } from '../../components/Card';
import { api } from '../../services/api'; 

interface CrimpingDashboardProps {
  currentUser: User;
}

export const CrimpingDashboard: React.FC<CrimpingDashboardProps> = ({ currentUser }) => {
  const [view, setView] = useState<'LIST' | 'CREATE'>('LIST');
  const [orders, setOrders] = useState<ProductionOrder[]>([]); 
  const [loading, setLoading] = useState(true);
  const [selectedOrder, setSelectedOrder] = useState<ProductionOrder | null>(null);

  const [tools, setTools] = useState<CrimpingTool[]>([]);
  const [terminals, setTerminals] = useState<TerminalSpec[]>([]);
  const [wires, setWires] = useState<WireSpec[]>([]);
  const [standards, setStandards] = useState<PullForceStandard[]>([]);

  const [isClosing, setIsClosing] = useState(false);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [fetchedOrders, fetchedTools, fetchedTerminals, fetchedWires, fetchedStandards] = await Promise.all([
        api.getOrders(),
        api.getCrimpingTools(),
        api.getTerminalSpecs(),
        api.getWireSpecs(),
        api.getPullForceStandards()
      ]);

      setOrders(fetchedOrders);
      setTools(fetchedTools);
      setTerminals(fetchedTerminals);
      setWires(fetchedWires);
      setStandards(fetchedStandards);
    } catch (error) {
      console.error("数据加载失败", error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateOrder = async (newOrder: ProductionOrder) => {
    try {
        await api.createOrder(newOrder);
        const latestOrders = await api.getOrders();
        setOrders(latestOrders);
        setView('LIST');
    } catch (e: any) {
        alert(`创建失败: ${e.message}`);
    }
  };

  const handleAddRecord = async (orderId: string, type: SubmissionType) => {
    const initialSamples: TerminalSample[] = [
        { id: 0, sampleIndex: 1, measuredForce: 0, isPassed: false }, 
        { id: 0, sampleIndex: 2, measuredForce: 0, isPassed: false }, 
        { id: 0, sampleIndex: 3, measuredForce: 0, isPassed: false }
    ];

    const generateId = () => {
        if (typeof crypto !== 'undefined' && crypto.randomUUID) {
            return crypto.randomUUID();
        }
        return Date.now().toString(36) + Math.random().toString(36).substring(2);
    };

    const newRecord: InspectionRecord = {
      id: generateId(), 
      orderId,
      type: type === SubmissionType.FIRST_PIECE ? "首件" : "末件",
      submitterName: currentUser.name, 
      submittedAt: new Date().toISOString(),
      status: AuditStatus.PENDING,
      samples: initialSamples,
      auditNote: null
    };

    try {
        await api.addRecord(orderId, newRecord);
        await loadData();
        if (selectedOrder) {
            const latestOrders = await api.getOrders();
            const updated = latestOrders.find(o => o.id === selectedOrder.id);
            if (updated) setSelectedOrder(updated);
        }
    } catch (e: any) {
        alert(`提交失败: ${e.message}`);
    }
  };

  const handleAuditRecord = async (orderId: string, recordId: string, samples: TerminalSample[]) => {
    const allPassed = samples.every(s => s.isPassed);
    const newStatus = allPassed ? AuditStatus.PASSED : AuditStatus.FAILED;

    try {
        await api.auditRecord(recordId, samples, currentUser.name, newStatus, null);
        const latestOrders = await api.getOrders();
        setOrders(latestOrders);
        if (selectedOrder) {
            const updated = latestOrders.find(o => o.id === selectedOrder.id);
            if (updated) setSelectedOrder(updated);
        }
    } catch (e: any) {
        alert(`审核提交失败: ${e.message}`);
    }
  };

  /**
   * 增强后的结束订单逻辑 (带调试输出)
   */
  const handleCloseOrder = async (orderId: string) => {
      console.log("准备结束订单, ID:", orderId);

      const orderToClose = orders.find(o => String(o.id) === String(orderId));

      if (!orderToClose) {
          console.error("在订单列表中未找到 ID 为", orderId, "的订单");
          alert("系统错误：未能在当前列表中找到该订单信息，请刷新页面重试。");
          return;
      }

      // 1. 业务逻辑校验
      const hasFirstPiecePassed = orderToClose.records.some(r => r.type === "首件" && r.status === AuditStatus.PASSED);
      const hasLastPiece = orderToClose.records.some(r => r.type === "末件");
      const hasPendingRecords = orderToClose.records.some(r => r.status === AuditStatus.PENDING);

      console.log("业务校验结果:", { hasFirstPiecePassed, hasLastPiece, hasPendingRecords });

      if (!hasFirstPiecePassed) {
          alert("【无法结束】\n此订单尚无“合格”的首件记录。必须先完成首件审核且合格后才能结束。");
          return;
      }

      if (!hasLastPiece) {
          alert("【无法结束】\n尚未提交“末件”记录。生产结束前必须提交末件测试。");
          return;
      }

      if (hasPendingRecords) {
          alert("【无法结束】\n此订单仍有记录处于“待审核”状态。请检验人员完成所有记录的判定。");
          return;
      }

      // 2. 二次确认
      if(!window.confirm("【最终确认】\n\n确定要结束此订单吗？结束后将不可更改。")) {
          return;
      }

      setIsClosing(true);
      console.log("开始调用后端接口 [PATCH] /Orders/" + orderId + "/close");

      try {
          await api.closeOrder(orderId);
          console.log("后端接口调用成功");

          // 乐观更新 UI
          const updatedOrders = orders.map(o => o.id === orderId ? { ...o, isClosed: true } : o);
          setOrders(updatedOrders);
          if (selectedOrder && selectedOrder.id === orderId) {
             setSelectedOrder({ ...selectedOrder, isClosed: true });
          }

          alert("订单已成功结束并归档。");
          // 后台静默刷新
          const latestOrders = await api.getOrders();
          setOrders(latestOrders);
      } catch (e: any) {
          console.error("调用后端失败:", e);
          alert(`关闭订单失败: ${e.message}\n\n请检查网络连接或后端服务是否正常。`);
          await loadData();
      } finally {
          setIsClosing(false);
      }
  };

  if (view === 'CREATE') {
    return (
      <>
        <div className="mb-4">
             <button onClick={() => setView('LIST')} className="text-sm text-gray-500 flex items-center gap-1">
                &larr; 返回列表
             </button>
        </div>
        <CreateOrder 
            onSubmit={handleCreateOrder} 
            dailyCounter={orders.length + 1} 
            creatorName={currentUser.name}
            tools={tools}
            terminals={terminals}
            wires={wires}
            standards={standards}
        />
      </>
    );
  }

  if (selectedOrder) {
    const liveOrder = orders.find(o => o.id === selectedOrder.id) || selectedOrder;
    return (
      <OrderDetailView 
        order={liveOrder} 
        onBack={() => setSelectedOrder(null)}
        onAddRecord={handleAddRecord}
        onAuditRecord={handleAuditRecord}
        onCloseOrder={handleCloseOrder}
        currentUser={currentUser}
        tools={tools}
        terminals={terminals}
        wires={wires}
        isClosing={isClosing}
      />
    );
  }

  return (
    <div className="space-y-4">
      {currentUser.role === UserRole.EMPLOYEE && (
        <button 
            onClick={() => setView('CREATE')}
            className="w-full bg-white border-2 border-dashed border-gray-300 text-gray-500 py-4 rounded-xl font-medium hover:bg-gray-50 hover:border-brand-300 hover:text-brand-500 transition-colors"
        >
            + 新增生产订单
        </button>
      )}

      {loading ? (
        <div className="text-center py-10 text-gray-400">
            <div className="animate-spin w-6 h-6 border-2 border-brand-500 border-t-transparent rounded-full mx-auto mb-2"></div>
            正在加载数据...
        </div>
      ) : (
        orders.map(order => (
          <Card key={order.id} onClick={() => setSelectedOrder(order)} className={`border-l-4 ${order.isClosed ? 'border-l-gray-400 opacity-75' : 'border-l-brand-500'}`}>
            <div className="flex justify-between items-start">
              <div>
                <h3 className={`font-bold ${order.isClosed ? 'text-gray-600 line-through' : 'text-gray-900'}`}>{order.productName}</h3>
                <p className="text-xs text-gray-500 font-mono mt-1">{order.productionOrderNo}</p>
              </div>
              <div className="flex flex-col items-end gap-1">
                 {order.isClosed && <span className="text-[10px] bg-gray-200 text-gray-600 px-1.5 py-0.5 rounded font-bold">已结束</span>}
                 <span className={`px-2 py-1 text-xs rounded-md ${
                    order.records.length > 0 && order.records.some(r => r.status === AuditStatus.PENDING) 
                    ? 'bg-yellow-100 text-yellow-700'
                    : 'bg-green-100 text-green-700'
                }`}>
                    {order.records.length} 条记录
                </span>
              </div>
            </div>
            <div className="mt-3 flex gap-2 text-xs text-gray-500">
                <span>标准: {order.standardPullForce}N</span>
                <span>•</span>
                <span>{terminals.find(t => String(t.id) === String(order.terminalSpecId))?.name || '未知端子'}</span>
            </div>
          </Card>
        ))
      )}
    </div>
  );
};

const OrderDetailView: React.FC<{
  order: ProductionOrder;
  onBack: () => void;
  onAddRecord: (orderId: string, type: SubmissionType) => void;
  onAuditRecord: (orderId: string, recordId: string, samples: TerminalSample[]) => void;
  onCloseOrder: (orderId: string) => void;
  currentUser: User;
  tools: CrimpingTool[];
  terminals: TerminalSpec[];
  wires: WireSpec[];
  isClosing?: boolean;
}> = ({ order, onBack, onAddRecord, onAuditRecord, onCloseOrder, currentUser, tools, terminals, wires, isClosing }) => {

  const firstPiecePassed = order.records.some(r => r.type === "首件" && r.status === AuditStatus.PASSED);
  const hasPendingFirstPiece = order.records.some(r => r.type === "首件" && r.status === AuditStatus.PENDING);

  const getToolName = (val: string) => tools.find(t => String(t.id) === val || t.model === val)?.model || val;
  const getTerminalName = (id: string) => terminals.find(t => String(t.id) === id)?.name || id;
  const getWireName = (id: string) => wires.find(w => String(w.id) === id)?.displayName || id;

  return (
    <div className="space-y-4 pb-10">
      <button onClick={onBack} className="text-sm text-gray-500 mb-2 flex items-center">&larr; 返回列表</button>

      {order.isClosed && (
        <div className="bg-gray-800 text-white p-3 rounded-lg text-sm flex items-center gap-2">
            <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" /></svg>
            <span>此订单已归档结束，处于只读模式。</span>
        </div>
      )}

      <div className={`bg-white p-4 rounded-xl shadow-sm border border-gray-100 relative overflow-hidden transition-all duration-500 ${order.isClosed ? 'grayscale opacity-90' : ''}`}>
        <div className="absolute top-0 right-0 p-2 opacity-10">
            <svg className="w-24 h-24 text-brand-600" fill="currentColor" viewBox="0 0 24 24"><path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-1 17.93c-3.95-.49-7-3.85-7-7.93 0-.62.08-1.21.21-1.79L9 15v1c0 1.1.9 2 2 2v1.93zm6.9-2.54c-.26-.81-1-1.39-1.9-1.39h-1v-3c0-.55-.45-1-1-1H8v-2h2c.55 0 1-.45 1-1V7h2c1.1 0 2-.9 2-2v-.41c2.93 1.19 5 4.06 5 7.41 0 2.08-.8 3.97-2.1 5.39z"/></svg>
        </div>
        <div className="flex justify-between mb-2 relative z-10">
            <h2 className="font-bold text-lg text-gray-900">{order.productName}</h2>
            <span className="font-mono text-xs bg-gray-100 px-2 py-1 rounded text-gray-600 self-center">{order.productionOrderNo}</span>
        </div>
        <div className="grid grid-cols-2 gap-y-2 text-xs text-gray-600 relative z-10 mt-3">
            <div className="flex flex-col">
                <span className="text-gray-400">标准拉力</span>
                <span className="font-bold text-lg text-brand-600">{order.standardPullForce} N</span>
            </div>
            <div className="flex flex-col">
                <span className="text-gray-400">压接工具</span>
                <span className="font-medium text-gray-800">{getToolName(order.toolNo)}</span>
            </div>
            <div className="flex flex-col">
                <span className="text-gray-400">端子规格</span>
                <span className="font-medium text-gray-800">{getTerminalName(order.terminalSpecId)}</span>
            </div>
            <div className="flex flex-col">
                <span className="text-gray-400">导线规格</span>
                <span className="font-medium text-gray-800">{getWireName(order.wireSpecId)}</span>
            </div>
        </div>
      </div>

      {!order.isClosed && currentUser.role === UserRole.EMPLOYEE && (
        <div className="grid grid-cols-2 gap-3">
          <button
            disabled={hasPendingFirstPiece || firstPiecePassed} 
            onClick={() => onAddRecord(order.id, SubmissionType.FIRST_PIECE)}
            className={`py-3 rounded-xl font-medium shadow-sm transition-all border flex flex-col items-center justify-center gap-1 h-20 ${
               firstPiecePassed 
                ? 'bg-green-50 border-green-200 text-green-700 cursor-not-allowed opacity-60' 
                : hasPendingFirstPiece 
                    ? 'bg-yellow-50 border-yellow-200 text-yellow-700 cursor-not-allowed opacity-80'
                    : 'bg-white border-brand-200 text-brand-600 hover:bg-brand-50'
            }`}
          >
            <span className="font-bold">{firstPiecePassed ? '首件合格' : '提交首件'}</span>
            <span className="text-[10px] font-normal">{hasPendingFirstPiece ? '等待审核...' : firstPiecePassed ? '已允许生产' : '需审核后生产'}</span>
          </button>

          <button
            disabled={!firstPiecePassed}
            onClick={() => onAddRecord(order.id, SubmissionType.LAST_PIECE)}
            className={`py-3 rounded-xl font-medium shadow-sm transition-all border flex flex-col items-center justify-center gap-1 h-20 ${
              !firstPiecePassed 
                ? 'bg-gray-50 border-gray-200 text-gray-400 cursor-not-allowed' 
                : 'bg-brand-600 border-brand-600 text-white hover:bg-brand-700'
            }`}
          >
            <span className="font-bold">提交末件</span>
            <span className="text-[10px] font-normal">结束生产时提交</span>
          </button>
        </div>
      )}

      {!order.isClosed && currentUser.role === UserRole.AUDITOR && (
          <button 
            onClick={() => onCloseOrder(order.id)}
            disabled={isClosing}
            className={`w-full text-red-600 border border-red-200 py-3 rounded-xl font-medium transition-colors flex items-center justify-center gap-2 ${
                isClosing ? 'bg-red-50 opacity-50 cursor-wait' : 'bg-red-50 hover:bg-red-100'
            }`}
          >
            {isClosing ? (
                 <>
                    <div className="w-4 h-4 border-2 border-red-600 border-t-transparent rounded-full animate-spin"></div>
                    <span>校验并结束中...</span>
                 </>
            ) : (
                <>
                    <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" /></svg>
                    <span>结束订单 (归档)</span>
                </>
            )}
          </button>
      )}

      <div className="space-y-4">
        {order.records.map(record => (
          <InspectionCard 
            key={record.id} 
            record={record} 
            standardPullForce={order.standardPullForce}
            isAuditor={currentUser.role === UserRole.AUDITOR}
            isOrderClosed={!!order.isClosed}
            onAuditSubmit={(samples) => onAuditRecord(order.id, record.id, samples)}
          />
        ))}
      </div>
    </div>
  );
};

const InspectionCard: React.FC<{
    record: InspectionRecord;
    standardPullForce: number;
    isAuditor: boolean;
    isOrderClosed: boolean;
    onAuditSubmit: (samples: TerminalSample[]) => void;
}> = ({ record, standardPullForce, isAuditor, isOrderClosed, onAuditSubmit }) => {

    const isPending = record.status === AuditStatus.PENDING;
    const canAudit = isAuditor && isPending && !isOrderClosed;

    const [editSamples, setEditSamples] = useState<TerminalSample[]>(
        record.samples.map(s => ({ ...s }))
    );

    const handleValueChange = (index: number, valStr: string) => {
        const val = parseFloat(valStr);
        const newSamples = [...editSamples];
        newSamples[index] = {
            ...newSamples[index],
            measuredForce: isNaN(val) ? 0 : val,
            isPassed: !isNaN(val) ? val >= standardPullForce : false
        };
        setEditSamples(newSamples);
    };

    const handleSave = () => {
        if (editSamples.some(s => !s.measuredForce && s.measuredForce !== 0)) {
            alert("请填写所有端子的测试值");
            return;
        }
        onAuditSubmit(editSamples);
    };

    const displaySamples = canAudit ? editSamples : record.samples;

    return (
        <Card className={`overflow-hidden p-0 ${isPending ? 'border-yellow-200 ring-4 ring-yellow-50' : 'border-gray-200'} ${isOrderClosed ? 'opacity-80' : ''}`}>
            <div className={`px-4 py-3 border-b flex justify-between items-center ${isPending ? 'bg-yellow-50' : 'bg-gray-50'}`}>
                <div className="flex items-center gap-2">
                    <span className={`px-2 py-1 rounded text-xs font-bold ${
                        record.type === "首件" ? 'bg-purple-600 text-white' : 'bg-blue-600 text-white'
                    }`}>
                        {record.type}
                    </span>
                    <div className="flex flex-col">
                        <span className="text-xs text-gray-500 leading-tight">提交人: {record.submitterName}</span>
                        <span className="text-[10px] text-gray-400 leading-tight">{new Date(record.submittedAt).toLocaleString()}</span>
                    </div>
                </div>
                <StatusBadge status={record.status} />
            </div>

            <div className="p-4">
                <table className="w-full text-sm">
                    <thead>
                        <tr className="text-left text-xs text-gray-500 border-b border-gray-100">
                            <th className="pb-2 font-medium w-16">样件</th>
                            <th className="pb-2 font-medium">拉力(N)</th>
                            <th className="pb-2 font-medium text-center">结论</th>
                        </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-50">
                        {displaySamples.map((sample, idx) => (
                            <tr key={idx}>
                                <td className="py-3 font-medium text-gray-700">#{sample.sampleIndex}</td>
                                <td className="py-3 pr-2">
                                    {canAudit ? (
                                        <input 
                                            type="number" 
                                            className="w-full border rounded px-2 py-1"
                                            value={sample.measuredForce || ''}
                                            onChange={(e) => handleValueChange(idx, e.target.value)}
                                        />
                                    ) : (
                                        <span className="font-mono text-gray-900">{sample.measuredForce}</span>
                                    )}
                                </td>
                                <td className="py-3 text-center">
                                    {sample.isPassed ? 
                                        <span className="text-green-600 text-xs">合格</span> : 
                                        <span className="text-red-500 text-xs">NG</span>}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                {canAudit && (
                    <div className="mt-4 text-right">
                         <button onClick={handleSave} className="bg-brand-600 text-white px-4 py-2 rounded text-sm">提交判定</button>
                    </div>
                )}
            </div>
        </Card>
    );
};

const StatusBadge: React.FC<{status: AuditStatus}> = ({status}) => {
    if(status === AuditStatus.PENDING) return <span className="text-xs bg-yellow-100 text-yellow-800 px-2 py-1 rounded">待审核</span>;
    if(status === AuditStatus.PASSED) return <span className="text-xs bg-green-100 text-green-800 px-2 py-1 rounded">合格</span>;
    return <span className="text-xs bg-red-100 text-red-800 px-2 py-1 rounded">不合格</span>;
}

import { ProductionOrder, User, InspectionRecord, UserRole, TerminalSample, CrimpingTool, TerminalSpec, WireSpec, PullForceStandard } from '../types';

// C# 后端地址
const API_BASE_URL = 'http://localhost:5075/api';

const handleResponse = async (response: Response) => {
  // 1. 处理成功响应 (200-299)
  if (response.ok) {
    if (response.status === 204) return null;

    const text = await response.text();
    // 兼容 C# return Ok() 空包体的情况
    if (!text) return null; 

    try {
      return JSON.parse(text);
    } catch (e) {
      console.warn("Response was not valid JSON but request succeeded:", text);
      return null;
    }
  }

  // 2. 处理错误响应
  const errorText = await response.text();
  console.error(`API Error (${response.status}):`, errorText);

  let errorMessage = `请求失败: ${response.status}`;

  if (errorText) {
    try {
        const jsonError = JSON.parse(errorText);
        // 尝试提取 C# 标准错误格式
        const msg = jsonError.message || jsonError.title || jsonError.error;

        let details = '';
        if (jsonError.errors) {
            details = Object.entries(jsonError.errors)
                .map(([key, msgs]) => `${key}: ${(msgs as any[]).join(', ')}`)
                .join('; ');
        }

        if (msg) {
            errorMessage = details ? `${msg} (${details})` : msg;
        } else if (details) {
            errorMessage = details;
        }
    } catch (e) {
        if (errorText.length < 500 && !errorText.trim().startsWith('<')) {
            errorMessage = `${errorMessage}: ${errorText}`;
        }
    }
  }

  throw new Error(errorMessage);
};

export const api = {
  // --- AuthController ---
  login: async (username: string, password: string): Promise<User> => {
    const response = await fetch(`${API_BASE_URL}/Auth/login`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, password }),
    });
    const data = await handleResponse(response);

    const mappedRole = data.role === 1 ? UserRole.AUDITOR : UserRole.EMPLOYEE;

    return {
      username: data.employeeId,
      name: data.name,
      role: mappedRole,
      token: undefined
    };
  },

  // --- ConfigController ---

  getCrimpingTools: async (): Promise<CrimpingTool[]> => {
    const response = await fetch(`${API_BASE_URL}/config/tools`);
    return handleResponse(response);
  },

  getTerminalSpecs: async (): Promise<TerminalSpec[]> => {
    const response = await fetch(`${API_BASE_URL}/config/terminals`);
    return handleResponse(response);
  },

  getWireSpecs: async (): Promise<WireSpec[]> => {
    const response = await fetch(`${API_BASE_URL}/config/wires`);
    return handleResponse(response);
  },

  getPullForceStandards: async (): Promise<PullForceStandard[]> => {
    const response = await fetch(`${API_BASE_URL}/config/standards`);
    return handleResponse(response);
  },

  // --- OrdersController ---

  getOrders: async (): Promise<ProductionOrder[]> => {
    const timestamp = new Date().getTime();
    const response = await fetch(`${API_BASE_URL}/Orders?_t=${timestamp}`, {
      headers: {
        'Cache-Control': 'no-cache',
        'Pragma': 'no-cache'
      }
    });
    return handleResponse(response);
  },

  createOrder: async (order: ProductionOrder): Promise<ProductionOrder> => {
    const response = await fetch(`${API_BASE_URL}/Orders`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(order),
    });
    return handleResponse(response);
  },

  addRecord: async (orderId: string, record: InspectionRecord): Promise<void> => {
    const response = await fetch(`${API_BASE_URL}/Orders/${orderId}/records`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(record),
    });
    return handleResponse(response);
  },

  auditRecord: async (recordId: string, samples: TerminalSample[], auditorName: string, status: number, auditNote?: string): Promise<void> => {
    const response = await fetch(`${API_BASE_URL}/Orders/records/${recordId}/audit`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ 
        samples,
        auditorName,
        status,
        auditNote
      }),
    });
    return handleResponse(response);
  },

  /**
   * 关闭/结束订单
   */
  closeOrder: async (orderId: string): Promise<void> => {
    console.log(`[API] Attempting PATCH to: ${API_BASE_URL}/Orders/${orderId}/close`);
    const response = await fetch(`${API_BASE_URL}/Orders/${orderId}/close`, {
        method: 'PATCH',
        headers: { 
            'Content-Type': 'application/json',
            'Accept': 'application/json'
        },
        body: JSON.stringify(true),
    });
    return handleResponse(response);
  }
};

import React, { useState, useEffect } from 'react';
import { ProductionOrder, SubmissionType, AuditStatus, InspectionRecord, UserRole, TerminalSample, User, CrimpingTool, TerminalSpec, WireSpec, PullForceStandard } from '../../types';
import { CreateOrder } from './CreateOrder';
import { Card } from '../../components/Card';
import { api } from '../../services/api'; 

interface CrimpingDashboardProps {
  currentUser: User;
}

export const CrimpingDashboard: React.FC<CrimpingDashboardProps> = ({ currentUser }) => {
  const [view, setView] = useState<'LIST' | 'CREATE'>('LIST');
  const [orders, setOrders] = useState<ProductionOrder[]>([]); 
  const [loading, setLoading] = useState(true);
  const [selectedOrder, setSelectedOrder] = useState<ProductionOrder | null>(null);

  const [tools, setTools] = useState<CrimpingTool[]>([]);
  const [terminals, setTerminals] = useState<TerminalSpec[]>([]);
  const [wires, setWires] = useState<WireSpec[]>([]);
  const [standards, setStandards] = useState<PullForceStandard[]>([]);

  const [isClosing, setIsClosing] = useState(false);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [fetchedOrders, fetchedTools, fetchedTerminals, fetchedWires, fetchedStandards] = await Promise.all([
        api.getOrders(),
        api.getCrimpingTools(),
        api.getTerminalSpecs(),
        api.getWireSpecs(),
        api.getPullForceStandards()
      ]);

      setOrders(fetchedOrders);
      setTools(fetchedTools);
      setTerminals(fetchedTerminals);
      setWires(fetchedWires);
      setStandards(fetchedStandards);
    } catch (error) {
      console.error("数据加载失败", error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateOrder = async (newOrder: ProductionOrder) => {
    try {
        await api.createOrder(newOrder);
        const latestOrders = await api.getOrders();
        setOrders(latestOrders);
        setView('LIST');
    } catch (e: any) {
        alert(`创建失败: ${e.message}`);
    }
  };

  const handleAddRecord = async (orderId: string, type: SubmissionType) => {
    const initialSamples: TerminalSample[] = [
        { id: 0, sampleIndex: 1, measuredForce: 0, isPassed: false }, 
        { id: 0, sampleIndex: 2, measuredForce: 0, isPassed: false }, 
        { id: 0, sampleIndex: 3, measuredForce: 0, isPassed: false }
    ];

    const generateId = () => {
        if (typeof crypto !== 'undefined' && crypto.randomUUID) {
            return crypto.randomUUID();
        }
        return Date.now().toString(36) + Math.random().toString(36).substring(2);
    };

    const newRecord: InspectionRecord = {
      id: generateId(), 
      orderId,
      type: type === SubmissionType.FIRST_PIECE ? "首件" : "末件",
      submitterName: currentUser.name, 
      submittedAt: new Date().toISOString(),
      status: AuditStatus.PENDING,
      samples: initialSamples,
      auditNote: null
    };

    try {
        await api.addRecord(orderId, newRecord);
        await loadData();
        if (selectedOrder) {
            const latestOrders = await api.getOrders();
            const updated = latestOrders.find(o => o.id === selectedOrder.id);
            if (updated) setSelectedOrder(updated);
        }
    } catch (e: any) {
        alert(`提交失败: ${e.message}`);
    }
  };

  const handleAuditRecord = async (orderId: string, recordId: string, samples: TerminalSample[]) => {
    const allPassed = samples.every(s => s.isPassed);
    const newStatus = allPassed ? AuditStatus.PASSED : AuditStatus.FAILED;

    try {
        await api.auditRecord(recordId, samples, currentUser.name, newStatus, null);
        const latestOrders = await api.getOrders();
        setOrders(latestOrders);
        if (selectedOrder) {
            const updated = latestOrders.find(o => o.id === selectedOrder.id);
            if (updated) setSelectedOrder(updated);
        }
    } catch (e: any) {
        alert(`审核提交失败: ${e.message}`);
    }
  };

  const handleCloseOrder = async (orderId: string) => {
      console.log("准备结束订单, ID:", orderId);
      const orderToClose = orders.find(o => String(o.id) === String(orderId));
      if (!orderToClose) return;

      const hasFirstPiecePassed = orderToClose.records.some(r => r.type === "首件" && r.status === AuditStatus.PASSED);
      const hasLastPiece = orderToClose.records.some(r => r.type === "末件");
      const hasPendingRecords = orderToClose.records.some(r => r.status === AuditStatus.PENDING);

      if (!hasFirstPiecePassed) { alert("【无法结束】尚无合格首件。"); return; }
      if (!hasLastPiece) { alert("【无法结束】尚未提交末件。"); return; }
      if (hasPendingRecords) { alert("【无法结束】尚有待审核记录。"); return; }

      setIsClosing(true);
      try {
          await api.closeOrder(orderId);
          const updatedOrders = orders.map(o => o.id === orderId ? { ...o, isClosed: true } : o);
          setOrders(updatedOrders);
          if (selectedOrder && selectedOrder.id === orderId) {
             setSelectedOrder({ ...selectedOrder, isClosed: true });
          }
          alert("订单已成功结束并归档。");
          const latestOrders = await api.getOrders();
          setOrders(latestOrders);
      } catch (e: any) {
          alert(`关闭订单失败: ${e.message}`);
          await loadData();
      } finally { setIsClosing(false); }
  };

  /**
   * 计算订单的最终判定状态
   */
  const getOrderOverallStatus = (order: ProductionOrder) => {
    if (!order.isClosed) return null;
    const hasFailed = order.records.some(r => r.status === AuditStatus.FAILED);
    return hasFailed ? "NG" : "合格";
  };

  if (view === 'CREATE') {
    return (
      <>
        <div className="mb-4">
             <button onClick={() => setView('LIST')} className="text-sm text-gray-500 flex items-center gap-1">
                &larr; 返回列表
             </button>
        </div>
        <CreateOrder 
            onSubmit={handleCreateOrder} 
            dailyCounter={orders.length + 1} 
            creatorName={currentUser.name}
            tools={tools}
            terminals={terminals}
            wires={wires}
            standards={standards}
        />
      </>
    );
  }

  if (selectedOrder) {
    const liveOrder = orders.find(o => o.id === selectedOrder.id) || selectedOrder;
    return (
      <OrderDetailView 
        order={liveOrder} 
        onBack={() => setSelectedOrder(null)}
        onAddRecord={handleAddRecord}
        onAuditRecord={handleAuditRecord}
        onCloseOrder={handleCloseOrder}
        currentUser={currentUser}
        tools={tools}
        terminals={terminals}
        wires={wires}
        isClosing={isClosing}
      />
    );
  }

  return (
    <div className="space-y-4">
      {currentUser.role === UserRole.EMPLOYEE && (
        <button 
            onClick={() => setView('CREATE')}
            className="w-full bg-white border-2 border-dashed border-gray-300 text-gray-500 py-4 rounded-xl font-medium hover:bg-gray-50 hover:border-brand-300 hover:text-brand-500 transition-colors"
        >
            + 新增生产订单
        </button>
      )}

      {loading ? (
        <div className="text-center py-10 text-gray-400">
            <div className="animate-spin w-6 h-6 border-2 border-brand-500 border-t-transparent rounded-full mx-auto mb-2"></div>
            正在加载数据...
        </div>
      ) : (
        <div className="space-y-3">
            {orders.map(order => {
                const finalStatus = getOrderOverallStatus(order);
                return (
                    <Card key={order.id} onClick={() => setSelectedOrder(order)} className={`border-l-4 transition-all ${order.isClosed ? 'border-l-gray-300 bg-gray-50' : 'border-l-brand-500 bg-white'}`}>
                        <div className="flex justify-between items-start mb-2">
                            <div className="flex-1 min-w-0 pr-2">
                                <h3 className={`font-bold text-base truncate ${order.isClosed ? 'text-gray-600' : 'text-gray-900'}`}>
                                    {order.productName || '未命名产品'}
                                </h3>
                                <div className="flex flex-wrap items-center gap-x-3 gap-y-1 mt-1">
                                    <span className="font-mono text-[10px] text-gray-400 bg-gray-100 px-1 rounded">{order.productionOrderNo}</span>
                                    <span className="text-[10px] text-gray-400">{new Date(order.createdAt).toLocaleString('zh-CN', { month: 'numeric', day: 'numeric', hour: '2-digit', minute: '2-digit' })}</span>
                                </div>
                            </div>

                            <div className="flex flex-col items-end gap-1 shrink-0">
                                {order.isClosed ? (
                                    <span className={`text-[10px] font-bold px-1.5 py-0.5 rounded border ${finalStatus === '合格' ? 'bg-green-50 text-green-700 border-green-200' : 'bg-red-50 text-red-700 border-red-200'}`}>
                                        已结束: {finalStatus}
                                    </span>
                                ) : (
                                    <span className={`px-2 py-1 text-[10px] rounded font-medium ${
                                        order.records.length > 0 && order.records.some(r => r.status === AuditStatus.PENDING) 
                                        ? 'bg-yellow-100 text-yellow-700'
                                        : 'bg-blue-50 text-blue-700'
                                    }`}>
                                        {order.records.length} 条记录
                                    </span>
                                )}
                            </div>
                        </div>

                        <div className="flex justify-between items-center text-xs border-t border-gray-100 pt-2 mt-1">
                            <div className="flex items-center gap-2 text-gray-500">
                                <span className="bg-gray-100 px-1.5 rounded">{terminals.find(t => String(t.id) === String(order.terminalSpecId))?.name || '未知端子'}</span>
                                <span className="text-gray-300">|</span>
                                <span>标准: {order.standardPullForce}N</span>
                            </div>
                            {currentUser.role === UserRole.AUDITOR && (
                                <div className="flex items-center gap-1 text-gray-400">
                                    <svg className="w-3 h-3" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" /></svg>
                                    <span>{order.creatorName}</span>
                                </div>
                            )}
                        </div>
                    </Card>
                );
            })}
        </div>
      )}
    </div>
  );
};

const OrderDetailView: React.FC<{
  order: ProductionOrder;
  onBack: () => void;
  onAddRecord: (orderId: string, type: SubmissionType) => void;
  onAuditRecord: (orderId: string, recordId: string, samples: TerminalSample[]) => void;
  onCloseOrder: (orderId: string) => void;
  currentUser: User;
  tools: CrimpingTool[];
  terminals: TerminalSpec[];
  wires: WireSpec[];
  isClosing?: boolean;
}> = ({ order, onBack, onAddRecord, onAuditRecord, onCloseOrder, currentUser, tools, terminals, wires, isClosing }) => {

  const firstPiecePassed = order.records.some(r => r.type === "首件" && r.status === AuditStatus.PASSED);
  const hasPendingFirstPiece = order.records.some(r => r.type === "首件" && r.status === AuditStatus.PENDING);

  const getToolName = (val: string) => tools.find(t => String(t.id) === val || t.model === val)?.model || val;
  const getTerminalName = (id: string) => terminals.find(t => String(t.id) === id)?.name || id;
  const getWireName = (id: string) => wires.find(w => String(w.id) === id)?.displayName || id;

  return (
    <div className="space-y-4 pb-10">
      <button onClick={onBack} className="text-sm text-gray-500 mb-2 flex items-center">&larr; 返回列表</button>

      {order.isClosed && (
        <div className={`p-3 rounded-lg text-sm flex items-center justify-between shadow-sm border ${
            order.records.some(r => r.status === AuditStatus.FAILED) 
            ? 'bg-red-600 text-white border-red-700' 
            : 'bg-green-600 text-white border-green-700'
        }`}>
            <div className="flex items-center gap-2">
                <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" /></svg>
                <span className="font-bold">此订单已归档 (结论: {order.records.some(r => r.status === AuditStatus.FAILED) ? '不合格/NG' : '合格'})</span>
            </div>
        </div>
      )}

      <div className={`bg-white p-4 rounded-xl shadow-sm border border-gray-100 relative overflow-hidden transition-all duration-500 ${order.isClosed ? 'grayscale opacity-90' : ''}`}>
        <div className="absolute top-0 right-0 p-2 opacity-10">
            <svg className="w-24 h-24 text-brand-600" fill="currentColor" viewBox="0 0 24 24"><path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-1 17.93c-3.95-.49-7-3.85-7-7.93 0-.62.08-1.21.21-1.79L9 15v1c0 1.1.9 2 2 2v1.93zm6.9-2.54c-.26-.81-1-1.39-1.9-1.39h-1v-3c0-.55-.45-1-1-1H8v-2h2c.55 0 1-.45 1-1V7h2c1.1 0 2-.9 2-2v-.41c2.93 1.19 5 4.06 5 7.41 0 2.08-.8 3.97-2.1 5.39z"/></svg>
        </div>
        <div className="flex justify-between mb-2 relative z-10">
            <h2 className="font-bold text-lg text-gray-900">{order.productName}</h2>
            <span className="font-mono text-xs bg-gray-100 px-2 py-1 rounded text-gray-600 self-center">{order.productionOrderNo}</span>
        </div>
        <div className="grid grid-cols-2 gap-y-2 text-xs text-gray-600 relative z-10 mt-3">
            <div className="flex flex-col">
                <span className="text-gray-400">标准拉力</span>
                <span className="font-bold text-lg text-brand-600">{order.standardPullForce} N</span>
            </div>
            <div className="flex flex-col">
                <span className="text-gray-400">创建时间</span>
                <span className="font-medium text-gray-800">{new Date(order.createdAt).toLocaleString()}</span>
            </div>
            <div className="flex flex-col">
                <span className="text-gray-400">端子规格</span>
                <span className="font-medium text-gray-800">{getTerminalName(order.terminalSpecId)}</span>
            </div>
            <div className="flex flex-col">
                <span className="text-gray-400">提交者</span>
                <span className="font-medium text-gray-800">{order.creatorName}</span>
            </div>
        </div>
      </div>

      {!order.isClosed && currentUser.role === UserRole.EMPLOYEE && (
        <div className="grid grid-cols-2 gap-3">
          <button
            disabled={hasPendingFirstPiece || firstPiecePassed} 
            onClick={() => onAddRecord(order.id, SubmissionType.FIRST_PIECE)}
            className={`py-3 rounded-xl font-medium shadow-sm transition-all border flex flex-col items-center justify-center gap-1 h-20 ${
               firstPiecePassed 
                ? 'bg-green-50 border-green-200 text-green-700 cursor-not-allowed opacity-60' 
                : hasPendingFirstPiece 
                    ? 'bg-yellow-50 border-yellow-200 text-yellow-700 cursor-not-allowed opacity-80'
                    : 'bg-white border-brand-200 text-brand-600 hover:bg-brand-50'
            }`}
          >
            <span className="font-bold">{firstPiecePassed ? '首件合格' : '提交首件'}</span>
            <span className="text-[10px] font-normal">{hasPendingFirstPiece ? '等待审核...' : firstPiecePassed ? '已允许生产' : '需审核后生产'}</span>
          </button>

          <button
            disabled={!firstPiecePassed}
            onClick={() => onAddRecord(order.id, SubmissionType.LAST_PIECE)}
            className={`py-3 rounded-xl font-medium shadow-sm transition-all border flex flex-col items-center justify-center gap-1 h-20 ${
              !firstPiecePassed 
                ? 'bg-gray-50 border-gray-200 text-gray-400 cursor-not-allowed' 
                : 'bg-brand-600 border-brand-600 text-white hover:bg-brand-700'
            }`}
          >
            <span className="font-bold">提交末件</span>
            <span className="text-[10px] font-normal">结束生产时提交</span>
          </button>
        </div>
      )}

      {!order.isClosed && currentUser.role === UserRole.AUDITOR && (
          <button 
            onClick={() => onCloseOrder(order.id)}
            disabled={isClosing}
            className={`w-full text-red-600 border border-red-200 py-3 rounded-xl font-medium transition-colors flex items-center justify-center gap-2 ${
                isClosing ? 'bg-red-50 opacity-50 cursor-wait' : 'bg-red-50 hover:bg-red-100'
            }`}
          >
            {isClosing ? (
                 <>
                    <div className="w-4 h-4 border-2 border-red-600 border-t-transparent rounded-full animate-spin"></div>
                    <span>校验并结束中...</span>
                 </>
            ) : (
                <>
                    <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" /></svg>
                    <span>结束订单 (归档)</span>
                </>
            )}
          </button>
      )}

      <div className="space-y-4">
        {order.records.map(record => (
          <InspectionCard 
            key={record.id} 
            record={record} 
            standardPullForce={order.standardPullForce}
            isAuditor={currentUser.role === UserRole.AUDITOR}
            isOrderClosed={!!order.isClosed}
            onAuditSubmit={(samples) => onAuditRecord(order.id, record.id, samples)}
          />
        ))}
      </div>
    </div>
  );
};

const InspectionCard: React.FC<{
    record: InspectionRecord;
    standardPullForce: number;
    isAuditor: boolean;
    isOrderClosed: boolean;
    onAuditSubmit: (samples: TerminalSample[]) => void;
}> = ({ record, standardPullForce, isAuditor, isOrderClosed, onAuditSubmit }) => {

    const isPending = record.status === AuditStatus.PENDING;
    const canAudit = isAuditor && isPending && !isOrderClosed;

    const [editSamples, setEditSamples] = useState<TerminalSample[]>(
        record.samples.map(s => ({ ...s }))
    );

    const handleValueChange = (index: number, valStr: string) => {
        const val = parseFloat(valStr);
        const newSamples = [...editSamples];
        newSamples[index] = {
            ...newSamples[index],
            measuredForce: isNaN(val) ? 0 : val,
            isPassed: !isNaN(val) ? val >= standardPullForce : false
        };
        setEditSamples(newSamples);
    };

    const handleSave = () => {
        if (editSamples.some(s => !s.measuredForce && s.measuredForce !== 0)) {
            alert("请填写所有端子的测试值");
            return;
        }
        onAuditSubmit(editSamples);
    };

    const displaySamples = canAudit ? editSamples : record.samples;

    return (
        <Card className={`overflow-hidden p-0 ${isPending ? 'border-yellow-200 ring-4 ring-yellow-50' : 'border-gray-200'} ${isOrderClosed ? 'opacity-80' : ''}`}>
            <div className={`px-4 py-3 border-b flex justify-between items-center ${isPending ? 'bg-yellow-50' : 'bg-gray-50'}`}>
                <div className="flex items-center gap-2">
                    <span className={`px-2 py-1 rounded text-xs font-bold ${
                        record.type === "首件" ? 'bg-purple-600 text-white' : 'bg-blue-600 text-white'
                    }`}>
                        {record.type}
                    </span>
                    <div className="flex flex-col">
                        <span className="text-xs text-gray-500 leading-tight">提交人: {record.submitterName}</span>
                        <span className="text-[10px] text-gray-400 leading-tight">{new Date(record.submittedAt).toLocaleString()}</span>
                    </div>
                </div>
                <StatusBadge status={record.status} />
            </div>

            <div className="p-4">
                <table className="w-full text-sm">
                    <thead>
                        <tr className="text-left text-xs text-gray-500 border-b border-gray-100">
                            <th className="pb-2 font-medium w-16">样件</th>
                            <th className="pb-2 font-medium">拉力(N)</th>
                            <th className="pb-2 font-medium text-center">结论</th>
                        </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-50">
                        {displaySamples.map((sample, idx) => (
                            <tr key={idx}>
                                <td className="py-3 font-medium text-gray-700">#{sample.sampleIndex}</td>
                                <td className="py-3 pr-2">
                                    {canAudit ? (
                                        <input 
                                            type="number" 
                                            className="w-full border rounded px-2 py-1"
                                            value={sample.measuredForce || ''}
                                            onChange={(e) => handleValueChange(idx, e.target.value)}
                                        />
                                    ) : (
                                        <span className="font-mono text-gray-900">{sample.measuredForce}</span>
                                    )}
                                </td>
                                <td className="py-3 text-center">
                                    {sample.isPassed ? 
                                        <span className="text-green-600 text-xs">合格</span> : 
                                        <span className="text-red-500 text-xs">NG</span>}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                {canAudit && (
                    <div className="mt-4 text-right">
                         <button onClick={handleSave} className="bg-brand-600 text-white px-4 py-2 rounded text-sm">提交判定</button>
                    </div>
                )}
            </div>
        </Card>
    );
};

const StatusBadge: React.FC<{status: AuditStatus}> = ({status}) => {
    if(status === AuditStatus.PENDING) return <span className="text-xs bg-yellow-100 text-yellow-800 px-2 py-1 rounded">待审核</span>;
    if(status === AuditStatus.PASSED) return <span className="text-xs bg-green-100 text-green-800 px-2 py-1 rounded">合格</span>;
    return <span className="text-xs bg-red-100 text-red-800 px-2 py-1 rounded">不合格</span>;
}

import React, { useState, useEffect } from 'react';
import { ProductionOrder, SubmissionType, AuditStatus, InspectionRecord, UserRole, TerminalSample, User, CrimpingTool, TerminalSpec, WireSpec, PullForceStandard } from '../../types';
import { CreateOrder } from './CreateOrder';
import { Card } from '../../components/Card';
import { api } from '../../services/api'; 

interface CrimpingDashboardProps {
  currentUser: User;
}

export const CrimpingDashboard: React.FC<CrimpingDashboardProps> = ({ currentUser }) => {
  const [view, setView] = useState<'LIST' | 'CREATE'>('LIST');
  const [orders, setOrders] = useState<ProductionOrder[]>([]); 
  const [loading, setLoading] = useState(true);
  const [selectedOrder, setSelectedOrder] = useState<ProductionOrder | null>(null);

  const [tools, setTools] = useState<CrimpingTool[]>([]);
  const [terminals, setTerminals] = useState<TerminalSpec[]>([]);
  const [wires, setWires] = useState<WireSpec[]>([]);
  const [standards, setStandards] = useState<PullForceStandard[]>([]);

  const [isClosing, setIsClosing] = useState(false);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [fetchedOrders, fetchedTools, fetchedTerminals, fetchedWires, fetchedStandards] = await Promise.all([
        api.getOrders(),
        api.getCrimpingTools(),
        api.getTerminalSpecs(),
        api.getWireSpecs(),
        api.getPullForceStandards()
      ]);

      setOrders(fetchedOrders);
      setTools(fetchedTools);
      setTerminals(fetchedTerminals);
      setWires(fetchedWires);
      setStandards(fetchedStandards);
    } catch (error) {
      console.error("数据加载失败", error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateOrder = async (newOrder: ProductionOrder) => {
    try {
        await api.createOrder(newOrder);
        const latestOrders = await api.getOrders();
        setOrders(latestOrders);
        setView('LIST');
    } catch (e: any) {
        alert(`创建失败: ${e.message}`);
    }
  };

  const handleAddRecord = async (orderId: string, type: SubmissionType) => {
    const initialSamples: TerminalSample[] = [
        { id: 0, sampleIndex: 1, measuredForce: 0, isPassed: false }, 
        { id: 0, sampleIndex: 2, measuredForce: 0, isPassed: false }, 
        { id: 0, sampleIndex: 3, measuredForce: 0, isPassed: false }
    ];

    const generateId = () => {
        if (typeof crypto !== 'undefined' && crypto.randomUUID) {
            return crypto.randomUUID();
        }
        return Date.now().toString(36) + Math.random().toString(36).substring(2);
    };

    const newRecord: InspectionRecord = {
      id: generateId(), 
      orderId,
      type: type === SubmissionType.FIRST_PIECE ? "首件" : "末件",
      submitterName: currentUser.name, 
      submittedAt: new Date().toISOString(),
      status: AuditStatus.PENDING,
      samples: initialSamples,
      auditNote: null
    };

    try {
        await api.addRecord(orderId, newRecord);
        await loadData();
        if (selectedOrder) {
            const latestOrders = await api.getOrders();
            const updated = latestOrders.find(o => o.id === selectedOrder.id);
            if (updated) setSelectedOrder(updated);
        }
    } catch (e: any) {
        alert(`提交失败: ${e.message}`);
    }
  };

  const handleAuditRecord = async (orderId: string, recordId: string, samples: TerminalSample[]) => {
    const allPassed = samples.every(s => s.isPassed);
    const newStatus = allPassed ? AuditStatus.PASSED : AuditStatus.FAILED;

    try {
        await api.auditRecord(recordId, samples, currentUser.name, newStatus, null);
        const latestOrders = await api.getOrders();
        setOrders(latestOrders);
        if (selectedOrder) {
            const updated = latestOrders.find(o => o.id === selectedOrder.id);
            if (updated) setSelectedOrder(updated);
        }
    } catch (e: any) {
        alert(`审核提交失败: ${e.message}`);
    }
  };

  const handleCloseOrder = async (orderId: string) => {
      const orderToClose = orders.find(o => String(o.id) === String(orderId));
      if (!orderToClose) return;

      // 业务校验
      const hasFirstPiecePassed = orderToClose.records.some(r => r.type === "首件" && r.status === AuditStatus.PASSED);
      const hasLastPiece = orderToClose.records.some(r => r.type === "末件");
      const hasPendingRecords = orderToClose.records.some(r => r.status === AuditStatus.PENDING);

      if (!hasFirstPiecePassed) { alert("【无法结束】必须先有合格的首件记录。"); return; }
      if (!hasLastPiece) { alert("【无法结束】尚未提交末件测试记录。"); return; }
      if (hasPendingRecords) { alert("【无法结束】尚有记录待审核判定。"); return; }

      // 二次确认
      if (!window.confirm("确定要结束此订单吗？\n结束后订单将进入归档状态，不可再添加或修改记录。")) {
          return;
      }

      setIsClosing(true);
      try {
          await api.closeOrder(orderId);
          alert("订单已成功结束并归档。");
          const latestOrders = await api.getOrders();
          setOrders(latestOrders);
          if (selectedOrder && selectedOrder.id === orderId) {
             const updated = latestOrders.find(o => o.id === orderId);
             if (updated) setSelectedOrder(updated);
          }
      } catch (e: any) {
          alert(`关闭订单失败: ${e.message}`);
          await loadData();
      } finally {
          setIsClosing(false);
      }
  };

  /**
   * 计算订单的最终判定状态
   */
  const getOrderOverallStatus = (order: ProductionOrder) => {
    if (!order.isClosed) return null;
    const hasFailed = order.records.some(r => r.status === AuditStatus.FAILED);
    return hasFailed ? "不合格" : "合格";
  };

  if (view === 'CREATE') {
    return (
      <>
        <div className="mb-4">
             <button onClick={() => setView('LIST')} className="text-sm text-gray-500 flex items-center gap-1">
                &larr; 返回列表
             </button>
        </div>
        <CreateOrder 
            onSubmit={handleCreateOrder} 
            dailyCounter={orders.length + 1} 
            creatorName={currentUser.name}
            tools={tools}
            terminals={terminals}
            wires={wires}
            standards={standards}
        />
      </>
    );
  }

  if (selectedOrder) {
    const liveOrder = orders.find(o => o.id === selectedOrder.id) || selectedOrder;
    return (
      <OrderDetailView 
        order={liveOrder} 
        onBack={() => setSelectedOrder(null)}
        onAddRecord={handleAddRecord}
        onAuditRecord={handleAuditRecord}
        onCloseOrder={handleCloseOrder}
        currentUser={currentUser}
        tools={tools}
        terminals={terminals}
        wires={wires}
        isClosing={isClosing}
      />
    );
  }

  return (
    <div className="space-y-4">
      {currentUser.role === UserRole.EMPLOYEE && (
        <button 
            onClick={() => setView('CREATE')}
            className="w-full bg-white border-2 border-dashed border-gray-300 text-gray-500 py-4 rounded-xl font-medium hover:bg-gray-50 hover:border-brand-300 hover:text-brand-500 transition-colors"
        >
            + 新增生产订单
        </button>
      )}

      {loading ? (
        <div className="text-center py-10 text-gray-400">
            <div className="animate-spin w-6 h-6 border-2 border-brand-500 border-t-transparent rounded-full mx-auto mb-2"></div>
            正在加载数据...
        </div>
      ) : (
        <div className="space-y-3">
            {orders.map(order => {
                const finalStatus = getOrderOverallStatus(order);
                const isFailed = finalStatus === "不合格";

                return (
                    <Card key={order.id} onClick={() => setSelectedOrder(order)} className={`border-l-4 transition-all ${order.isClosed ? 'border-l-gray-300 bg-gray-50' : 'border-l-brand-500 bg-white'}`}>
                        <div className="flex justify-between items-start mb-2">
                            <div className="flex-1 min-w-0 pr-2">
                                <h3 className={`font-bold text-base truncate ${order.isClosed ? 'text-gray-600' : 'text-gray-900'}`}>
                                    {order.productName || '未命名产品'}
                                </h3>
                                <div className="flex flex-wrap items-center gap-x-3 gap-y-1 mt-1">
                                    <span className="font-mono text-[10px] text-gray-400 bg-gray-100 px-1 rounded">{order.productionOrderNo}</span>
                                    <span className="text-[10px] text-gray-400">
                                        {new Date(order.createdAt).toLocaleString('zh-CN', { month: 'numeric', day: 'numeric', hour: '2-digit', minute: '2-digit' })}
                                    </span>
                                </div>
                            </div>

                            <div className="flex flex-col items-end gap-1 shrink-0">
                                {order.isClosed ? (
                                    <span className={`text-[10px] font-bold px-1.5 py-0.5 rounded border ${isFailed ? 'bg-red-50 text-red-700 border-red-200' : 'bg-green-50 text-green-700 border-green-200'}`}>
                                        已结束: {finalStatus}
                                    </span>
                                ) : (
                                    <span className={`px-2 py-1 text-[10px] rounded font-medium ${
                                        order.records.length > 0 && order.records.some(r => r.status === AuditStatus.PENDING) 
                                        ? 'bg-yellow-100 text-yellow-700'
                                        : 'bg-blue-50 text-blue-700'
                                    }`}>
                                        {order.records.length} 条记录
                                    </span>
                                )}
                            </div>
                        </div>

                        <div className="flex justify-between items-center text-[11px] border-t border-gray-100 pt-2 mt-1">
                            <div className="flex items-center gap-2 text-gray-500">
                                <span className="bg-gray-100 px-1.5 rounded truncate max-w-[80px]">
                                    {terminals.find(t => String(t.id) === String(order.terminalSpecId))?.name || '未知端子'}
                                </span>
                                <span className="text-gray-300">|</span>
                                <span>标准: {order.standardPullForce}N</span>
                            </div>

                            <div className="flex items-center gap-1 text-gray-400">
                                {currentUser.role === UserRole.AUDITOR && (
                                    <>
                                        <svg className="w-3 h-3" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" /></svg>
                                        <span className="truncate max-w-[60px]">{order.creatorName}</span>
                                    </>
                                )}
                            </div>
                        </div>
                    </Card>
                );
            })}
        </div>
      )}
    </div>
  );
};

const OrderDetailView: React.FC<{
  order: ProductionOrder;
  onBack: () => void;
  onAddRecord: (orderId: string, type: SubmissionType) => void;
  onAuditRecord: (orderId: string, recordId: string, samples: TerminalSample[]) => void;
  onCloseOrder: (orderId: string) => void;
  currentUser: User;
  tools: CrimpingTool[];
  terminals: TerminalSpec[];
  wires: WireSpec[];
  isClosing?: boolean;
}> = ({ order, onBack, onAddRecord, onAuditRecord, onCloseOrder, currentUser, tools, terminals, wires, isClosing }) => {

  const firstPiecePassed = order.records.some(r => r.type === "首件" && r.status === AuditStatus.PASSED);
  const hasPendingFirstPiece = order.records.some(r => r.type === "首件" && r.status === AuditStatus.PENDING);

  const getToolName = (val: string) => tools.find(t => String(t.id) === val || t.model === val)?.model || val;
  const getTerminalName = (id: string) => terminals.find(t => String(t.id) === id)?.name || id;
  const getWireName = (id: string) => wires.find(w => String(w.id) === id)?.displayName || id;

  const isOrderFailed = order.records.some(r => r.status === AuditStatus.FAILED);

  return (
    <div className="space-y-4 pb-10">
      <button onClick={onBack} className="text-sm text-gray-500 mb-2 flex items-center">&larr; 返回列表</button>

      {order.isClosed && (
        <div className={`p-3 rounded-lg text-sm flex items-center justify-between shadow-sm border ${
            isOrderFailed 
            ? 'bg-red-600 text-white border-red-700' 
            : 'bg-green-600 text-white border-green-700'
        }`}>
            <div className="flex items-center gap-2">
                <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" /></svg>
                <span className="font-bold uppercase">已归档 ({isOrderFailed ? '不合格' : '合格'})</span>
            </div>
            <span className="text-[10px] opacity-80">只读模式</span>
        </div>
      )}

      <div className={`bg-white p-4 rounded-xl shadow-sm border border-gray-100 relative overflow-hidden transition-all duration-500 ${order.isClosed ? 'grayscale opacity-90' : ''}`}>
        <div className="absolute top-0 right-0 p-2 opacity-10">
