                <>
                    <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" /></svg>
                    <span>确认并结束订单 (归档)</span>
                </>
            )}
          </button>
      )}

      <div className="space-y-4">
        {order.records.map(record => (
          <InspectionCard 
            key={record.id} 
            record={record} 
            standardPullForce={order.standardPullForce}
            isAuditor={currentUser.role === UserRole.AUDITOR}
            isOrderClosed={!!order.isClosed}
            onAuditSubmit={(samples) => onAuditRecord(order.id, record.id, samples)}
          />
        ))}
      </div>
    </div>
  );
};

const InspectionCard: React.FC<{
    record: InspectionRecord;
    standardPullForce: number;
    isAuditor: boolean;
    isOrderClosed: boolean;
    onAuditSubmit: (samples: TerminalSample[]) => void;
}> = ({ record, standardPullForce, isAuditor, isOrderClosed, onAuditSubmit }) => {

    const isPending = record.status === AuditStatus.PENDING;
    const canAudit = isAuditor && isPending && !isOrderClosed;

    const [editSamples, setEditSamples] = useState<TerminalSample[]>(
        record.samples.map(s => ({ ...s }))
    );

    const handleValueChange = (index: number, valStr: string) => {
        const val = parseFloat(valStr);
        const newSamples = [...editSamples];
        newSamples[index] = {
            ...newSamples[index],
            measuredForce: isNaN(val) ? 0 : val,
            isPassed: !isNaN(val) ? val >= standardPullForce : false
        };
        setEditSamples(newSamples);
    };

    const handleSave = () => {
        if (editSamples.some(s => !s.measuredForce && s.measuredForce !== 0)) {
            alert("请填写所有端子的测试拉力值");
            return;
        }
        onAuditSubmit(editSamples);
    };

    const displaySamples = canAudit ? editSamples : record.samples;

    return (
        <Card className={`overflow-hidden p-0 ${isPending ? 'border-yellow-200 ring-4 ring-yellow-50' : 'border-gray-200'} ${isOrderClosed ? 'opacity-80' : ''}`}>
            <div className={`px-4 py-3 border-b flex justify-between items-center ${isPending ? 'bg-yellow-50' : 'bg-gray-50'}`}>
                <div className="flex items-center gap-2">
                    <span className={`px-2 py-1 rounded text-xs font-bold ${
                        record.type === "首件" ? 'bg-purple-600 text-white' : 'bg-blue-600 text-white'
                    }`}>
                        {record.type}
                    </span>
                    <div className="flex flex-col">
                        <span className="text-xs text-gray-500 leading-tight">提交: {record.submitterName}</span>
                        <span className="text-[10px] text-gray-400 leading-tight">{new Date(record.submittedAt).toLocaleString()}</span>
                    </div>
                </div>
                <StatusBadge status={record.status} />
            </div>

            <div className="p-4">
                <table className="w-full text-sm">
                    <thead>
                        <tr className="text-left text-xs text-gray-500 border-b border-gray-100">
                            <th className="pb-2 font-medium w-16">样件</th>
                            <th className="pb-2 font-medium">拉力(N)</th>
                            <th className="pb-2 font-medium text-center">结论</th>
                        </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-50">
                        {displaySamples.map((sample, idx) => (
                            <tr key={idx}>
                                <td className="py-3 font-medium text-gray-700">#{sample.sampleIndex}</td>
                                <td className="py-3 pr-2">
                                    {canAudit ? (
                                        <input 
                                            type="number" 
                                            className="w-full border rounded px-2 py-1 focus:ring-2 focus:ring-brand-500 outline-none"
                                            value={sample.measuredForce || ''}
                                            onChange={(e) => handleValueChange(idx, e.target.value)}
                                        />
                                    ) : (
                                        <span className="font-mono text-gray-900">{sample.measuredForce}</span>
                                    )}
                                </td>
                                <td className="py-3 text-center">
                                    {sample.isPassed ? 
                                        <span className="text-green-600 text-xs font-bold">合格</span> : 
                                        <span className="text-red-500 text-xs font-bold">不合格</span>}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                {canAudit && (
                    <div className="mt-4 text-right">
                         <button onClick={handleSave} className="bg-brand-600 text-white px-6 py-2 rounded-lg text-sm font-bold shadow-md active:scale-95 transition-transform">提交判定结论</button>
                    </div>
                )}
            </div>
        </Card>
    );
};

const StatusBadge: React.FC<{status: AuditStatus}> = ({status}) => {
    if(status === AuditStatus.PENDING) return <span className="text-xs bg-yellow-100 text-yellow-800 px-2 py-1 rounded">待审核</span>;
    if(status === AuditStatus.PASSED) return <span className="text-xs bg-green-100 text-green-800 px-2 py-1 rounded">合格</span>;
    return <span className="text-xs bg-red-100 text-red-800 px-2 py-1 rounded">不合格</span>;
}

import React, { useState } from 'react';
import { User } from '../types';
import { api } from '../services/api'; 
import { Input } from '../components/Input';

interface LoginProps {
  onLogin: (user: User) => void;
}

export const Login: React.FC<LoginProps> = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [autoLogin, setAutoLogin] = useState(false);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleLogin = async () => {
    if(!username || !password) {
        setError("请输入账号和密码");
        return;
    }

    setIsLoading(true);
    setError('');

    try {
      const user = await api.login(username, password);

      // 如果开启了自动登录，将用户信息保存到本地存储
      if (autoLogin) {
        localStorage.setItem('qc_user_session', JSON.stringify(user));
      } else {
        localStorage.removeItem('qc_user_session');
      }

      onLogin(user);
    } catch (err: any) {
      setError(err.message || '登录失败，请重试');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 px-4">
      <div className="bg-white p-8 rounded-2xl shadow-xl w-full max-w-sm space-y-6">
        <div className="text-center">
          <div className="w-16 h-16 bg-brand-100 text-brand-600 rounded-2xl mx-auto flex items-center justify-center mb-4">
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-8 h-8">
              <path strokeLinecap="round" strokeLinejoin="round" d="M9 12.75L11.25 15 15 9.75M21 12c0 1.268-.63 2.39-1.593 3.068a3.745 3.745 0 01-1.043 3.296 3.745 3.745 0 01-3.296 1.043A3.745 3.745 0 0112 21c-1.268 0-2.39-.63-3.068-1.593a3.746 3.746 0 01-3.296-1.043 3.745 3.745 0 01-1.043-3.296A3.745 3.745 0 013 12c0-1.268.63-2.39 1.593-3.068a3.745 3.745 0 011.043-3.296 3.746 3.746 0 013.296-1.043A3.746 3.746 0 0112 3c1.268 0 2.39.63 3.068 1.593a3.746 3.746 0 013.296 1.043 3.746 3.746 0 011.043 3.296A3.745 3.745 0 0121 12z" />
            </svg>
          </div>
          <h1 className="text-2xl font-bold text-gray-900">质量控制系统</h1>
          <p className="text-sm text-gray-500 mt-2">请使用员工或检验员账号登录</p>
        </div>

        <div className="space-y-4">
            <Input 
                label="账号" 
                placeholder="请输入账号" 
                value={username}
                onChange={e => setUsername(e.target.value)}
                disabled={isLoading} 
            />
            <Input 
                label="密码" 
                type="password"
                placeholder="请输入密码"
                value={password}
                onChange={e => setPassword(e.target.value)}
                disabled={isLoading}
            />

            <div className="flex items-center gap-2 px-1">
                <input 
                    type="checkbox" 
                    id="autoLogin" 
                    checked={autoLogin}
                    onChange={(e) => setAutoLogin(e.target.checked)}
                    className="w-4 h-4 text-brand-600 border-gray-300 rounded focus:ring-brand-500"
                />
                <label htmlFor="autoLogin" className="text-sm text-gray-600 select-none">下次自动登录</label>
            </div>
        </div>

        {error && <div className="bg-red-50 text-red-500 text-sm p-3 rounded-lg text-center">{error}</div>}

        <button 
            onClick={handleLogin}
            disabled={isLoading}
            className={`w-full py-3.5 rounded-xl font-bold text-white transition-colors shadow-lg 
                ${isLoading ? 'bg-gray-400 cursor-not-allowed' : 'bg-brand-600 hover:bg-brand-700 shadow-brand-500/30'}`}
        >
            {isLoading ? '登录中...' : '立即登录'}
        </button>
      </div>
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { Layout } from './components/Layout';
import { Dashboard } from './pages/Dashboard';
import { Login } from './pages/Login';
import { CrimpingDashboard } from './pages/TerminalCrimping/CrimpingDashboard';
import { SpecialProcessMenu } from './pages/SpecialProcessMenu'; 
import { ProcessType, User } from './types';

function App() {
  const [user, setUser] = useState<User | null>(null);
  const [currentProcess, setCurrentProcess] = useState<ProcessType | null>(null);
  const [isInitializing, setIsInitializing] = useState(true);

  // 检查自动登录状态
  useEffect(() => {
    const savedUser = localStorage.getItem('qc_user_session');
    if (savedUser) {
      try {
        const parsedUser = JSON.parse(savedUser);
        setUser(parsedUser);
      } catch (e) {
        console.error("解析本地会话失败", e);
        localStorage.removeItem('qc_user_session');
      }
    }
    setIsInitializing(false);
  }, []);

  // 如果正在初始化，显示一个简单的加载页，避免登录页闪烁
  if (isInitializing) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-brand-500 border-t-transparent rounded-full"></div>
      </div>
    );
  }

  if (!user) {
    return <Login onLogin={setUser} />;
  }

  const renderContent = () => {
    if (!currentProcess) {
      return <Dashboard onNavigate={setCurrentProcess} />;
    }

    switch (currentProcess) {
      case ProcessType.SPECIAL_PROCESS_MENU:
        return (
            <SpecialProcessMenu 
                onNavigate={setCurrentProcess} 
                onBack={() => setCurrentProcess(null)} 
            />
        );

      case ProcessType.TERMINAL_CRIMPING:
        return <CrimpingDashboard currentUser={user} />;

      default:
        return (
          <div className="text-center py-10">
            <p className="text-gray-500">该模块正在开发中...</p>
            <button 
                onClick={() => setCurrentProcess(null)}
                className="mt-4 text-brand-600 underline"
            >
                返回首页
            </button>
          </div>
        );
    }
  };

  const getTitle = () => {
      if (!currentProcess) return "检验过程记录";
      if (currentProcess === ProcessType.SPECIAL_PROCESS_MENU) return "特殊过程";
      if (currentProcess === ProcessType.TERMINAL_CRIMPING) return "压接端子检验";
      return "未知模块";
  };

  const handleLogout = () => {
    // 退出时清除自动登录信息
    localStorage.removeItem('qc_user_session');
    setUser(null);
    setCurrentProcess(null);
  };

  return (
    <Layout 
      title={getTitle()}
      showBack={!!currentProcess}
      onBack={() => {
          if (currentProcess === ProcessType.TERMINAL_CRIMPING) {
              setCurrentProcess(ProcessType.SPECIAL_PROCESS_MENU);
          } else {
              setCurrentProcess(null);
          }
      }}
      user={user}
      onLogout={handleLogout}
    >
      {renderContent()}
    </Layout>
  );
}

export default App;

