  // 获取用户名单 (Id + Name)
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        setIsLoadingUsers(true);
        const users = await api.getUsers();
        // 在控制台打印请求到的员工信息
        console.log('[Login] 收到员工列表数据:', users);
        setUserList(users);
      } catch (err: any) {
        console.error("无法加载员工名单", err);
        setError("无法加载员工名单，请检查服务器连接");
      } finally {
        setIsLoadingUsers(false);
      }
    };
    fetchUsers();
  }, []);

