[HttpDelete("records/{recordId}")]
public async Task<IActionResult> DeleteRecord(string recordId)
{
    try
    {
        await _service.DeleteRecordAsync(recordId);
        return Ok(new { message = "删除成功", recordId });
        // 或者：return NoContent();
    }
    catch (InvalidOperationException ex)
    {
