using Microsoft.AspNetCore.Mvc;
using QualityControlAPI.Services.Auth;
using QualityControlAPI.Models;

namespace QualityControlAPI.Controllers
{
    [ApiController]
    [Route("api/[controller]")]
    public class AuthController : ControllerBase
    {
        private readonly AuthService _authService;

        public AuthController(AuthService authService)
        {
            _authService = authService;
        }

        [HttpPost("login")]
        public async Task<ActionResult<User>> Login([FromBody] LoginRequest request)
        {
            var user = await _authService.LoginAsync(request.Username, request.Password);
            if (user == null)
            {
                return Unauthorized("账号或密码错误");
            }
            // 返回包含新 Token 的用户信息
            return Ok(user);
        }

        // ✅ 新增：自动登录/状态检查接口
        [HttpPost("check-token")]
        public async Task<ActionResult<User>> CheckToken([FromBody] TokenCheckRequest request)
        {
            var user = await _authService.ValidateTokenAsync(request.EmployeeId, request.Token);
            if (user == null)
            {
                return Unauthorized("登录已过期或已在其他地方登录");
            }
            return Ok(user); // 验证成功，返回用户信息
        }

        [HttpGet("users")]
        public async Task<IActionResult> GetAllUsersIdAndName()
        {
            var list = await _authService.GetAllUserIdAndNamesAsync();
            return Ok(list);
        }
    }

    public class LoginRequest { public string Username { get; set; } public string Password { get; set; } }
}using Microsoft.EntityFrameworkCore;
using QualityControlAPI.Data;
using QualityControlAPI.Models;

namespace QualityControlAPI.Services.Auth
{
    public class AuthService
    {
        private readonly AppDbContext _context;

        public AuthService(AppDbContext context)
        {
            _context = context;
        }

        // ✅ 新增：获取所有用户（Id + Name）
        public async Task<List<UserNameDto>> GetAllUserIdAndNamesAsync()
        {
            return await _context.Users
                .AsNoTracking()
                .OrderBy(u => u.Name)
                .Select(u => new UserNameDto
                {
                    Id = u.EmployeeId,
                    Name = u.Name
                })
                .ToListAsync();
        }

        public async Task<User?> LoginAsync(string username, string password)
        {
            // 1. 验证账号密码
            var user = await _context.Users
                .FirstOrDefaultAsync(u => u.EmployeeId == username && u.Password == password);

            if (user != null)
            {
                // 2. 登录成功，生成新 Token（实现“后登顶掉前登”）
                user.Token = Guid.NewGuid().ToString("N");
                // 设置有效期，例如 7 天
                user.TokenExpireTime = DateTime.Now.AddDays(7);

                await _context.SaveChangesAsync();
            }
            return user;
        }

        // 新增：通过 Token 验证用户是否依然合法在线
        public async Task<User?> ValidateTokenAsync(string employeeId, string token)
        {
            return await _context.Users
                .AsNoTracking() // 仅查询，不需要追踪
                .FirstOrDefaultAsync(u => u.EmployeeId == employeeId
                                       && u.Token == token
                                       && u.TokenExpireTime > DateTime.Now);
        }
    }
}[Table("Users")]
public class User
{
    [Key][Column("Id")] public int Id { get; set; }
    [Column("Name")] public required string Name { get; set; }
    [Column("EmployeeId")] public required string EmployeeId { get; set; }
    [Column("Password")] public required string Password { get; set; }
    [Column("Role")] public int Role { get; set; }

    // --- 新增字段 ---
    [Column("Token")] public string? Token { get; set; }
    [Column("TokenExpireTime")] public DateTime? TokenExpireTime { get; set; }
}

// 新增：用于自动登录/状态检查的请求体
public class TokenCheckRequest
{
    public string EmployeeId { get; set; }
